_a-0000edaa-0a95-8000-9e03-011c48011c48_.sail_code:
/*  This rule constructs the manipulation audit for all current documents of the vendor.                   */
/*  And then iterates over the constructed audit to check if any entry has a change in                     */
/*  their factor mappings and if there is no change it removes them. This has been done to                 */
/*                                                                                                         */
/*    1. Avoid mapping audit capture for new documents uploaded in the current instance.                   */
/*       (This is avoided coz, the document addition audit entry already has factor mapping info in it)    */
/*    2. Avoid mapping audit if the factor mapping is not changed for any document                         */
a!localVariables(
  local!existingDocs: rule!AS_CO_UT_indexWhere(
    value: index(
      ri!originalEvalDocs,
      recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.evaluationDocumentId,
      null
    ),
    initialArray: index(ri!addedDocs, "evalDocumentId", null),
    finalArray: ri!addedDocs,
    isRemove: false()
  ),
  if(
    rule!AS_CO_UT_checkIsNullOrEmpty(
      value: index(
        local!existingDocs,
        "evalDocumentId",
        null
      )
    ),
    {},
    rule!AS_CO_UT_rejectNullValuesFromArray(
      array: {
        a!flatten(
          #"SYSTEM_SYSRULES_forEach"(
            items: rule!AS_GSS_BL_constructFieldChangesForEvalDocs(
              evaluationDocuments: local!existingDocs,
              factorDocMapping: ri!updatedFactorDocMapping,
              originalFactorDocMapping: ri!updatedOriginalFacDocMappings,
              externalAction: cons!AS_GSS_ENUM_AUDIT_ACTION_CODE_DOC_UPDATE
            ),
            expression: a!localVariables(
              local!fieldChanges: rule!AS_CO_UT_indexWhere(
                value: "factorIds",
                initialArray: fv!item.fieldChanges.fieldName,
                finalArray: fv!item.fieldChanges,
                isRemove: false()
              ),
              if(
                rule!AS_GSS_UT_CompareLists(
                  listOne: rule!AS_CO_UT_rejectNullValuesFromArray(array: local!fieldChanges.oldValue),
                  listTwo: rule!AS_CO_UT_rejectNullValuesFromArray(array: local!fieldChanges.newValue),
                  lengthMatters: true(),
                  orderMatters: false()
                ),
                {},
                fv!item
              )
            )
          )
        )
      }
    )
  )
)

