      getActiveAndInactive: false(),
      fields: {
        recordType!AS_GSS_EvaluatorTeam_SYNCEDRECORD.teamId
      }
    ),
    /*Fetches the existing mask evaluator table entries for the current evaluiation*/
    local!existingAliasEntries: rule!AS_GSS_QR_getMaskEvaluatorDetails(
      returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
      evaluationId: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId]
    ),
    /*Contructs the existing entries in map formats*/
    local!existingAliasMaps: rule!AS_GSS_UT_constructAliasMapsFromRecordInputs(
      evaluatorAliases: local!existingAliasEntries
    ),
    local!teamMembers: if(
      rule!AS_CO_UT_checkIsNullOrEmpty(
        value: local!evaluationTeams[recordType!AS_GSS_EvaluatorTeam_SYNCEDRECORD.teamId]
      ),
      {},
      rule!AS_GSS_QR_getEvaluatorTeamMembership(
        returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
        teamId: local!evaluationTeams[recordType!AS_GSS_EvaluatorTeam_SYNCEDRECORD.teamId],
        fields: {
          recordType!AS_GSS_EvaluatorTeamMembership_SYNCEDRECORD.member
        }
      )
    ),
    /*Nick names needs to be generated only for the users who does not have an existing*/
    /*entry in the mask evaluators table. The below difference takes care of that*/
    local!newUsers: difference(
      touniformstring(
        index(
          local!teamMembers,
          recordType!AS_GSS_EvaluatorTeamMembership_SYNCEDRECORD.member,
          null
        )
      ),
      touniformstring(
        index(
          local!existingAliasMaps,
          "userName",
          null
        )
      )
    ),
    local!aliasNames: rule!AS_GSS_generateAliasName(
      username: local!newUsers,
      evaluationId: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId],
      existingUserNickName: local!existingAliasMaps,
      bundle: local!bundle
    ),
    if(
      rule!AS_CO_UT_checkIsNullOrEmpty(
        value: local!aliasNames
      ),
      ri!evaluation,
      rule!AS_GSS_UT_updateRecordsByModelRecord(
        records: ri!evaluation,
        modelRecord: recordType!AS_GSS_Evaluation_SYNCEDRECORD(
