/*Nullifying the evaluator team column for active factors*/
if(
  or(
    rule!AS_CO_UT_isBlank(ri!evaluationFactors),
    rule!AS_CO_UT_isBlank(
      rule!AS_CO_UT_filterCdtByField(
        cdt: ri!evaluationFactors,
        field: "evaluatorTeam.teamId",
        value: ri!deletedEvaluatorTeam.teamId
      )
    )
  ),
  null,
  a!save(
    ri!evaluationFactors[wherecontains(
      ri!deletedEvaluatorTeam.teamId, ri!evaluationFactors.evaluatorTeam.teamId
    )],
    #"SYSTEM_SYSRULES_forEach"(
      items: ri!evaluationFactors[wherecontains(
        ri!deletedEvaluatorTeam.teamId, ri!evaluationFactors.evaluatorTeam.teamId
      )],
      expression: rule!AS_CO_UT_updateCdtFieldsByFieldValuePairs(
        cdt: fv!item,
        fieldValuePairs: {
          { field: "evaluatorTeam", value: null },
          if(
            rule!AS_CO_UT_isBlank(
              rule!AS_CO_UT_filterCdtByField(
                cdt: fv!item.assignments,
                field: "isActive",
                value: true
              )
            ),
            {},
            {
              field: "assignments",
              value: rule!AS_CO_UT_cast(
                type: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_Criteria_Assignments?list',
                value: if(
                  /*Nullifying the assignments when the evaluator team and the assignments are new (Not yet added to the DB*/
                  and(
                    rule!AS_CO_UT_isBlank(ri!deletedEvaluatorTeam.createdBy),
                    rule!AS_CO_UT_isBlank(
                      rule!AS_CO_UT_indexPath(
                        cdt: rule!AS_CO_UT_filterCdtByField(
                          cdt: fv!item.assignments,
                          field: "isActive",
                          value: true
                        ),
                        field: "createdBy"
                      )
                    )
                  ),
                  null,
                  rule!AS_CO_UT_rejectNullValuesFromArray(
                    {
                      /*Deleting or deactivating active assignments for the evaluator team associated with criteria*/
                      #"SYSTEM_SYSRULES_forEach"(
                        items: rule!AS_CO_UT_filterCdtByField(
                          cdt: fv!item.assignments,
                          field: "isActive",
                          value: true
                        ),
                        expression: if(
                          rule!AS_CO_UT_isBlank(fv!item.assignmentId),
                          null,
                          rule!AS_CO_UT_updateCdtFields(
                            cdt: fv!item,
                            fields: "isActive",
                            newValues: false
                          )
                        )
                      ),
                      /*Retaining inactive entries*/
                      rule!AS_CO_UT_filterCdtByField(
                        cdt: fv!item.assignments,
                        field: "isActive",
                        value: false
                      )
                    }
                  )
                )
              )
            }
          )
        }
      )
    )
  )
)

