a!localVariables(
  /*Get the distinct IDs*/
  local!distinctIds: rule!AS_CO_UT_distinct(
    tointeger(
      {
        index(
          ri!originalObjects,
          ri!auditConfig.idField,
          {}
        ),
        index(
          ri!newObjects,
          ri!auditConfig.idField,
          {}
        )
      }
    )
  ),
  if(
    rule!AS_CO_UT_isBlank(local!distinctIds),
    {},
    /*This flatten converts to a list of dictionary, which prevents odd behavior in the parent*/
    a!flatten(
      #"SYSTEM_SYSRULES_forEach"(
        items: local!distinctIds,
        expression: a!flatten(
          rule!AS_ADT_UT_calculateAudit_SingleObject(
            originalObject: index(
              rule!AS_CO_UT_filterCdtByField(
                cdt: ri!originalObjects,
                field: ri!auditConfig.idField,
                value: fv!item
              ),
              1,
              {}
            ),
            newObject: index(
              rule!AS_CO_UT_filterCdtByField(
                cdt: ri!newObjects,
                field: ri!auditConfig.idField,
                value: fv!item
              ),
              1,
              {}
            ),
            auditConfig: ri!auditConfig,
            username: ri!username,
            externalAction: ri!externalAction,
            auditType: ri!auditType
          )
        )
      )
    )
  )
)

