_a-0000e675-e3a8-8000-9bcb-011c48011c48_.sail_code:
if(
  /* If both original and new factorDocMappings are blank, then there were no factorDocMappings before, so no factorDocMappings after */
  and(
    rule!AS_CO_UT_isBlank(
      rule!AS_CO_UT_replaceNull(
        nullableValue: ri!originalFactorDocMappings,
        replacementValue: ""
      )
    ),
    rule!AS_CO_UT_isBlank(
      rule!AS_CO_UT_replaceNull(
        nullableValue: ri!newFactorDocMappings,
        replacementValue: ""
      )
    )
  ),
  {},

  a!flatten(
    if(
      rule!AS_CO_UT_isBlank(ri!originalFactorDocMappings),
      /* If there were no factorDocMappings before the cancel, then we should deactivate all new ones */
      #"SYSTEM_SYSRULES_forEach"(
        items: ri!newFactorDocMappings,
        expression: rule!AS_CO_UT_updateCdtFields(
          cdt: fv!item,
          fields: "isActive",
          newValues: false
        )
      ),

      if(
        /* If there were factorDocMappings before the cancel and no new factorDocMappings, then reactive all the old ones */
        rule!AS_CO_UT_isBlank(ri!newFactorDocMappings),
        #"SYSTEM_SYSRULES_forEach"(
          items: ri!originalFactorDocMappings,
          expression: rule!AS_CO_UT_updateCdtFields(
            cdt: fv!item,
            fields: "isActive",
            newValues: true
          )
        ),

        /* Else reactivate all old factorDocMappings and deactivate any new factorDocMappings */
        a!localVariables(
          local!distinctDocumentMappingIds: union(
            ri!newFactorDocMappings.documentMappingId,
            ri!originalFactorDocMappings.documentMappingId
          ),
          #"SYSTEM_SYSRULES_forEach"(
            items: local!distinctDocumentMappingIds,
            expression: a!localVariables(
              local!originalFactor: rule!AS_CO_UT_filterCdtByField(
                cdt: ri!originalFactorDocMappings,
                field: "documentMappingId",
                value: fv!item
              ),
              local!newFactor: rule!AS_CO_UT_filterCdtByField(
                cdt: ri!newFactorDocMappings,
                field: "documentMappingId",
                value: fv!item
              ),

              if(
                rule!AS_CO_UT_isBlank(local!originalFactor),
                /* If there is no corresponding old factor, then deactivate the new one */
                rule!AS_CO_UT_updateCdtFields(
                  cdt: local!newFactor,
                  fields: {"isActive"},
                  newValues: {false}
                ),
                /* Else we will use the original factor value */
                local!originalFactor
              )
            )
          )
        )
      )
    )
  )
)

