a!localVariables(
  local!isUnsavedSubfactor,
  /*Sub factor details will not be shown for an unsaved new factor*/
  if(
    ri!isUnSavedMainFactor,
    {},
    {
      #"SYSTEM_SYSRULES_sectionLayout_v1"(
        label : "Subfactors",
        labelColor: "STANDARD",
        labelHeadingTag: "H3",
        labelSize: "MEDIUM",
        contents: {
          /*Edit and Delete buttons*/
          if(
            ri!showSubFactorGrid = true,
            {
              #"SYSTEM_SYSRULES_ButtonArrayLayout"(
                align: "START",
                buttons: {
                  #"SYSTEM_SYSRULES_buttonWidget_v1"(
                    label: "Add",
                    icon: "plus",
                    style: "OUTLINE",
                    saveInto: {
                      a!save(
                        ri!selectedSubFactors,
                        rule!AS_CO_UT_cast(
                          type: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_Criteria',
                          value: {
                            /*Mapping the temporary criteria Id to the sub factors to be used in the grid selection*/
                            /*criteriaId: if(*/
                            /*rule!AS_CO_UT_isBlank(ri!evaluationSubFactors),*/
                            /*1,*/
                            /*rule!AS_CO_UT_returnLastIndexOfArray(ri!evaluationSubFactors).criteriaId + 1*/
                            /*),*/
                            parentCriteriaId: ri!evaluationFactor.criteriaId,
                            criteriaStatus: rule!AS_GSS_QE_getRefDataByTypes(
                              refDataIds: cons!AS_GSS_REF_ID_EVALUATION_STATUS_TEMP
                            ),
                            evaluation: ri!evaluation,
                            isActive: true,
                            /*Using createdBy as reference to differentiate existing factors and factors added newly*/
                            createdBy: null,
                            createdDatetime: null,
                            criteriaChair: if(
                              rule!AS_CO_UT_isBlank(ri!evaluationFactor),
                              null,
                              ri!evaluationFactor.criteriaChair
                            )
                          }
                        )
                      ),
                      /*Append the newly added sub factor to the evaluation sub factors*/
                      a!save(
                        ri!evaluationSubFactors,
                        append(
                          ri!evaluationSubFactors,
                          ri!selectedSubFactors
                        )
                      ),
                      a!save(ri!showSubFactorGrid, false),
                      a!save(local!isUnsavedSubfactor, true)
                    },
                    disabled: or(
                      rule!AS_CO_UT_isNotBlank(ri!selectedSubFactors),
                      ri!isMainFactorEdited = true
                    ),
                    accessibilityText: "Press enter to add factor"
                  ),
                  #"SYSTEM_SYSRULES_buttonWidget_v1"(
                    label: "Edit",
                    icon: "pencil",
                    style: "OUTLINE",
                    saveInto: { a!save(ri!showSubFactorGrid, false) },
                    disabled: or(
                      if(
                        rule!AS_CO_UT_isBlank(ri!selectedSubFactors),
                        true,
                        length(ri!selectedSubFactors) > 1
                      ),
                      ri!isMainFactorEdited = true
                    ),
                    accessibilityText: "Press enter to edit factor"
                  ),
                  #"SYSTEM_SYSRULES_buttonWidget_v1"(
                    label: "Delete",
                    icon: "trash",
                    style: "OUTLINE",
                    saveInto: {
                      /*Save isActive as false for deleted factor*/
                      a!save(
                        ri!evaluationSubFactors,
                        rule!AS_CO_UT_updateCdtFields(
                          cdt: ri!evaluationSubFactors,
                          fields: { "isActive" },
                          indices: wherecontains(
                            tointeger(ri!selectedSubFactors.criteriaId),
                            tointeger(ri!evaluationSubFactors.criteriaId)
                          ),
                          newValues: { false }
                        )
                      ),
                      if(
                        rule!AS_CO_UT_isBlank(ri!selectedSubFactors),
                        {},
                        #"SYSTEM_SYSRULES_writeToDataStoreEntity"(
                          dataStoreEntity: cons!AS_GSS_ENT_CRITERIA,
                          valueToStore: ri!evaluationSubFactors[wherecontains(
                            ri!selectedSubFactors.criteriaId, ri!evaluationSubFactors.criteriaId
                          )]
                        )
                      ),
                      #"SYSTEM_SYSRULES_forEach"(
                        items: ri!selectedSubFactors,
                        expression: if(
                          rule!AS_CO_UT_isNotBlank(fv!item.createdBy),
                          {
                            rule!AS_GSS_BL_saveIntoForCriteriaAssignmentsInCriteriaPage(criterias: fv!item)
                          },
                          /*Remove deleted sub factor that is added in the current instance*/
                          a!save(
                            ri!evaluationSubFactors,
                            remove(
                              ri!evaluationSubFactors,
                              wherecontains(
                                tointeger(fv!item.criteriaId),
                                tointeger(ri!evaluationSubFactors.criteriaId)
                              )
                            )
                          )
                        )
                      ),
                      /*Nullifying the selection after a sub-factor is deleted*/
                      a!save(
                        ri!selectedSubFactors,
                        rule!AS_CO_UT_initializeBlankLocalVariable(
                          type: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_Criteria?list'
                        )
                      )
                    },
                    disabled: or(
                      rule!AS_CO_UT_isBlank(ri!selectedSubFactors),
                      ri!isMainFactorEdited = true
                    ),
                    confirmMessage: "Are you sure you want to delete the sub-factor(s)?",
                    accessibilityText: "Press enter to delete factor(s)"
                  )
                }
              ),
              rule!AS_GSS_GRD_evaluationSubFactors(
                evaluationFactor: ri!evaluationFactor,
                evaluationSubFactors: rule!AS_CO_UT_filterCdtByField(
                  cdt: ri!evaluationSubFactors,
                  field: "parentCriteriaId",
                  value: ri!evaluationFactor.criteriaId
                ),
                selectedSubFactors: ri!selectedSubFactors,
                i18nData: ri!i18nData,
                showSubFactorGrid: ri!showSubFactorGrid
              )
            },
            {}
          )
        }
      ),
      rule!AS_GSS_CPS_evaluationFactorFields(
        selectedEvaluationFactors: ri!selectedSubFactors,
        evaluationFactors: ri!evaluationSubFactors,
        isSubFactor: true,
        isUnsavedFactor: local!isUnsavedSubfactor,
        readOnly: ri!showSubFactorGrid,
        showWhen: rule!AS_CO_UT_not(ri!showSubFactorGrid = true),
        evaluation: ri!evaluation,
        confirmValidationGroup: "SUBFACTOR",
        i18nData: ri!i18nData,
        parentEvaluationFactor: ri!evaluationFactor
      )
    }
  )
)

bf4ce837-72cd-4fef-8c7f-186a03f95aa8.sail_code:
if(
  /* If both original and new factors are blank, then there were no factors before, so no factors after */
  and(
    rule!AS_CO_UT_isBlank(
      rule!AS_CO_UT_replaceNull(
        nullableValue: ri!originalFactors,
        replacementValue: ""
      )
    ),
    rule!AS_CO_UT_isBlank(
      rule!AS_CO_UT_replaceNull(
        nullableValue: ri!newFactors,
        replacementValue: ""
      )
    )
  ),
  {},

  a!flatten(
    if(
      rule!AS_CO_UT_isBlank(ri!originalFactors),
      /* If there were no factors before the cancel, then we should deactivate all new ones */
      #"SYSTEM_SYSRULES_forEach"(
        items: ri!newFactors,
        expression: rule!AS_CO_UT_updateCdtFields(
          cdt: fv!item,
          fields: "isActive",
          newValues: false
        )
      ),

      if(
        /* If there were factors before the cancel and no new factors, then reactive all the old ones */
        rule!AS_CO_UT_isBlank(ri!newFactors),
        #"SYSTEM_SYSRULES_forEach"(
          items: ri!originalFactors,
          expression: rule!AS_CO_UT_updateCdtFields(
            cdt: fv!item,
            fields: "isActive",
            newValues: true
          )
        ),

        /* Else reactivate all old factors and deactivate any new factors */
        a!localVariables(
          local!distinctFactorIds: union(
            ri!newFactors.criteriaId,
            ri!originalFactors.criteriaId
          ),
          #"SYSTEM_SYSRULES_forEach"(
            items: local!distinctFactorIds,
            expression: a!localVariables(
              local!originalFactor: rule!AS_CO_UT_filterCdtByField(
                cdt: ri!originalFactors,
                field: "criteriaId",
                value: fv!item
              ),
              local!newFactor: rule!AS_CO_UT_filterCdtByField(
                cdt: ri!newFactors,
                field: "criteriaId",
                value: fv!item
              ),

              if(
                rule!AS_CO_UT_isBlank(local!originalFactor),
                /* If there is no corresponding old factor, then deactivate the new one */
                rule!AS_CO_UT_updateCdtFields(
                  cdt: local!newFactor,
                  fields: {"isActive"},
                  newValues: {false}
                ),
                /* Else we will use the original factor value */
                local!originalFactor
              )
            )
          )
        )
      )
    )
  )
)

