    rule!AS_GSS_UT_isBlank(
      value: ri!evaluationFactors[local!activeParentFactorIndexes],
      checkIfAllCdtFieldsAreNull: true
    ),
    rule!AS_GSS_CRD_EmptyStateForWeights(bundle: ri!bundle),
    {
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          #"SYSTEM_SYSRULES_columnLayout"(width: "AUTO"),
