_a-0000e5da-a251-8000-9bbe-011c48011c48_.sail_code:
a!localVariables(
  local!activeAssignments: #"SYSTEM_SYSRULES_forEach"(
    items: ri!selectedAssignees,
    expression: if(
      contains(
        rule!AS_CO_UT_filterCdtByField(
          cdt: ri!criteria.assignments,
          field: "isActive",
          value: true
        ).assignee.username,
        fv!item
      ),
      /*Retaining existing active assignments and adding new assignments added*/
      rule!AS_CO_UT_filterCdtByField(
        cdt: ri!criteria.assignments,
        field: "assignee.username",
        value: fv!item
      ),
      rule!AS_CO_UT_cast(
        type: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_Criteria_Assignments',
        value: {
          assignmentId: null,
          assignee: rule!AS_CO_UT_filterCdtByField(
            cdt: ri!userDetails,
            field: "username",
            value: fv!item
          ),
          isActive: true
        }
      )
    )
  ),
  /*Reatining existing inactive assignments*/
  local!inactiveAssignments: rule!AS_CO_UT_filterCdtByField(
    cdt: ri!criteria.assignments,
    field: "isActive",
    value: false
  ),
  /*Deactivating or nullifying removed assignments*/
  local!deactivatedAssignments: a!localVariables(
    local!unselectedActiveAssignments: difference(
      rule!AS_CO_UT_filterCdtByField(
        cdt: ri!criteria.assignments,
        field: "isActive",
        value: true
      ).assignee.username,
      ri!selectedAssignees
    ),
    if(
      rule!AS_CO_UT_isBlank(
        local!unselectedActiveAssignments
      ),
      null,
      #"SYSTEM_SYSRULES_forEach"(
        items: local!unselectedActiveAssignments,
        expression: if(
          rule!AS_CO_UT_isBlank(
            rule!AS_CO_UT_filterCdtByField(
              cdt: ri!criteria.assignments,
              field: "assignee.username",
              value: fv!item
            ).assignmentId
          ),
          null,
          rule!AS_CO_UT_updateCdtFields(
            cdt: rule!AS_CO_UT_filterCdtByField(
              cdt: ri!criteria.assignments,
              field: "assignee.username",
              value: fv!item
            ),
            fields: "isActive",
            newValues: false
          )
        )
      )
    )
  ),  
  rule!AS_CO_UT_cast(
    type: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_Criteria_Assignments?list',
    value: rule!AS_CO_UT_rejectNullValuesFromArray(
      {
        local!activeAssignments,
        local!deactivatedAssignments,
        local!inactiveAssignments
      }
    )
  )
)

#"SYSTEM_SYSRULES_forEach"(
  items: ri!auditParams,
  expression: #"SYSTEM_SYSRULES_entityData"(
    entity: fv!item.auditDse,
    data: if(
      /*Action only audit*/
      fv!item.auditType = cons!AS_GAM_ADT_TXT_AUDIT_TYPE_ACTION_ONLY,
      rule!AS_GAM_ADT_UT_constructActionOnlyAuditData(
        initiator: ri!initiator,
        auditParams: fv!item
      ),
      /*Manipulation Audit*/
      if(
        fv!item.auditType = cons!AS_GAM_ADT_TXT_AUDIT_TYPE_MANIPULATION,
        rule!AS_GAM_ADT_UT_constructManipulatedAuditData(
          initiator: ri!initiator,
          auditParams: fv!item
        ),
        /*Generic field level audit*/
        rule!AS_GAM_ADT_UT_buildAuditEntityData(
          auditParams: fv!item,
          initiator: ri!initiator
        )
      )
    )
  )
)

/*updating the criteria assignments when they have value*/
a!save(
  ri!criterias,
  #"SYSTEM_SYSRULES_forEach"(
    items: ri!criterias,
    expression: if(
      rule!AS_CO_UT_isBlank(fv!item.assignments),
      fv!item,
      rule!AS_CO_UT_updateCdtFieldsByFieldValuePairs(
        cdt: fv!item,
        fieldValuePairs: {
          field: "assignments",
          value: rule!AS_CO_UT_cast(
            type: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_Criteria_Assignments?list',
            value: rule!AS_CO_UT_rejectNullValuesFromArray(
              #"SYSTEM_SYSRULES_forEach"(
                items: fv!item.assignments,
                expression: if(
                  rule!AS_CO_UT_isBlank(fv!item.assignmentId),
                  null,
                  rule!AS_CO_UT_updateCdtFields(
                    cdt: fv!item,
                    fields: "isActive",
                    newValues: false
                  )
                )
              )
            )
          )
        }
      )
    )
  )
)

