                        ),
                        null,
                        ri!evaluationSubFactors[wherecontains(true, ri!evaluationSubFactors.isActive)]
                      ),
                      deletedEvaluatorTeam: ri!selectedEvaluatorTeam
                    ),
                    /*Save the first active team as seleted team*/
                    a!save(
                      ri!selectedEvaluatorTeam,
                      if(
                        rule!AS_CO_UT_isNotBlank(
                          rule!AS_CO_UT_filterCdtByField(
                            cdt: ri!evaluatorTeams,
                            field: "isActive",
                            value: true
                          )
                        ),
                        rule!AS_CO_UT_filterCdtByField(
                          cdt: ri!evaluatorTeams,
                          field: "isActive",
                          value: true
                        )[1],
                        rule!AS_CO_UT_initializeBlankLocalVariable(
                          type: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_EvaluatorTeam'
                        )
                      )
                    ),
                    a!save(ri!readOnly, true),
                    /*Nullifying newly picked memberships if any*/
                    a!save(ri!newMemberships, null)
                  },
                  confirmMessage: "Deleting this evaluator team will also delete the associated evaluators. Are you sure you want to delete?",
                  accessibilityText: "Press enter to delete evaluator team"
                )
              },
              showWhen: ri!readOnly = true,
              align: "END"
            )
          )
        }
      ),
      #"SYSTEM_SYSRULES_sectionLayout_v1"(
        showWhen: ri!readOnly = true,
        divider: "BELOW",
        marginBelow: "STANDARD"
      ),
      /*CREATE/EDIT TEAM view starts*/
      rule!AS_CO_INP_textField(
        label: "Team Title",
        required: true,
        value: ri!selectedEvaluatorTeam.teamName,
        validations: rule!AS_GSS_VD_duplicateTeamTitleValidation(
          evaluatorTeams: rule!AS_CO_UT_rejectCdtByField(
            cdt: ri!evaluatorTeams,
            field: "teamId",
            value: ri!selectedEvaluatorTeam.teamId
          ),
          selectedEvalTeam: ri!selectedEvaluatorTeam
        ),
        saveInto: ri!selectedEvaluatorTeam.teamName,
        labelPosition: "ABOVE",
        placeholder: "Provide a title for the team",
        showWhen: ri!readOnly <> true,
        maxLength: cons!AS_GAM_ENUM_TEXT_LENGTH_LARGE
      ),
      rule!AS_CO_INP_paragraphField(
        label: "Description",
        instructions: rule!AS_CO_UT_instructionForCharactersEntered(
          value: ri!selectedEvaluatorTeam.teamDescription,
          maxCharacters: cons!AS_CO_ENUM_PARAGRAPH_LENGTH_MEDIUM
        ),
        value: ri!selectedEvaluatorTeam.teamDescription,
        saveInto: ri!selectedEvaluatorTeam.teamDescription,
        placeholder: "Provide a description about the team",
        showWhen: ri!readOnly <> true,
        maxLength: cons!AS_CO_ENUM_PARAGRAPH_LENGTH_MEDIUM
      ),
      if(
        rule!AS_CO_UT_not(ri!readOnly = true),
        {
          rule!AS_CO_CPS_cancelSaveButtons(
            confirmLabel: "Save",
            cancelLabel: "Cancel",
            confirmSaveInto: {
              a!save(
                ri!evaluatorTeams[wherecontains(
                  ri!selectedEvaluatorTeam.teamId, a!flatten(ri!evaluatorTeams).teamId
                )],
                ri!selectedEvaluatorTeam
              ),
              a!save(ri!readOnly, true),
              a!save(ri!isUnsavedTeam, false),
              a!save(local!triggerRefresh, now())
            },
            cancelSaveInto: {
              a!save(
                ri!selectedEvaluatorTeam,
                local!evaluatorTeam
              ),
              /*For newly added team /, the selected team needs to be removed from the index when canceled*/
              if(
                ri!isUnsavedTeam = true,
                {
                  a!save(
                    ri!evaluatorTeams,
                    remove(
                      ri!evaluatorTeams,
                      wherecontains(
                        ri!selectedEvaluatorTeam.teamId,
                        a!flatten(ri!evaluatorTeams).teamId
                      )
                    )
                  ),
                  a!save(
                    ri!selectedEvaluatorTeam,
                    if(
                      rule!AS_CO_UT_isNotBlank(
                        rule!AS_CO_UT_filterCdtByField(
                          cdt: ri!evaluatorTeams,
                          field: "isActive",
                          value: true
                        )
                      ),
                      rule!AS_CO_UT_filterCdtByField(
                        cdt: ri!evaluatorTeams,
                        field: "isActive",
                        value: true
                      )[1],
                      rule!AS_CO_UT_initializeBlankLocalVariable(
                        type: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_EvaluatorTeam?list'
                      )
                    )
                  )
                },
                {}
              ),
              a!save(ri!readOnly, true),
              a!save(ri!isUnsavedTeam, false)
            }
          ),
          #"SYSTEM_SYSRULES_sectionLayout_v1"(
            showWhen: ri!isUnsavedTeam = false,
            divider: "BELOW"
          )
        },
        {}
      )/*EDIT TEAM view ends*/

    },
    {}
  )
)

