/*Sub factors*/
a!localVariables(
  local!subFactorsIndices: wherecontains(
    tointeger(ri!parentEvaluationFactor.criteriaId),
    tointeger(
      ri!allEvaluationSubFactors.parentCriteriaId
    )
  ),
  #"SYSTEM_SYSRULES_gridLayout"(
    showWhen: a!isNotNullOrEmpty(
      ri!allEvaluationSubFactors[local!subFactorsIndices]
    ),
    label: "Subfactors",
    headerCells: {
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
        label: "ID"
      ),
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
        label: "Subfactor"
      ),
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
        label: "Due Date"
      ),
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
        label: "Team"
      ),
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
        label: "Evaluators"
      )
    },
    columnConfigs: {
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "ICON"),
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "NARROW"),
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "NARROW"),
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "NARROW"),
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "NARROW")
    },
    rows: #"SYSTEM_SYSRULES_forEach"(
      /*Indexing subfactors associated with the parent factor*/
      items: ri!allEvaluationSubFactors[local!subFactorsIndices],
      expression: a!localVariables(
        local!accessibilityTextForCurrentSubfactor: rule!AS_GAM_CO_I18N_UT_displayLabel(
          bundle: ri!i18nData,
          bundleKey: "acs_SubfactorNameWithArguments",
          arguments: concat(
            fv!item.factorNumber,
            " ",
            fv!item.criteriaName
          )
        ),
        local!activeMembersForEvaluationTeam: rule!AS_CO_UT_indexPath(
          cdt: rule!AS_CO_UT_filterCdtByField(
            cdt: rule!AS_CO_UT_indexPath(
              cdt: fv!item,
              field: "evaluatorTeam.teamMembership"
            ),
            field: "isActive",
            value: true
          ),
          field: "member"
        ),
        #"SYSTEM_SYSRULES_gridRowLayout"(
          contents: {
            rule!AS_CO_INP_textField(
              value: fv!item.factorNumber,
              readOnly: true
            ),
            rule!AS_CO_INP_textField(
              value: fv!item.criteriaName,
              readOnly: true
            ),
            rule!AS_CO_INP_dateField(
              label: if(
                cons!AS_GSS_TOGGLE_AUTOMATION_TESTING_ENABLED,
                concat("Due ", fv!item.factorNumber),
                null
              ),
              readOnly: ri!readOnly,
              value: fv!item.dueDate,
              validations: rule!AS_GSS_VD_criteriaDueDateValidation(
                evalStartDate: ri!evaluation.evaluationStartDate,
                evalDueDate: ri!evaluation.evaluationDueDate,
                factorDueDate: fv!item.dueDate,
                isSubFactor: true,
                parentFactorDueDate: ri!parentEvaluationFactor.dueDate
              ),
              saveInto: fv!item.dueDate,
              validationGroup: "",
              accessibilityText: local!accessibilityTextForCurrentSubfactor
            ),
            rule!AS_CO_CP_dropdownField(
              label: if(
                cons!AS_GSS_TOGGLE_AUTOMATION_TESTING_ENABLED,
                concat("Team ", fv!item.factorNumber),
                null
              ),
              labelPosition: "COLLAPSED",
              readOnly: ri!readOnly,
              choiceLabels: ri!activeEvaluatorTeams.teamName,
              choiceValues: ri!activeEvaluatorTeams.teamId,
              placeholderLabel: "Select an Evaluation Team",
              value: fv!item.evaluatorTeam.teamId,
              saveInto: {
                a!save(
                  fv!item.evaluatorTeam,
                  rule!AS_CO_UT_filterCdtByField(
                    cdt: ri!activeEvaluatorTeams,
                    field: "teamId",
                    value: save!value
                  )
                ),
                /*Nullifying or marking inactive for assignments when evaluator team changes*/
                a!save(
                  fv!item.assignments,
                  rule!AS_CO_UT_rejectNullValuesFromArray(
                    #"SYSTEM_SYSRULES_forEach"(
                      items: fv!item.assignments,
                      expression: if(
                        rule!AS_CO_UT_isBlank(fv!item.assignmentId),
                        null,
                        rule!AS_CO_UT_updateCdtFields(
                          cdt: fv!item,
                          fields: "isActive",
                          newValues: false
                        )
                      )
                    )
                  )
                )
              },
              accessibilityText: local!accessibilityTextForCurrentSubfactor
            ),
            rule!AS_CO_CP_multipleDropdownField(
              label: if(
                cons!AS_GSS_TOGGLE_AUTOMATION_TESTING_ENABLED,
                concat("Evaluators ", fv!item.factorNumber),
                "Evaluators"
              ),
              readOnly: ri!readOnly,
              choiceLabels: #"SYSTEM_SYSRULES_forEach"(
                items: rule!AS_CO_UT_rejectNullValuesFromArray(local!activeMembersForEvaluationTeam),
                expression: rule!AS_GAM_UT_concatUserFirstNameLastName(
                  firstName: fv!item.firstName,
                  lastName: fv!item.lastName
                )
              ),
              choiceValues: touniformstring(
                rule!AS_CO_UT_rejectNullValuesFromArray(
                  local!activeMembersForEvaluationTeam.username
                )
              ),
              placeholderLabel: "Select Evaluators",
              value: rule!AS_CO_UT_rejectNullValuesFromArray(
                touniformstring(
                  rule!AS_CO_UT_indexPath(
                    cdt: rule!AS_CO_UT_filterCdtByField(
                      cdt: fv!item.assignments,
                      field: "isActive",
                      value: true
                    ),
                    field: "assignee"
                  ).username
                )
              ),
              saveInto: {
                a!save(
                  fv!item.assignments,
                  rule!AS_GSS_BL_saveIntoForCriteriaAssignments(
                    criteria: fv!item,
                    selectedAssignees: save!value,
                    userDetails: ri!userDetails
                  )
                )
              },
              accessibilityText: local!accessibilityTextForCurrentSubfactor
            )
          }
        )
      )
    )
  )
)

