_a-0000e5da-a251-8000-9bbe-011c48011c48_.sail_code:
if(
  rule!AS_CO_UT_isBlank(
    rule!AS_CO_UT_filterCdtByField(
      cdt: ri!evaluationFactors,
      field: "isActive",
      value: true
    )
  ),
  {},
  {
    #"SYSTEM_SYSRULES_columnsLayout"(
      columns: {
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: #"SYSTEM_SYSRULES_sectionLayout_v1"(
            label: rule!AS_GSS_UT_appendFactorNumberAndCriteriaName(
              criteriaName: ri!selectedEvaluationFactor.criteriaName,
              factorNumber: ri!selectedEvaluationFactor.factorNumber
            ),
            labelSize: "MEDIUM_PLUS",
            labelHeadingTag: "H2",
            labelColor: "STANDARD",
            contents: {}
          ),
          width: "5X"
        ),
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: #"SYSTEM_SYSRULES_sideBySideLayout"(
            items: {
              /*Edit and Delete buttons*/
              #"SYSTEM_SYSRULES_sideBySideItem"(
                item: #"SYSTEM_SYSRULES_ButtonArrayLayout"(
                  buttons: {
                    #"SYSTEM_SYSRULES_ButtonWidget"(
                      label: "Edit",
                      icon: "pencil",
                      saveInto: { a!save(ri!readOnly, false) },
                      /*Disabled when a sub factor is edited*/
                      disabled: ri!showSubFactorGrid = false,
                      accessibilityText: "Press enter to edit factor"
                    ),
                    #"SYSTEM_SYSRULES_ButtonWidget"(
                      label: "Delete",
                      icon: "trash",
                      saveInto: {
                        /*Save isActive as false for deleted factor*/
                        if(
                          rule!AS_CO_UT_isBlank(
                            rule!AS_CO_UT_filterCdtByField(
                              cdt: ri!evaluationFactors,
                              field: "criteriaId",
                              value: ri!selectedEvaluationFactor.criteriaId
                            )
                          ),
                          null,
                          a!save(
                            ri!evaluationFactors,
                            rule!AS_CO_UT_updateCdtFields(
                              cdt: ri!evaluationFactors,
                              fields: { "isActive" },
                              indices: wherecontains(
                                ri!selectedEvaluationFactor.criteriaId,
                                tointeger(
                                  a!flatten(ri!evaluationFactors).criteriaId
                                )
                              ),
                              newValues: { false }
                            )
                          )
                        ),
                        if(
                          rule!AS_CO_UT_isBlank(
                            rule!AS_CO_UT_filterCdtByField(
                              cdt: ri!evaluationSubFactors,
                              field: "parentCriteriaId",
                              value: ri!selectedEvaluationFactor.criteriaId
                            )
                          ),
                          null,
                          /*Save isActive as false for sub factors associated with deleted factor*/
                          a!save(
                            ri!evaluationSubFactors,
                            rule!AS_CO_UT_updateCdtFields(
                              cdt: ri!evaluationSubFactors,
                              fields: { "isActive" },
                              indices: wherecontains(
                                ri!selectedEvaluationFactor.criteriaId,
                                tointeger(
                                  a!flatten(ri!evaluationSubFactors).parentCriteriaId
                                )
                              ),
                              newValues: { false }
                            )
                          )
                        ),
                        if(
                          and(
                            rule!AS_CO_UT_isBlank(ri!evaluationFactors),
                            rule!AS_CO_UT_isBlank(ri!evaluationSubFactors)
                          ),
                          {},
                          #"SYSTEM_SYSRULES_writeToDataStoreEntity"(
                            dataStoreEntity: cons!AS_GSS_ENT_CRITERIA,
                            valueToStore: rule!AS_CO_UT_rejectNullValuesFromArray(
                              {
                                ri!evaluationFactors,
                                ri!evaluationSubFactors
                              }
                            )
                          )
                        ),
                        if(
                          rule!AS_CO_UT_isNotBlank(ri!selectedEvaluationFactor.createdBy),
                          {
                            /*Save Criteria Assignments as false for factors*/
                            if(
                              rule!AS_CO_UT_isBlank(
                                rule!AS_CO_UT_filterCdtByField(
                                  cdt: ri!evaluationFactors,
                                  field: "criteriaId",
                                  value: ri!selectedEvaluationFactor.criteriaId
                                )
                              ),
                              null,
                              rule!AS_GSS_BL_saveIntoForCriteriaAssignmentsInCriteriaPage(
                                criterias: ri!evaluationFactors[wherecontains(
                                  ri!selectedEvaluationFactor.criteriaId, tointeger(
                                    a!flatten(ri!evaluationFactors).criteriaId
                                  )
                                )]
                              )
                            ),
                            /*Save Criteria Assignments as false for each active subfactors*/
                            if(
                              rule!AS_CO_UT_isBlank(
                                rule!AS_CO_UT_filterCdtByField(
                                  cdt: ri!evaluationSubFactors,
                                  field: "parentCriteriaId",
                                  value: ri!selectedEvaluationFactor.criteriaId
                                )
                              ),
                              null,
                              rule!AS_GSS_BL_saveIntoForCriteriaAssignmentsInCriteriaPage(
                                criterias: ri!evaluationSubFactors[wherecontains(
                                  ri!selectedEvaluationFactor.criteriaId, tointeger(
                                    a!flatten(ri!evaluationSubFactors).parentCriteriaId
                                  )
                                )]
                              )
                            )
                          },
                          {
                            /*Remove new deleted factor*/
                            a!save(
                              ri!evaluationFactors,
                              remove(
                                ri!evaluationFactors,
                                wherecontains(
                                  ri!selectedEvaluationFactor.criteriaId,
                                  tointeger(
                                    a!flatten(ri!evaluationFactors).criteriaId
                                  )
                                )
                              )
                            ),
                            /*Remove sub factors associated with new deleted factor*/
                            a!save(
                              ri!evaluationSubFactors,
                              remove(
                                ri!evaluationSubFactors,
                                wherecontains(
                                  ri!selectedEvaluationFactor.criteriaId,
                                  tointeger(
                                    a!flatten(ri!evaluationSubFactors).parentCriteriaId
                                  )
                                )
                              )
                            )
                          }
                        ),
                        /*Save the first active factor as seleted factor*/
                        a!save(
                          ri!selectedEvaluationFactor,
                          if(
                            rule!AS_CO_UT_isNotBlank(
                              rule!AS_CO_UT_filterCdtByField(
                                cdt: ri!evaluationFactors,
                                field: "isActive",
                                value: true
                              )
                            ),
                            rule!AS_CO_UT_filterCdtByField(
                              cdt: ri!evaluationFactors,
                              field: "isActive",
                              value: true
                            )[1],
                            rule!AS_CO_UT_initializeBlankLocalVariable(
                              type: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_Criteria'
                            )
                          )
                        ),
                        a!save(ri!readOnly, true)
                      },
                      /*Disabled when a sub factor is edited*/
                      disabled: ri!showSubFactorGrid = false,
                      confirmMessage: "Deleting this factor will also delete the associated subfactors. Are you sure you want to delete?",
                      accessibilityText: "Press enter to delete factor"
                    )
                  },
                  showWhen: ri!readOnly = true,
                  align: "END"
                )
              )
            },
            showWhen: rule!AS_CO_UT_not(ri!isUnsavedFactor = true)
          )
        )
      }
    ),
    rule!AS_GSS_CPS_evaluationFactorFields(
      selectedEvaluationFactors: ri!selectedEvaluationFactor,
      evaluationFactors: ri!evaluationFactors,
      associatedEvaluationSubFactors: ri!evaluationSubFactors,
      isSubFactor: false,
      readOnly: ri!readOnly,
      isUnsavedFactor: ri!isUnsavedFactor,
      showWhen: rule!AS_CO_UT_isNotBlank(ri!selectedEvaluationFactor),
      confirmValidationGroup: "FACTOR",
      evaluation: ri!evaluation,
      i18nData: ri!i18nData
    )
  }
)

#"SYSTEM_SYSRULES_sectionLayout_v1"(
  label: "Factor List",
  labelColor: "STANDARD",
  labelHeadingTag: "H2",
  labelSize: "MEDIUM",
  contents: {
    /*list of factors*/
    #"SYSTEM_SYSRULES_forEach"(
      items: rule!AS_CO_UT_filterCdtByField(
        cdt: ri!evaluationFactors,
        field: "isActive",
        value: true
      ),
      expression: a!localVariables(
        local!activeSubFactors: a!refreshVariable(
          value: rule!AS_CO_UT_filterCdtByField(
            cdt: rule!AS_CO_UT_filterCdtByField(
              cdt: ri!evaluationSubFactors,
              field: "parentCriteriaId",
              value: fv!item.criteriaId
            ),
            field: "isActive",
            value: true
          ),
          refreshOnReferencedVarChange: true,
          refreshOnVarChange: ri!selectedEvaluationFactor
        ),
        local!isCurrentItemSelected: a!refreshVariable(
          value: if(
            rule!AS_CO_UT_isNotBlank(ri!selectedEvaluationFactor.createdBy),
            and(
              rule!AS_CO_UT_isNotBlank(fv!item.createdBy),
              tointeger(fv!item.criteriaId) = tointeger(ri!selectedEvaluationFactor.criteriaId)
            ),
            and(
              rule!AS_CO_UT_isBlank(fv!item.createdBy),
              tointeger(fv!item.criteriaId) = tointeger(ri!selectedEvaluationFactor.criteriaId)
            )
          ),
          refreshOnReferencedVarChange: true,
          refreshOnVarChange: ri!selectedEvaluationFactor
        ),
        #"SYSTEM_SYSRULES_cardLayout"(
          showBorder: false,
          /*Saving the current index to selectedEvaluationFactor*/
          link: #"SYSTEM_SYSRULES_dynamicLink"(
            saveInto: {
              a!save(ri!selectedEvaluationFactor, fv!item)
            },
            showWhen: /*Disabling the selection when other factor / subfactor is edited*/
            rule!AS_CO_UT_not(
              or(
                ri!isUnsavedFactor = true,
                ri!readOnly = false,
                ri!showSubFactorGrid = false
              )
            )
          ),
          style: if(
            local!isCurrentItemSelected = true,
            "ACCENT",
            "NONE"
          ),
          contents: {
            #"SYSTEM_SYSRULES_richTextDisplayField"(
              value: {
