_a-0000e2cd-bc96-8000-9ba2-011c48011c48_.sail_code:
cast(
  typeof(ri!cdt),
  with(
    local!modifiedBy: rule!AS_CO_UT_replaceNull(
      nullableValue: ri!modifiedBy, 
      replacementValue: loggedInUser()
    ),
    local!cdtFields: rule!AS_CO_UT_returnCdtFields(typeOf: ri!cdt),
    local!possibleMetaDataFields: touniformstring(
      /*These fields should always exist in any CDT for which metadata is captured*/
      "modifiedDatetime",
      "modifiedBy",
      "createdDatetime",
      "createdBy",
      /*These fields may not exist in every CDT for which metadata is captured*/
      "isDeleted",
      "isActive"
    ),
    local!capturedMetadataFields: intersection(
      local!cdtFields,
      local!possibleMetaDataFields
    ),
    a!flatten(
      #"SYSTEM_SYSRULES_forEach"(
        items: ri!cdt,
        expression: {
          rule!AS_CO_UT_updateCdtFields(
            cdt: fv!item,
            fields: local!capturedMetadataFields,
            newValues: rule!AS_CO_UT_displayValueMultiple(
              values: local!capturedMetadataFields,
              inArray: local!possibleMetaDataFields,
              replacement: {
                now(),
                local!modifiedBy,
                rule!AS_CO_UT_replaceNull(
                  nullableValue: index(fv!item, "createdDatetime", null),
                  replacementValue: now()
                ),
                rule!AS_CO_UT_replaceNull(
                  nullableValue: index(fv!item, "createdBy", null),
                  replacementValue: local!modifiedBy
                ),
                rule!AS_CO_UT_booleanDefaultFalse(
                  boolean: index(fv!item, "isDeleted", null)
                ),
                rule!AS_CO_UT_booleanDefaultTrue(boolean: index(fv!item, "isActive", null))
              }
            )
          )
        }
      )
    )
  )
)

_a-0000e5da-a251-8000-9bbe-011c48011c48_.sail_code:
/*Updating new evaluation sub factors with parent criteria Id*/
if(
  rule!AS_CO_UT_isBlank(ri!evalSubFactors),
  null,
  rule!AS_CO_UT_cast(
    type: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_Criteria?list',
    value: #"SYSTEM_SYSRULES_forEach"(
      items: ri!evalSubFactors,
      expression: a!localVariables(
        local!subFactor: fv!item,
        local!parentFactor: rule!AS_CO_UT_filterCdtByField(
          cdt: ri!evaluationFactors,
          field: "criteriaId",
          value: fv!item.parentCriteriaId
        ),
        rule!AS_CO_UT_captureMetadata(
          cdt: #"SYSTEM_SYSRULES_forEach"(
            items: local!subFactor,
            expression: rule!AS_CO_UT_updateCdtFieldsByFieldValuePairs(
              cdt: fv!item,
              fieldValuePairs: {
                /*Removed the condition to nullify criteria Id as we are writing to Criteria DSE while saving the factor in "Setup Factor" step*/
                {
                  field: "criteriaId",
                  value: fv!item.criteriaId
                },
                {
                  field: "parentCriteriaId",
                  value: if(
                    rule!AS_CO_UT_isBlank(local!parentFactor),
                    null,
                    a!flatten(local!parentFactor).criteriaId
                  )
                },
                {
                  field: "evaluation",
                  value: rule!AS_CO_UT_replaceNull(
                    nullableValue: fv!item.evaluation,
                    replacementValue: ri!evaluation
                  )
                },
                {
                  field: "criteriaStatus",
                  value: if(
                    rule!AS_CO_UT_isBlank(local!parentFactor),
                    null,
                    a!flatten(local!parentFactor).criteriaStatus
                  )
                },
                if(
                  rule!AS_CO_UT_isBlank(fv!item.assignments),
                  {},
                  {
                    field: "assignments",
                    value: rule!AS_CO_UT_captureMetadataIfUpdated(
                      originalCdt: if(
                        rule!AS_CO_UT_isBlank(fv!item.createdBy),
                        null,
                        rule!AS_CO_UT_indexPath(
                          cdt: a!flatten(
                            rule!AS_CO_UT_filterCdtByField(
                              cdt: ri!originalEvalSubFactors,
                              field: "criteriaId",
                              value: fv!item.criteriaId
                            )
                          ),
                          field: "assignments"
                        )
                      ),
                      updatedCdt: fv!item.assignments,
                      modifiedBy: ri!initiator
                    )
                  }
                )
              }
            )
          ),
          modifiedBy: ri!initiator
        )
      )
    )
  )
)

