a!localVariables(
  local!triggerRefresh,
  local!selectedTeamMemberships: rule!AS_CO_UT_initializeBlankLocalVariable(
    type: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_TeamMembership?list'
  ),
  {
    #"SYSTEM_SYSRULES_columnsLayout"(
      columns: {
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: {
            rule!AS_CO_CP_pickerFieldRecord(
              label: if(
                cons!AS_GSS_TOGGLE_AUTOMATION_TESTING_ENABLED,
                "Select Team Members",
                null
              ),
              placeholder: "Search for Evaluator(s) to add",
              recordType: recordType!AS_GSS_User_RECORD,
              value: touniformstring(ri!newMemberships),
              saveInto: ri!newMemberships,
              /*Filtering unadded users*/
              filters: #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!AS_GSS_User_RECORD.username,
                operator: "not in",
                value: rule!AS_CO_UT_rejectNullValuesFromArray(
                  touniformstring(
                    {
                      ri!newMemberships,
                      rule!AS_CO_UT_filterCdtByField(
                        cdt: ri!selectedEvaluatorTeam.teamMembership,
                        field: "isActive",
                        value: true
                      ).member.username
                    }
                  )
                )
              ),
              /*Disabling picker field when team is edited*/
              disabled: ri!isTeamEdited = true
            )
          }
        ),
        #"SYSTEM_SYSRULES_columnLayout"()
      }
    ),
    #"SYSTEM_SYSRULES_ButtonArrayLayout"(
      align: "START",
      buttons: {
        #"SYSTEM_SYSRULES_ButtonWidget"(
          label: "Add Team Member",
          icon: "plus",
          style: "SECONDARY",
          saveInto: {
            /*Adding the new team member / Reactivating the team member when re-added*/
            #"SYSTEM_SYSRULES_forEach"(
              items: ri!newMemberships,
              expression: /*Appending new team members to teamMemnership*/
              a!save(
                ri!selectedEvaluatorTeam.teamMembership,
                append(
                  ri!selectedEvaluatorTeam.teamMembership,
                  rule!AS_CO_UT_cast(
                    type: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_TeamMembership',
                    value: {
                      /*Mapping the temporary teamMembershipId to the teamMemberships to be used in the grid selection*/
                      teamMembershipId: if(
                        rule!AS_CO_UT_isBlank(
                          ri!selectedEvaluatorTeam.teamMembership
                        ),
                        1,
                        rule!AS_CO_UT_returnLastIndexOfArray(
                          ri!selectedEvaluatorTeam.teamMembership.teamMembershipId
                        ) + 1
                      ),
                      member: rule!AS_GSS_QE_getUser(
                        usernames: fv!item,
                        returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
                        executeWhen: rule!AS_CO_UT_isNotBlank(fv!item)
                      ),
                      isActive: true,
                      /*Using createdBy as reference to differentiate existing team members and members added newly*/
                      createdBy: null,
                      createdDatetime: null
                    }
                  )
                )
              )
            ),
            /*Saving the selectedEvaluatorTeam details back to evaluatorTream*/
            a!save(
              ri!evaluatorTeams[wherecontains(
                ri!selectedEvaluatorTeam.teamId, a!flatten(ri!evaluatorTeams).teamId
              )],
              ri!selectedEvaluatorTeam
            ),
            /*Updating the evaluatorTeam in factor and subfactor*/
            rule!AS_GSS_BL_saveIntoForCriteriaAssignmentsOnAddTeamMembership(
              evaluationFactors: if(
                rule!AS_CO_UT_isBlank(
                  rule!AS_CO_UT_filterCdtByField(
                    cdt: ri!evaluationFactors,
                    field: "isActive",
                    value: true
                  )
                ),
                null,
                ri!evaluationFactors[wherecontains(true, ri!evaluationFactors.isActive)]
              ),
              selectedEvaluatorTeam: ri!selectedEvaluatorTeam
            ),
            rule!AS_GSS_BL_saveIntoForCriteriaAssignmentsOnAddTeamMembership(
              evaluationFactors: if(
                rule!AS_CO_UT_isBlank(
                  rule!AS_CO_UT_filterCdtByField(
                    cdt: ri!evaluationSubFactors,
                    field: "isActive",
                    value: true
                  )
                ),
                null,
                ri!evaluationSubFactors[wherecontains(true, ri!evaluationSubFactors.isActive)]
              ),
              selectedEvaluatorTeam: ri!selectedEvaluatorTeam
            ),
            /*Generate alias names for new evaluators and update existing alias list*/
            a!save(
              ri!evaluatorAliases,
              append(
                ri!evaluatorAliases,
                rule!AS_GSS_generateAliasName(
                  username: difference(
                    ri!newMemberships,
                    touniformstring(index(ri!evaluatorAliases, "userName", {}))
                  ),
                  evaluationId: ri!evaluation.evaluationId,
                  existingUserNickName: ri!evaluatorAliases,
                  bundle: ri!i18nData
                )
              )
            ),
            a!save(local!triggerRefresh, now()),
            a!save(ri!newMemberships, null)
          },
          disabled: /*Disabling add member button when team is edited or first new member is picked*/
          or(
            rule!AS_CO_UT_isBlank(ri!newMemberships),
            ri!isTeamEdited = true
          ),
          accessibilityText: "Press enter to add team member"
        ),
        #"SYSTEM_SYSRULES_ButtonWidget"(
          label: "Delete Team Member",
          icon: "trash",
          style: "SECONDARY",
          saveInto: {
            /*Save isActive as false for deleted team memberships*/
            #"SYSTEM_SYSRULES_forEach"(
              items: local!selectedTeamMemberships,
              expression: if(
                rule!AS_CO_UT_isNotBlank(a!flatten(fv!item).createdBy),
                a!save(
                  ri!selectedEvaluatorTeam.teamMembership,
                  rule!AS_CO_UT_updateCdtFields(
                    cdt: ri!selectedEvaluatorTeam.teamMembership,
                    fields: { "isActive" },
                    indices: wherecontains(
                      tointeger(a!flatten(fv!item).teamMembershipId),
                      tointeger(
                        a!flatten(
                          ri!selectedEvaluatorTeam.teamMembership
                        ).teamMembershipId
                      )
                    ),
                    newValues: { false }
                  )
                ),
                /*Remove deleted team memberships that is added in the current instance*/
                a!save(
                  ri!selectedEvaluatorTeam.teamMembership,
                  remove(
                    ri!selectedEvaluatorTeam.teamMembership,
                    wherecontains(
                      tointeger(fv!item.teamMembershipId),
                      tointeger(
                        a!flatten(
                          ri!selectedEvaluatorTeam.teamMembership
                        ).teamMembershipId
                      )
                    )
                  )
                )
              )
            ),
            /*Saving the selectedEvaluatorTeam details back to evaluatorTream*/
            a!save(
              ri!evaluatorTeams[wherecontains(
                ri!selectedEvaluatorTeam.teamId, a!flatten(ri!evaluatorTeams).teamId
              )],
              ri!selectedEvaluatorTeam
            ),
            /*Marking isActive false for the deleted team memberships in evaluation factors and subfactors*/
            rule!AS_GSS_BL_saveIntoForCriteriaAssignmentsOnDeleteTeamMembership(
              evaluationFactors: if(
                rule!AS_CO_UT_isBlank(
                  rule!AS_CO_UT_filterCdtByField(
                    cdt: ri!evaluationFactors,
                    field: "isActive",
                    value: true
                  )
                ),
                null,
                ri!evaluationFactors[wherecontains(true, ri!evaluationFactors.isActive)]
              ),
              deletedTeamMemberships: a!flatten(local!selectedTeamMemberships).member.username,
              selectedEvaluatorTeam: ri!selectedEvaluatorTeam
            ),
            rule!AS_GSS_BL_saveIntoForCriteriaAssignmentsOnDeleteTeamMembership(
              evaluationFactors: if(
                rule!AS_CO_UT_isBlank(
                  rule!AS_CO_UT_filterCdtByField(
                    cdt: ri!evaluationSubFactors,
                    field: "isActive",
                    value: true
                  )
                ),
                null,
                ri!evaluationSubFactors[wherecontains(true, ri!evaluationSubFactors.isActive)]
              ),
              deletedTeamMemberships: a!flatten(local!selectedTeamMemberships).member.username,
              selectedEvaluatorTeam: ri!selectedEvaluatorTeam
            ),
            a!save(local!triggerRefresh, now()),
            /*Nullifying the selection after a team memberships is deleted*/
            a!save(
              local!selectedTeamMemberships,
              rule!AS_CO_UT_initializeBlankLocalVariable(
                type: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_TeamMembership?list'
              )
            )
          },
          disabled: or(
            rule!AS_CO_UT_isBlank(local!selectedTeamMemberships),
            ri!isTeamEdited = true
          ),
          confirmMessage: "Are you sure you want to delete the team member(s)?",
          accessibilityText: "Press enter to delete team member(s)"
        )
      }
    ),
    rule!AS_GSS_GRD_evaluatorTeamMemberships(
      selectedEvaluatorTeam: ri!selectedEvaluatorTeam,
      i18nData: ri!i18nData,
      triggerRefresh: local!triggerRefresh,
      selectedTeamMemberships: local!selectedTeamMemberships,
      evaluatorAliases: ri!evaluatorAliases,
      evaluation: ri!evaluation
    )
  }
)

