_a-0000e5da-a251-8000-9bbe-011c48011c48_.sail_code:
a!localVariables(
  local!ratingTypes: rule!AS_GSS_QE_getRefRatings(
    additionalFilters: #"SYSTEM_SYSRULES_queryFilter"(
      field: "parentRatingId",
      operator: "is null"
    )
  ),
  local!triggerRefresh,
  local!selectedEvaluationFactor: if(
    rule!AS_CO_UT_isBlank(ri!selectedEvaluationFactors),
    null,
    ri!selectedEvaluationFactors[1]
  ),
  local!evaluationFactor: a!refreshVariable(
    value: local!selectedEvaluationFactor,
    refreshOnReferencedVarChange: false,
    refreshOnVarChange: {
      local!triggerRefresh,
      ri!isUnsavedFactor,
      ri!readOnly
    }
  ),
  /*This interface will be used for both factors and sub-factors*/
  #"SYSTEM_SYSRULES_sectionLayout_v1"(
    label: if(
      ri!isUnsavedFactor = true,
      rule!AS_GAM_CO_I18N_UT_displayLabel(
        bundle: ri!i18nData,
        bundleKey: if(
          ri!isSubFactor = true,
          "lbl_NewSubFactor",
          "lbl_NewFactor"
        )
      ),
      if(
        ri!isSubFactor = true,
        "",
        "Summary"
      )
    ),
    labelColor: "STANDARD",
    labelHeadingTag: "H3",
    labelSize: "MEDIUM",
    contents: {
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              rule!AS_CO_INP_textField(
                label: "ID" & " *",
                required: true,
                readOnly: ri!readOnly,
                value: local!selectedEvaluationFactor.factorNumber,
                validations: rule!AS_GSS_VD_duplicateFactorIdValidation(
                  evaluationFactors: rule!AS_CO_UT_rejectCdtByField(
                    cdt: ri!evaluationFactors,
                    field: "criteriaId",
                    value: local!selectedEvaluationFactor.criteriaId
                  ),
                  selectedEvalFactor: local!selectedEvaluationFactor,
                  isSubFactor: ri!isSubFactor
                ),
                saveInto: local!selectedEvaluationFactor.factorNumber,
                validationGroup: "SAVE NEXT " & ri!confirmValidationGroup,
                labelPosition: "ABOVE",
                placeholder: "E.g.: 123456",
                maxLength: cons!AS_GAM_ENUM_TEXT_LENGTH_MEDIUM_SHORT
              ),
              rule!AS_CO_INP_dateField(
                label: "Due Date",
                readOnly: ri!readOnly,
                value: local!selectedEvaluationFactor.dueDate,
                validations: rule!AS_GSS_VD_criteriaDueDateValidation(
                  evalStartDate: ri!evaluation.evaluationStartDate,
                  evalDueDate: ri!evaluation.evaluationDueDate,
                  readOnly: ri!readOnly,
                  factorDueDate: local!selectedEvaluationFactor.dueDate,
                  isSubFactor: ri!isSubFactor,
                  parentFactorDueDate: if(
                    ri!isSubFactor = true,
                    ri!parentEvaluationFactor.dueDate,
                    ""
                  )
                ),
                saveInto: local!selectedEvaluationFactor.dueDate,
                validationGroup: ri!confirmValidationGroup,
                labelPosition: "ABOVE"
              ),
              if(
                ri!readOnly = true,
                rule!AS_GSS_CPS_richTextWithShowMoreLess(
                  label: "Description",
                  labelPosition: "ABOVE",
                  showHyphen: true,
                  text: local!selectedEvaluationFactor.criteriaDesc,
                  shortenToLen: cons!AS_CO_ENUM_PARAGRAPH_LENGTH_SHORT,
                  i18nData: ri!i18nData,
                  marginBelow: "STANDARD"
                ),
                rule!AS_CO_INP_paragraphField(
                  label: "Description",
                  instructions: rule!AS_CO_UT_instructionForCharactersEntered(
                    value: local!selectedEvaluationFactor.criteriaDesc,
                    maxCharacters: cons!AS_CO_ENUM_PARAGRAPH_LENGTH_LARGE
                  ),
                  value: local!selectedEvaluationFactor.criteriaDesc,
                  saveInto: local!selectedEvaluationFactor.criteriaDesc,
                  labelPosition: "ABOVE",
                  validationGroup: ri!confirmValidationGroup,
                  placeholder: "E.g.: This evaluation factor is to assess the technical aspects of the proposal",
                  maxLength: cons!AS_CO_ENUM_PARAGRAPH_LENGTH_LARGE
                )
              ),
              /*Instructions for Factors*/
              if(
                ri!isSubFactor,
                {},
                if(
                  ri!readOnly = true,
                  rule!AS_GSS_CPS_richTextWithShowMoreLess(
                    label: "Instructions",
                    labelPosition: "ABOVE",
                    showHyphen: true,
                    text: local!selectedEvaluationFactor.instruction,
                    shortenToLen: cons!AS_CO_ENUM_PARAGRAPH_LENGTH_SHORT,
                    i18nData: ri!i18nData,
                    marginBelow: "STANDARD"
                  ),
                  rule!AS_CO_INP_paragraphField(
                    label: "Instructions",
                    instructions: rule!AS_CO_UT_instructionForCharactersEntered(
                      value: local!selectedEvaluationFactor.instruction,
                      maxCharacters: cons!AS_CO_ENUM_PARAGRAPH_LENGTH_LARGE
                    ),
                    value: local!selectedEvaluationFactor.instruction,
                    saveInto: local!selectedEvaluationFactor.instruction,
                    labelPosition: "ABOVE",
                    maxLength: cons!AS_CO_ENUM_PARAGRAPH_LENGTH_LARGE
                  )
                )
              )
            }
          ),
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              rule!AS_CO_INP_textField(
                label: "Title" & " *",
                required: true,
                readOnly: ri!readOnly,
                value: index(
                  local!selectedEvaluationFactor,
                  "criteriaName",
                  {}
                ),
                validations: rule!AS_GSS_VD_duplicateCriteriaTitleValidation(
                  evaluationFactors: rule!AS_CO_UT_rejectCdtByField(
                    cdt: ri!evaluationFactors,
                    field: "criteriaId",
                    value: local!selectedEvaluationFactor.criteriaId
                  ),
                  selectedEvalFactor: local!selectedEvaluationFactor,
                  isSubFactor: ri!isSubFactor
                ),
                saveInto: local!selectedEvaluationFactor.criteriaName,
                validationGroup: "SAVE NEXT " & ri!confirmValidationGroup,
                labelPosition: "ABOVE",
                placeholder: "E.g.: Technical Evaluation",
                maxLength: cons!AS_GAM_ENUM_TEXT_LENGTH_LARGE
              ),
              if(
                and(
                  ri!readOnly = true,
                  rule!AS_CO_UT_isBlank(
                    local!selectedEvaluationFactor.criteriaChair.username
                  )
                ),
                rule!AS_CO_DSP_standardReadOnlyField(
                  label: "Factor Chair",
                  value: "-",
                  labelPosition: "ABOVE"
                ),
                rule!AS_CO_CP_pickerFieldRecord(
                  labelPosition: "ABOVE",
                  showWhen: rule!AS_CO_UT_not(ri!isSubFactor = true),
                  recordType: recordType!AS_GSS_User_RECORD,
                  placeholder: "Search users",
                  saveInto: {
                    a!save(
                      local!selectedEvaluationFactor.criteriaChair,
                      rule!AS_GSS_QE_getUser(
                        returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
                        executeWhen: rule!AS_CO_UT_isNotBlank(save!value),
                        usernames: save!value
                      )
                    )
                  },
                  value: local!selectedEvaluationFactor.criteriaChair.username,
                  maxSelections: 1,
                  label: "Factor Chair",
                  readOnly: ri!readOnly
                )
              ),
              rule!AS_CO_CP_dropdownField(
                label: "Rating",
                labelPosition: "ABOVE",
                readOnly: ri!readOnly,
                choiceLabels: local!ratingTypes.refLabel,
                choiceValues: local!ratingTypes.refRatingId,
                placeholderLabel: "Select a rating method",
                value: local!selectedEvaluationFactor.refRatingType.refRatingId,
                saveInto: a!save(
                  local!selectedEvaluationFactor.refRatingType,
                  if(
                    rule!AS_CO_UT_isBlank(save!value),
                    null,
                    rule!AS_CO_UT_filterCdtByField(
                      cdt: local!ratingTypes,
                      field: "refRatingId",
                      value: save!value
                    )
                  )
                )
              ),
              if(
                ri!readOnly = true,
                #"SYSTEM_SYSRULES_richTextDisplayField"(
                  label: "Instructions",
                  labelPosition: "ABOVE",
                  value: local!selectedEvaluationFactor.instruction,
                  showWhen: ri!isSubFactor
                ),
                rule!AS_CO_INP_paragraphField(
                  label: "Instructions",
                  instructions: rule!AS_CO_UT_instructionForCharactersEntered(
                    value: local!selectedEvaluationFactor.instruction,
                    maxCharacters: cons!AS_CO_ENUM_PARAGRAPH_LENGTH_LARGE
                  ),
                  value: local!selectedEvaluationFactor.instruction,
                  saveInto: local!selectedEvaluationFactor.instruction,
                  labelPosition: "ABOVE",
                  maxLength: cons!AS_CO_ENUM_PARAGRAPH_LENGTH_LARGE,
                  showWhen: ri!isSubFactor
                )
              ),

            }
          )
        }
      ),
      #"SYSTEM_SYSRULES_sideBySideLayout"(items: { #"SYSTEM_SYSRULES_sideBySideItem"(width: "2X") }),
      if(
        rule!AS_CO_UT_not(ri!readOnly = true),
        rule!AS_CO_CPS_cancelSaveButtons(
          confirmLabel: "Save",
          cancelLabel: "Cancel",
          confirmSaveInto: {
            a!save(
              ri!evaluationFactors[tointeger(
                wherecontains(
                  tointeger(
                    a!flatten(local!selectedEvaluationFactor).criteriaId
                  ), a!flatten(ri!evaluationFactors).criteriaId
                )
              )],
              a!flatten(local!selectedEvaluationFactor)
            ),
            a!save(ri!readOnly, true),
            a!save(ri!isUnsavedFactor, false),
            a!save(local!triggerRefresh, now()),
            if(
              or(
                ri!isSubFactor = true,
                rule!AS_CO_UT_isBlank(ri!associatedEvaluationSubFactors)
              ),
              {},
              a!save(
                ri!associatedEvaluationSubFactors[wherecontains(
                  ri!selectedEvaluationFactors.criteriaId, ri!associatedEvaluationSubFactors.parentCriteriaId
                )].criteriaChair,
                if(
                  rule!AS_CO_UT_isBlank(
                    local!selectedEvaluationFactor.criteriaChair
                  ),
                  null,
                  repeat(
                    length(
                      index(
                        ri!associatedEvaluationSubFactors,
                        wherecontains(
                          ri!selectedEvaluationFactors.criteriaId,
                          ri!associatedEvaluationSubFactors.parentCriteriaId
                        )
                      )
                    ),
                    local!selectedEvaluationFactor.criteriaChair
                  )
                )
              )
            ),
            #"SYSTEM_SYSRULES_writeToDataStoreEntity"(
              dataStoreEntity: cons!AS_GSS_ENT_CRITERIA,
              valueToStore: local!selectedEvaluationFactor,
              onSuccess: {
                a!save(
                  local!selectedEvaluationFactor,
                  fv!storedValues
                ),
                a!save(
                  ri!evaluationFactors,
                  #"SYSTEM_SYSRULES_forEach"(
                    items: ri!evaluationFactors,
                    expression: if(
                      rule!AS_CO_UT_isBlank(fv!item.criteriaId),
                      local!selectedEvaluationFactor,
                      fv!item
                    )
                  )
                )
              }
            ),
            /*Nullifying the selection after a sub-factor is edited and saved*/
            if(
              ri!isSubFactor = true,
              a!save(
                ri!selectedEvaluationFactors,
                rule!AS_CO_UT_initializeBlankLocalVariable(
                  type: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_Criteria?list'
                )
              ),
              a!save(
                ri!selectedEvaluationFactors,
                local!selectedEvaluationFactor
              )
            )
          },
          cancelSaveInto: {
            a!save(
              local!selectedEvaluationFactor,
              local!evaluationFactor
            ),
            /*For newly added factor / sub-factor, the selected factor needs to be removed from the index when canceled*/
            if(
              ri!isUnsavedFactor = true,
              {
                a!save(
                  ri!evaluationFactors,
                  remove(
                    ri!evaluationFactors,
                    wherecontains(
                      tointeger(
                        index(
                          local!selectedEvaluationFactor,
                          "criteriaId",
                          null
                        )
                      ),
                      index(ri!evaluationFactors, "criteriaId", null)
                    )
                  )
                ),
                if(
                  ri!isSubFactor = true,
                  null,
                  a!save(
                    ri!selectedEvaluationFactors,
                    if(
                      rule!AS_CO_UT_isNotBlank(ri!evaluationFactors),
                      ri!evaluationFactors[1],
                      rule!AS_CO_UT_initializeBlankLocalVariable(
                        type: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_Criteria?list'
                      )
                    )
                  )
                )
              },
              {}
            ),
            a!save(ri!readOnly, true),
            a!save(ri!isUnsavedFactor, false),
            /*Nullifying the selection after a sub-factor is edited and canceled*/
            if(
              ri!isSubFactor = true,
              a!save(
                ri!selectedEvaluationFactors,
                rule!AS_CO_UT_initializeBlankLocalVariable(
                  type: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_Criteria?list'
                )
              ),
              null
            )
          },
          confirmAccessibilityText: "Press Enter to save changes",
          cancelAccessibilityText: "Press Enter to cancel changes",
          confirmValidationGroup: ri!confirmValidationGroup
        ),
        {}
      )
    },
    showWhen: ri!showWhen
  )
)

/*We are capturing metadata for newObjects by compaing old and new objects to write to Object DSE*/
rule!AS_CO_UT_castToMapList(
  value: #"SYSTEM_SYSRULES_forEach"(
    items: ri!auditParams.newObjects,
    expression: rule!AS_ADT_UT_captureMetadataDuringAudit(
      newObject: fv!item,
      oldObject: index(
        if(
          rule!AS_CO_UT_isBlank(ri!auditParams.complexFieldName),
          if(
            rule!AS_CO_UT_isBlank(ri!auditParams.auditConfig.idField),
            null,
            rule!AS_CO_UT_filterCdtByField(
              cdt: ri!auditParams.originalObjects,
              field: ri!auditParams.auditConfig.idField,
              value: fv!item[ri!auditParams.auditConfig.idField]
            )
          ),
          if(
            rule!AS_CO_UT_isBlank(
              index(
                rule!AS_CO_UT_filterCdtByField(
                  cdt: ri!auditParams.auditConfig.complexFields,
                  field: "auditFieldName",
                  value: tostring(ri!auditParams.complexFieldName)
                ),
                "idField",
                null
              )
            ),
            null,
            rule!AS_CO_UT_filterCdtByField(
              cdt: ri!auditParams.originalObjects,
              field: tostring(
                index(
                  rule!AS_CO_UT_filterCdtByField(
                    cdt: ri!auditParams.auditConfig.complexFields,
                    field: "auditFieldName",
                    value: tostring(ri!auditParams.complexFieldName)
                  ),
                  "idField",
                  null
                )
              ),
              value: fv!item[tostring(
                index(
                  rule!AS_CO_UT_filterCdtByField(
                    cdt: ri!auditParams.auditConfig.complexFields, field: "auditFieldName", value: tostring(ri!auditParams.complexFieldName)
                  ), "idField", null
                )
              )]
            )
          )
        ),
        1,
        null
      ),
      auditConfig: ri!auditParams.auditConfig,
      modifiedBy: ri!auditParams.username
    )
  )
)

#"SYSTEM_SYSRULES_forEach"(
  items: ri!subFactors,
  expression: if(
    fv!item.isActive = false,
    fv!item,
    /*Applying the parent factor assignment to subfactors*/
    rule!AS_CO_UT_updateCdtFieldsByFieldValuePairs(
      cdt: fv!item,
      fieldValuePairs: {
        {
          field: "evaluatorTeam",
          value: ri!parentFactor.evaluatorTeam
        },
        {
          field: "assignments",
          value: /*Marking isActive false / deleteting existing assignments of subfactors*/
          rule!AS_CO_UT_cast(
            type: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_Criteria_Assignments?list',
            value: rule!AS_CO_UT_rejectNullValuesFromArray(
              {
                #"SYSTEM_SYSRULES_forEach"(
                  items: rule!AS_CO_UT_filterCdtByField(
                    cdt: fv!item.assignments,
                    field: "isActive",
                    value: true
                  ),
                  expression: if(
                    /*Retaining existing active assigments from the subfactor that are in the parent as well*/
                    contains(
                      rule!AS_CO_UT_indexPath(
                        cdt: rule!AS_CO_UT_filterCdtByField(
                          cdt: ri!parentFactor.assignments,
                          field: "isActive",
                          value: true
                        ),
                        field: "assignee.username"
                      ),
                      tostring(fv!item.assignee.username)
                    ),
                    fv!item,
                    if(
                      rule!AS_CO_UT_isBlank(fv!item.assignmentId),
                      null,
                      rule!AS_CO_UT_updateCdtFields(
                        cdt: fv!item,
                        fields: "isActive",
                        newValues: false
                      )
                    )
                  )
                ),
                /*Retaining inactive entries*/
                rule!AS_CO_UT_filterCdtByField(
                  cdt: fv!item.assignments,
                  field: "isActive",
                  value: false
                ),
                /*Updating and adding the assignments from parent factor*/
                if(
                  rule!AS_CO_UT_isBlank(
                    difference(
                      rule!AS_CO_UT_filterCdtByField(
                        cdt: ri!parentFactor.assignments,
                        field: "isActive",
                        value: true
                      ).assignee.username,
                      rule!AS_CO_UT_filterCdtByField(
                        cdt: fv!item.assignments,
                        field: "isActive",
                        value: true
                      ).assignee.username
                    )
                  ),
                  null,
                  #"SYSTEM_SYSRULES_forEach"(
                    items: difference(
                      rule!AS_CO_UT_filterCdtByField(
                        cdt: ri!parentFactor.assignments,
                        field: "isActive",
                        value: true
                      ).assignee.username,
                      rule!AS_CO_UT_filterCdtByField(
                        cdt: fv!item.assignments,
                        field: "isActive",
                        value: true
                      ).assignee.username
                    ),
                    expression: rule!AS_CO_UT_cast(
                      type: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_Criteria_Assignments',
                      value: {
                        assignmentId: null,
                        assignee: rule!AS_CO_UT_filterCdtByField(
                          cdt: ri!parentFactor.assignments,
                          field: "assignee.username",
                          value: fv!item
                        ).assignee,
                        isActive: true
                      }
                    )
                  )
                )
              }
            )
          )
        }
      }
    )
  )
)

if(
  rule!AS_CO_UT_isBlank(ri!evaluatorTeams),
  null,
  rule!AS_CO_UT_captureMetadata(
    cdt: #"SYSTEM_SYSRULES_forEach"(
      items: ri!evaluatorTeams,
      expression: rule!AS_CO_UT_updateCdtFieldsByFieldValuePairs(
        cdt: rule!AS_CO_UT_updateCdtFields(
          cdt: fv!item,
          fields: { "teamId", "evaluation" },
          newValues: {
            /*updating the tempPK and evaluation fields*/
            if(
              rule!AS_CO_UT_isBlank(fv!item.createdBy),
              null,
              fv!item.teamId
            ),
            rule!AS_CO_UT_replaceNull(
              nullableValue: fv!item.evaluation,
              replacementValue: ri!evaluation
            )
          }
        ),
        /*updating the tempPK and metadata of teamMembership*/
        fieldValuePairs: {
          field: "teamMembership",
          value: rule!AS_GSS_UT_updateEvaluatorTeamMembershipOnSubmit(
            evalTeam: fv!item,
            initiator: ri!initiator
          )
        }
      )
    ),
    modifiedBy: ri!initiator
  )
)

_a-0000e5bc-4a9a-8000-9bbc-011c48011c48_.sail_code:
/* This rule is used to construct the action only audit data (Simple / Complex field changes will be null)*/
rule!AS_CO_UT_dynamicDictionary(
  fieldValuePairs: {
    {
      field: ri!auditParams.auditConfig.idField,
      value: index(
        ri!auditParams.newObjects,
        ri!auditParams.auditConfig.idField,
        null
      )
    },
    { field: "username", value: ri!initiator },
    { field: "timestamp", value: now() },
    {
      field: "auditActionCode",
      value: ri!auditParams.externalAction
    },
    if(
      rule!AS_CO_UT_isNotBlank(
        ri!auditParams.auditConfig.foreignIdField
      ),
      {
        field: ri!auditParams.auditConfig.foreignIdField,
        value: index(
          ri!auditParams.newObjects,
          ri!auditParams.auditConfig.foreignIdField,
          null
        )
      },
      {}
    )
  }
)

_a-0000e5da-a251-8000-9bbe-011c48011c48_.sail_code:
#"SYSTEM_SYSRULES_ButtonArrayLayout"(
  align: "CENTER",
  buttons: #"SYSTEM_SYSRULES_buttonWidget_v1"(
    label: "New Factor",
    icon: "plus",
    /*Disabled when a factor is edited / new factor is been set up*/
    disabled: or(
      ri!isUnsavedFactor = true,
      ri!readOnly = false,
      ri!showSubFactorGrid = false
    ),
    saveInto: {
      a!save(ri!selectedEvaluationFactor, null),
      /*Construct new evaluation factor*/
      a!save(
        ri!selectedEvaluationFactor,
        rule!AS_CO_UT_cast(
          type: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_Criteria',
          value: {
            /*Mapping the temporary criteria Id to be used for associating with sub factors*/
            /*criteriaId: if(*/
            /*rule!AS_CO_UT_isBlank(ri!evaluationFactors),*/
            /*1,*/
            /*rule!AS_CO_UT_returnLastIndexOfArray(ri!evaluationFactors).criteriaId + 1*/
            /*),*/
            evaluation: ri!evaluation,
            criteriaStatus: rule!AS_GSS_QE_getRefDataByTypes(
              refDataIds: cons!AS_GSS_REF_ID_EVALUATION_STATUS_TEMP
            ),
            isActive: true,
            isDefaultEntry: false(),
            createdBy: null,
            createdDatetime: null
          }
        )
      ),
      /*Append the newly added factor to the evaluation factors*/
      a!save(
        ri!evaluationFactors,
        append(
          ri!evaluationFactors,
          ri!selectedEvaluationFactor
        )
      ),
      a!save(ri!readOnly, false),
      a!save(ri!isUnsavedFactor, true)
    },
    width: "FILL",
    accessibilityText: "Click to add new factor"
  )
)

if(
  /*Action only audit*/
  ri!auditParams.auditType = cons!AS_GAM_ADT_TXT_AUDIT_TYPE_ACTION_ONLY,
  rule!AS_GAM_ADT_UT_constructActionOnlyAuditData(
    initiator: ri!initiator,
    auditParams: ri!auditParams
  ),
  /*Manipulation Audit*/
  if(
    ri!auditParams.auditType = cons!AS_GAM_ADT_TXT_AUDIT_TYPE_MANIPULATION,
    rule!AS_GAM_ADT_UT_constructManipulatedAuditData(
      initiator: ri!initiator,
      auditParams: ri!auditParams
    ),
    /*Generic field level audit*/
    rule!AS_GAM_ADT_UT_buildFieldLevelAuditEntityData(auditParams: ri!auditParams)
  )
)

rule!AS_CO_UT_cast(
  type: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_Evaluation',
  value: {
    evaluationStatus: {
      refDataId: cons!AS_GSS_REF_ID_EVALUATION_STATUS_TEMP
    },
    sourceApplication: {
      refDataId: cons!AS_GSS_REF_ID_SOURCE_APPLICATION_GSS
    },
    isActive: false()
  }
)

