                        #"SYSTEM_SYSRULES_writeToDataStoreEntity"(
                          dataStoreEntity: cons!AS_GSS_ENT_FACTOR_DOCUMENT_MAPPING,
                          valueToStore: local!updatedFactorDocMappings
                        )
                      )
                    )
