              local!showSubFactorGrid = false,
              local!evalTeamReadOnly = false
            ),
            showWhen: not(local!isOnFirstPage),
            validate: false,
            accessibilityText: "Press Enter to go to previous step"
          ),
          #"SYSTEM_SYSRULES_buttonWidget_v1"(
            label: "Cancel",
            value: cons!AS_CO_ENUM_USER_ACTION_CANCEL,
            saveInto: {
              /* On cancel,
              rules need to run to compare the original ri!vendorDocs to the local!vendorDocs
              &
              ri!evaluationVendors to local!evaluationVendors
              Find the difference in both cdts, pass to PM to revert changes
              -- Create revert PM that will go and change isActive/isDeleted flags
              */
              ri!userAction,
              a!save(
                ri!evaluationFactors,
                rule!AS_GSS_UT_evaluationFactorCancelReconciliation(
                  originalFactors: ri!originalEvaluationFactors,
                  newFactors: ri!evaluationFactors
                )
              ),
              a!save(
                ri!evaluationSubFactors,
                rule!AS_GSS_UT_evaluationFactorCancelReconciliation(
                  originalFactors: ri!originalEvaluationSubFactors,
                  newFactors: ri!evaluationSubFactors
                )
              )
            },
            submit: true,
            style: "LINK",
            validate: false,
            confirmHeader: if(
