/*Updating new evaluation factors (Removing the temporary PK*/
if(
  rule!AS_CO_UT_isBlank(ri!evalFactors),
  null,
  rule!AS_CO_UT_captureMetadata(
    cdt: #"SYSTEM_SYSRULES_forEach"(
      items: ri!evalFactors,
      expression: rule!AS_CO_UT_updateCdtFieldsByFieldValuePairs(
        cdt: fv!item,
        fieldValuePairs: {
          /*Removed the condition to nullify criteria Id as we are writing to Criteria DSE while saving the factor in "Setup Factor" step*/
          {
            field: "criteriaId",
            value: fv!item.criteriaId
          },
          {
            field: "evaluation",
            value: rule!AS_CO_UT_replaceNull(
              nullableValue: fv!item.evaluation,
              replacementValue: ri!evaluation
            )
          },
          {
            field: "criteriaStatus",
            value: ri!evaluation.evaluationStatus
          },
          if(
            rule!AS_CO_UT_isBlank(fv!item.assignments),
            {},
            {
              field: "assignments",
              value: rule!AS_CO_UT_captureMetadataIfUpdated(
                originalCdt: if(
                  rule!AS_CO_UT_isBlank(fv!item.createdBy),
                  null,
                  rule!AS_CO_UT_indexPath(
                    cdt: a!flatten(
                      rule!AS_CO_UT_filterCdtByField(
                        cdt: ri!originalEvalFactors,
                        field: "criteriaId",
                        value: fv!item.criteriaId
                      )
                    ),
                    field: "assignments"
                  )
                ),
                updatedCdt: fv!item.assignments,
                modifiedBy: ri!initiator
              )
            }
          ),
          {
            field: "factorWeight",
            value: if(
              ri!evaluation.isWeightedFactorsRequired,
              fv!item.factorWeight,
              null
            )
          }
        }
      )
    ),
    modifiedBy: ri!initiator
  )
)

#"SYSTEM_SYSRULES_forEach"(
  items: ri!auditParams,
  expression: rule!AS_CO_UT_updateCdtFieldsByFieldValuePairs(
    cdt: fv!item,
    fieldValuePairs: {
      field: "originalObjects",
      value: rule!AS_CO_UT_replaceNull(
        nullableValue: a!flatten(fv!item.originalObjects),
        replacementValue: if(
          /*Initializing null when the objectDse is null*/
          rule!AS_CO_UT_isBlank(fv!item.objectDse),
          rule!AS_CO_UT_initializeBlankLocalVariable(
            type: a!flatten(fv!item).objectTypeNumber
          ),
          if(
            rule!AS_CO_UT_isBlank(fv!item.complexFieldName),
            if(
              /*Initializing null when the idField in the audit config is null*/
              rule!AS_CO_UT_isBlank(
                index(
                  a!flatten(fv!item.auditConfig),
                  "idField",
                  null
                )
              ),
              rule!AS_CO_UT_initializeBlankLocalVariable(
                type: a!flatten(fv!item).objectTypeNumber
              ),
              rule!AS_CO_UT_replaceNull(
                nullableValue: rule!AS_ADT_UT_getOriginalObjects(auditParams: fv!item),
                replacementValue: rule!AS_CO_UT_initializeBlankLocalVariable(
                  type: a!flatten(fv!item).objectTypeNumber
                )
              )
            ),
            if(
              /*Initializing null when idField of complexField's auditConfig is null*/
              rule!AS_CO_UT_isBlank(
                index(
                  rule!AS_CO_UT_filterCdtByField(
                    cdt: fv!item.auditConfig.complexFields,
                    field: "auditFieldName",
                    value: tostring(fv!item.complexFieldName)
                  ),
                  "idField",
                  null
                )
              ),
              rule!AS_CO_UT_initializeBlankLocalVariable(
                type: a!flatten(fv!item).objectTypeNumber
              ),
              /*Getting the original object for the complexFieldName object*/
              rule!AS_CO_UT_replaceNull(
                nullableValue: rule!AS_ADT_UT_getOriginalObjects(
                  auditParams: rule!AS_CO_UT_updateCdtFields(
                    cdt: fv!item,
                    fields: "auditConfig",
                    newValues: rule!AS_CO_UT_filterCdtByField(
                      cdt: fv!item.auditConfig.complexFields,
                      field: "auditFieldName",
                      value: tostring(fv!item.complexFieldName)
                    )
                  )
                ),
                replacementValue: rule!AS_CO_UT_initializeBlankLocalVariable(
                  type: a!flatten(fv!item).objectTypeNumber
                )
              )
            )
          )
        )
      )
    }
  )
)

_a-0000e5bc-4a9a-8000-9bbc-011c48011c48_.sail_code:
rule!AS_CO_UT_captureMetadata(
  cdt: rule!AS_CO_UT_updateCdtFields(
    cdt: ri!evaluation,
    fields: {
      "isSignaturesRequired",
      "isWeightedFactorsRequired"
    },
    newValues: {
      a!match(
        value: ri!evaluation.isSignaturesRequired,
        /*To handle defaulting*/
        whenTrue: and(
          cons!AS_GSS_TOGGLE_CONSENSUS_REPORT_SIGNATURE,
          a!isNullOrEmpty(fv!value)
        ),
        then: true,
        /*If AS_GSS_TOGGLE_CONSENSUS_REPORT_SIGNATURE is OFF, then it applies to
      only newly Set Up Evaluations and all other In Progress/Completed 
      Evaluations will follow the evaluation level toggle*/
        whenTrue: not(cons!AS_GSS_TOGGLE_CONSENSUS_REPORT_SIGNATURE),
        then: false,
        default: fv!value
      ),
      a!defaultValue(
        value: ri!evaluation.isWeightedFactorsRequired,
        default: true
      )
    }
  ),
  modifiedBy: ri!initiator
)

