_a-0000e5da-a251-8000-9bbe-011c48011c48_.sail_code:
#"SYSTEM_SYSRULES_sectionLayout_v1"(
  contents: {
    #"SYSTEM_SYSRULES_sectionLayout_v1"(
      label: "Evaluator Teams",
      labelColor: "STANDARD",
      labelHeadingTag: "H2",
      labelSize: "MEDIUM",
      contents: {}
    ),
    /*list of teams*/
    #"SYSTEM_SYSRULES_forEach"(
      items: rule!AS_CO_UT_filterCdtByField(
        cdt: { ri!evaluatorTeams },
        field: "isActive",
        value: true
      ),
      expression: a!localVariables(
        local!activeTeamMembers: a!refreshVariable(
          value: rule!AS_CO_UT_filterCdtByField(
            cdt: fv!item.teamMembership,
            field: "isActive",
            value: true
          ),
          refreshOnReferencedVarChange: true,
          refreshOnVarChange: ri!selectedEvaluatorTeam.teamMembership
        ),
        local!isCurrentItemSelected: a!refreshVariable(
          value: if(
            rule!AS_CO_UT_isNotBlank(ri!selectedEvaluatorTeam.createdBy),
            and(
              rule!AS_CO_UT_isNotBlank(fv!item.createdBy),
              tointeger(fv!item.teamId) = tointeger(ri!selectedEvaluatorTeam.teamId)
            ),
            and(
              rule!AS_CO_UT_isBlank(fv!item.createdBy),
              tointeger(fv!item.teamId) = tointeger(ri!selectedEvaluatorTeam.teamId)
            )
          ),
          refreshOnReferencedVarChange: true,
          refreshOnVarChange: ri!selectedEvaluatorTeam
        ),
        #"SYSTEM_SYSRULES_cardLayout"(
          showBorder: false,
          /*Saving the current index to selectedEvaluationTeam*/
          link: #"SYSTEM_SYSRULES_dynamicLink"(
            saveInto: {
              a!save(ri!selectedEvaluatorTeam, fv!item),
              /*Nullifying new members picked if any*/
              a!save(ri!newMemberships, null)
            },
            showWhen: /*Disabling the selection when other team is edited*/
            rule!AS_CO_UT_not(
              or(
                ri!isUnsavedTeam = true,
                ri!readOnly = false
              )
            )
          ),
          style: if(
            local!isCurrentItemSelected = true,
            "ACCENT",
            "NONE"
          ),
          contents: {
            #"SYSTEM_SYSRULES_richTextDisplayField"(
              value: {
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: if(
                    rule!AS_CO_UT_isBlank(fv!item.teamName),
                    "New Team",
                    fv!item.teamName
                  ),
                  color: "ACCENT",
                  style: if(
                    local!isCurrentItemSelected = true,
                    "STRONG",
                    "PLAIN"
                  ),
                  size: "MEDIUM"
                ),
                char(10),
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: {
                    if(
                      rule!AS_CO_UT_isBlank(local!activeTeamMembers),
                      0,
                      length(local!activeTeamMembers)
                    ),
                    " " & "Team Member(s)"
                  },
                  color: "SECONDARY"
                )
              }
            )
          }
        )
      )
    ),
    #"SYSTEM_SYSRULES_richTextDisplayField"(value: { char(10) }),
    /*New team button*/
    rule!AS_GSS_CPS_newTeamButton(
      evaluatorTeams: ri!evaluatorTeams,
      isUnsavedteam: ri!isUnsavedTeam,
      selectedEvaluatorTeam: ri!selectedEvaluatorTeam,
      readOnly: ri!readOnly,
      i18nData: ri!i18nData,
      newMemberships: ri!newMemberships
    )
  }
)

_a-0000e3e6-fb42-8000-9ba5-011c48011c48_.sail_code:
/*Do a strict comarison against types*/
cast(
   'type!{http://www.appian.com/ae/types/2009}Boolean',
  and(
    rule!AS_CO_UT_typeCompare(
      value1: ri!value1,
      value2: ri!value2
    ),
    ri!value1 = ri!value2
  )
)

_a-0000e447-933d-8000-9baa-011c48011c48_.sail_code:
if(
/* to prevent modulus from breaking */
  isnull(ri!value),
  ri!value,
  if(
    todecimal(ri!value) = tostring(ri!value),
    if(
      mod(ri!value, 1) = 0,
      text(
        todecimal(ri!value), "##"
      ),
      text(
        todecimal(ri!value), "##.##"
      )
    ),
    ri!value
  )
)

_a-0000e40a-de32-8000-9ba8-011c48011c48_.sail_code:
cast(
  typeof(ri!cdt),
  if(
    and(
      rule!AS_CO_UT_isBlank(ri!cdt),
      rule!AS_CO_UT_checkIfArrayType(
        typeOf: ri!cdt
      )
    ),
    ri!cdt,
    reduce(
      rule!AS_CO_UT_zINTERNAL_updateCdtFieldsByPathComplex_handler(
        cdt: _,
        fieldValuePair: _
      ),
      ri!cdt,
      a!flatten(ri!fieldValuePairs)
    )
  )
)

