_a-0000e2cd-bc96-8000-9ba2-011c48011c48_.sail_code:
cast(
   'type!{http://www.appian.com/ae/types/2009}Boolean',
  with(
    local!userToUse: rule!AS_CO_UT_replaceNull(
      nullableValue: ri!user, 
      replacementValue: loggedInUser()
    ),
    if(
      or(
        rule!AS_CO_UT_isBlank(ri!groups),
        not(rule!AS_CO_UT_checkIfUserIsActive(user: local!userToUse))
      ),
      false,
      any(
        #"urn:appian:function:v1:a:isusermemberofgroup?okey=a!isUserMemberOfGroup"(local!userToUse, _),
        ri!groups
      )
    )
  )
)

