    value: rule!AS_FRM_getRuleReferenceOrNoOp(
      ruleName: "AS_GSM_ENTRYPOINT_DISPLAY_createExternalVendor_Federal_v1"
    ),
    refreshAlways: true
  ),
  /* Actually execute the rule */
  local!rule(
    /* This should always be passed in every app reference rule */
    /* which returns a default value if the rule does not exist */
    ifNull_default: {},
    /* Entrypoint-specific inputs for that rule */
    vendorMap: ri!vendorMap,
    cachedVendor: ri!cachedVendor,
    submitSaves: ri!submitSaves,
    cancelSaves: ri!cancelSaves,
    triggerRefresh: ri!triggerRefresh
  )
)

