        recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.stateAndLocalIdentifier,
        {}
      ),
      finalArray: index(ri!vendorInfo, "data", null),
      isRemove: false()
    ),
    rule!AS_CO_UT_indexWhere(
      value: difference(
        touniformstring(
          if(
            rule!AS_CO_UT_isBlank(index(ri!vendorInfo, "data", null)),
            {},
            index(
              ri!vendorInfo.data,
              recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.federalIdentifier,
              {}
            )
          )
        ),
        union(
          touniformstring(
            if(
              rule!AS_CO_UT_isBlank(
                index(
                  ri!selectedVendorsMap,
                  "vendorDetails",
                  null
                )
              ),
              {},
              index(
                ri!selectedVendorsMap.vendorDetails,
                recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.federalIdentifier,
                {}
              )
            )
          ),
          touniformstring(
            if(
              rule!AS_CO_UT_isBlank(ri!existingEvaluationVendors),
              {},
              index(
                ri!existingEvaluationVendors,
                recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.federalIdentifier,
                {}
              )
            )
          )
        )
      ),
      initialArray: index(
        ri!vendorInfo.data,
        recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.federalIdentifier,
        {}
      ),
      finalArray: index(ri!vendorInfo, "data", null),
      isRemove: false()
    )
  ),
  #"SYSTEM_SYSRULES_ButtonArrayLayout"(
    buttons: {
      /*Add All on this page*/
      #"SYSTEM_SYSRULES_buttonWidget_v1"(
        label: "Add All on this page",
        value: ri!selectedVendorsMap,
        saveInto: a!save(
          ri!selectedVendorsMap,
          append(
            ri!selectedVendorsMap,
            #"SYSTEM_SYSRULES_forEach"(
              items: local!vendorsNotInSelectedMap,
              expression: rule!AS_GSS_constructSelectedVendorsMap(
                selectedVendor: fv!item,
                source: cons!AS_GSS_TXT_VENDOR_SOURCE_MANUAL,
                isActiveInSource: true()
              )
            )
          )
        ),
        size: "SMALL",
        showWhen: and(
          rule!AS_CO_UT_isNotBlank(local!vendorsNotInSelectedMap),
          rule!AS_CO_UT_isNotBlank(ri!vendorInfo.data)
        )
      ),
      /*Remove All on this page*/
      #"SYSTEM_SYSRULES_buttonWidget_v1"(
        label: "Remove All on this page",
        value: ri!selectedVendorsMap,
        saveInto: if(
          ri!isSLGToggleOn = true,
          a!save(
            ri!selectedVendorsMap,
            remove(
              ri!selectedVendorsMap,
              wherecontains(
                ri!vendorInfo.data[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.stateAndLocalIdentifier],
                index(
                  ri!selectedVendorsMap.vendorDetails,
                  recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.stateAndLocalIdentifier,
                  null
                )
              )
            )
          ),
          a!save(
            ri!selectedVendorsMap,
            remove(
              ri!selectedVendorsMap,
              wherecontains(
                ri!vendorInfo.data[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.federalIdentifier],
                index(
                  ri!selectedVendorsMap.vendorDetails,
                  recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.federalIdentifier,
                  null
                )
              )
            )
          )
        ),
        size: "SMALL",
        showWhen: and(
          rule!AS_CO_UT_isBlank(local!vendorsNotInSelectedMap),
          rule!AS_CO_UT_isNotBlank(ri!vendorInfo.data)
        )
      )
    },
    align: "START",
    marginBelow: "STANDARD"
  )
),

