a!localVariables(
  /* Integration to get vendor details*/
  local!vendorAndDocumentsData: if(
    rule!AS_CO_UT_isBlank(ri!solicitationNumber),
    null,
    rule!AS_GSS_INT_GET_getVendorsAndDocuments(
      solicitationNumber: ri!solicitationNumber,
      startIndex: a!defaultValue(ri!startIndex, 1),
      batchSize: a!defaultValue(ri!batchSize, 15),
      version: a!defaultValue(ri!version, 1),
      externalVendorId: ri!externalVendorId
    )
  ),
  local!responseBody: rule!AS_GSS_UT_deepIndex(
    item: local!vendorAndDocumentsData,
    indexArray: { "result", "body" }
  ),
  local!refAddressType: rule!AS_GSS_QR_getRefDataByTypes(
    refTypes: { cons!AS_GSS_REF_TYPE_VENDOR_ADDRESS }
  ),
  local!refCountry: rule!AS_GSS_QR_getRefCountry(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY
  ),
  local!refState: rule!AS_GSS_QR_getRefState(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY
  ),
  /* Constructing a dictionary to map based on the data returned*/
  if(
    or(
      rule!AS_CO_UT_isBlank(local!vendorAndDocumentsData.result),
      rule!AS_CO_UT_isBlank(
        rule!AS_GSS_UT_deepIndex(
          item: local!responseBody,
          indexArray: { "vendorDetails" }
        )
      )
    ),
    /*   If no data is retrieved from Integration*/
    a!map(
      data: null,
      totalCount: 0,
      error: a!defaultValue(
        value: rule!AS_GSS_UT_deepIndex(
          item: local!responseBody,
          indexArray: { "error" }
        ),
        default: cons!AS_GSS_TXT_NO_RESULTS_FOUND_FROM_SAM_GOV
      ),
      errorDescription: rule!AS_GSS_UT_deepIndex(
        item: local!responseBody,
        indexArray: { "message" }
      ),
      displayProps: rule!AS_GSS_UT_constructDisplayPropsForVmIntegrationResponse(
        statusCode: rule!AS_GSS_UT_deepIndex(
          item: local!vendorAndDocumentsData,
          indexArray: { "result", "statusCode" }
        ),
        isSealed: rule!AS_GSS_UT_deepIndex(
          item: local!responseBody,
          indexArray: { "isSealed" }
        )
      )
    ),
    /*   When data is retrieved */
    a!map(
      data: #"SYSTEM_SYSRULES_forEach"(
        items: local!vendorAndDocumentsData.result.body.vendorDetails,
        expression: /* Updating the vendor fields with data from the integration. */
        recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD(
          recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.evaluationId: ri!evaluationId,
          recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.legalName: rule!AS_CO_UT_indexPath(
            cdt: fv!item,
            field: "basicDetails.legalName"
          ),
          recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.businessName: rule!AS_CO_UT_indexPath(
            cdt: fv!item,
            field: "basicDetails.businessName"
          ),
          recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.address: {
            recordType!AS_GSS_EvaluationVendorAddress_SYNCEDRECORD(
              recordType!AS_GSS_EvaluationVendorAddress_SYNCEDRECORD.addressType: cons!AS_GSS_REF_ID_VENDOR_ADDRESS_TYPE_PHYSICAL,
              recordType!AS_GSS_EvaluationVendorAddress_SYNCEDRECORD.vendorAddressLine1: rule!AS_CO_UT_indexPath(
                cdt: fv!item,
                field: { "basicDetails.addressLine1" }
              ),
              recordType!AS_GSS_EvaluationVendorAddress_SYNCEDRECORD.vendorAddressLine2: rule!AS_CO_UT_indexPath(
                cdt: fv!item,
                field: { "basicDetails.addressLine2" }
              ),
              recordType!AS_GSS_EvaluationVendorAddress_SYNCEDRECORD.vendorCity: rule!AS_CO_UT_left(
                text: rule!AS_CO_UT_indexPath(
                  cdt: fv!item,
                  field: { "basicDetails.city" }
                ),
                numChars: 100
              ),
              recordType!AS_GSS_EvaluationVendorAddress_SYNCEDRECORD.vendorState: rule!AS_CO_UT_indexPath(
                cdt: fv!item,
                field: { "basicDetails.state" }
              ),
              recordType!AS_GSS_EvaluationVendorAddress_SYNCEDRECORD.vendorCountry: rule!AS_CO_UT_indexPath(
                cdt: fv!item,
                field: { "basicDetails.country" }
              ),
              recordType!AS_GSS_EvaluationVendorAddress_SYNCEDRECORD.vendorZipCode: rule!AS_CO_UT_left(
                text: rule!AS_CO_UT_indexPath(
                  cdt: fv!item,
                  field: { "basicDetails.zipCode" }
                ),
                numChars: 5
              ),
              recordType!AS_GSS_EvaluationVendorAddress_SYNCEDRECORD.vendorForeignPostalCode: rule!AS_CO_UT_left(
                text: rule!AS_CO_UT_indexPath(
                  cdt: fv!item,
                  field: { "basicDetails.foreignPostalCode" }
                ),
                numChars: 20
              ),
              recordType!AS_GSS_EvaluationVendorAddress_SYNCEDRECORD.isActive: true(),
              recordType!AS_GSS_EvaluationVendorAddress_SYNCEDRECORD.country: rule!AS_CO_UT_indexWhere(
                value: rule!AS_CO_UT_indexPath(
                  cdt: fv!item,
                  field: { "basicDetails.country" }
                ),
                initialArray: local!refCountry[recordType!AS_GAM_R_Country_SYNCEDRECORD.countryId],
                finalArray: local!refCountry,
                isRemove: false()
              ),
              recordType!AS_GSS_EvaluationVendorAddress_SYNCEDRECORD.state: rule!AS_CO_UT_indexWhere(
                value: rule!AS_CO_UT_indexPath(
                  cdt: fv!item,
                  field: { "basicDetails.state" }
                ),
                initialArray: local!refState[recordType!AS_GAM_R_State_SYNCEDRECORD.stateId],
                finalArray: local!refState,
                isRemove: false()
              ),
              recordType!AS_GSS_EvaluationVendorAddress_SYNCEDRECORD.referenceAddressType: rule!AS_CO_UT_indexWhere(
                value: cons!AS_GSS_REF_ID_VENDOR_ADDRESS_TYPE_PHYSICAL,
                initialArray: local!refAddressType[recordType!AS_GSS_R_Data_SYNCEDRECORD.refDataId],
                finalArray: local!refAddressType,
                isRemove: false()
              )
            ),
            recordType!AS_GSS_EvaluationVendorAddress_SYNCEDRECORD(
              recordType!AS_GSS_EvaluationVendorAddress_SYNCEDRECORD.addressType: cons!AS_GSS_REF_ID_VENDOR_ADDRESS_TYPE_MAILING,
              recordType!AS_GSS_EvaluationVendorAddress_SYNCEDRECORD.vendorAddressLine1: rule!AS_CO_UT_indexPath(
                cdt: fv!item,
                field: { "basicDetails.addressLine1" }
              ),
              recordType!AS_GSS_EvaluationVendorAddress_SYNCEDRECORD.vendorAddressLine2: rule!AS_CO_UT_indexPath(
                cdt: fv!item,
                field: { "basicDetails.addressLine2" }
              ),
              recordType!AS_GSS_EvaluationVendorAddress_SYNCEDRECORD.vendorCity: rule!AS_CO_UT_left(
                text: rule!AS_CO_UT_indexPath(
                  cdt: fv!item,
                  field: { "basicDetails.city" }
                ),
                numChars: 100
              ),
              recordType!AS_GSS_EvaluationVendorAddress_SYNCEDRECORD.vendorState: rule!AS_CO_UT_indexPath(
                cdt: fv!item,
                field: { "basicDetails.state" }
              ),
              recordType!AS_GSS_EvaluationVendorAddress_SYNCEDRECORD.vendorCountry: rule!AS_CO_UT_indexPath(
                cdt: fv!item,
                field: { "basicDetails.country" }
              ),
              recordType!AS_GSS_EvaluationVendorAddress_SYNCEDRECORD.vendorZipCode: rule!AS_CO_UT_left(
                text: rule!AS_CO_UT_indexPath(
                  cdt: fv!item,
                  field: { "basicDetails.zipCode" }
                ),
                numChars: 5
              ),
              recordType!AS_GSS_EvaluationVendorAddress_SYNCEDRECORD.vendorForeignPostalCode: rule!AS_CO_UT_left(
                text: rule!AS_CO_UT_indexPath(
                  cdt: fv!item,
                  field: { "basicDetails.foreignPostalCode" }
                ),
                numChars: 20
              ),
              recordType!AS_GSS_EvaluationVendorAddress_SYNCEDRECORD.isActive: true(),
              recordType!AS_GSS_EvaluationVendorAddress_SYNCEDRECORD.country: rule!AS_CO_UT_indexWhere(
                value: rule!AS_CO_UT_indexPath(
                  cdt: fv!item,
                  field: { "basicDetails.country" }
                ),
                initialArray: local!refCountry[recordType!AS_GAM_R_Country_SYNCEDRECORD.countryId],
                finalArray: local!refCountry,
                isRemove: false()
              ),
              recordType!AS_GSS_EvaluationVendorAddress_SYNCEDRECORD.state: rule!AS_CO_UT_indexWhere(
                value: rule!AS_CO_UT_indexPath(
                  cdt: fv!item,
                  field: { "basicDetails.state" }
                ),
                initialArray: local!refState[recordType!AS_GAM_R_State_SYNCEDRECORD.stateId],
                finalArray: local!refState,
                isRemove: false()
              ),
              recordType!AS_GSS_EvaluationVendorAddress_SYNCEDRECORD.referenceAddressType: rule!AS_CO_UT_indexWhere(
                value: cons!AS_GSS_REF_ID_VENDOR_ADDRESS_TYPE_MAILING,
                initialArray: local!refAddressType[recordType!AS_GSS_R_Data_SYNCEDRECORD.refDataId],
                finalArray: local!refAddressType,
                isRemove: false()
              )
            )
          },
          recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.cageCode: rule!AS_CO_UT_indexPath(cdt: fv!item, field: "basicDetails.cage"),
          recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.uniqueEntityId: rule!AS_CO_UT_indexPath(cdt: fv!item, field: "basicDetails.uei"),
          recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.tin: rule!AS_CO_UT_indexPath(cdt: fv!item, field: "basicDetails.tin"),
          recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.fein: rule!AS_CO_UT_indexPath(cdt: fv!item, field: "basicDetails.fein"),
          recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.status: rule!AS_CO_UT_indexPath(
            cdt: fv!item,
            field: "basicDetails.samGovStatus"
          ),
          recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.expirationDate: rule!AS_CO_UT_indexPath(
            cdt: fv!item,
            field: "basicDetails.samGovExpirationDate"
          ),
          recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.isActive: true(),
          recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.isUpdateRequired: false(),
          recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.sourceApplicationId: cons!AS_GSS_REF_ID_SOURCE_APPLICATION_VM,
          recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.federalIdentifier: concat(
            rule!AS_CO_UT_replaceNull(
              nullableValue: upper(
                rule!AS_CO_UT_indexPath(cdt: fv!item, field: "basicDetails.uei")
              ),
              replacementValue: "N/A"
            ),
            "|",
            rule!AS_CO_UT_replaceNull(
              nullableValue: upper(
                rule!AS_CO_UT_indexPath(cdt: fv!item, field: "basicDetails.cage")
              ),
              replacementValue: "N/A"
            )
          ),
          recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.stateAndLocalIdentifier: rule!AS_CO_UT_replaceNull(
            nullableValue: upper(
              rule!AS_CO_UT_indexPath(cdt: fv!item, field: "basicDetails.tin")
