            replacementValue: "N/A"
          ),
          recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.businessTypes: #"SYSTEM_SYSRULES_forEach"(
            items: rule!AS_CO_UT_indexPath(
              cdt: fv!item,
              field: "basicDetails.businessTypeCodes"
            ),
            expression: recordType!AS_GSS_EvaluationVendorBusinessType_SYNCEDRECORD(
              recordType!AS_GSS_EvaluationVendorBusinessType_SYNCEDRECORD.typeCode: fv!item
            )
          ),
          recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.externalVendorId: rule!AS_CO_UT_indexPath(
