            items: rule!AS_CO_UT_indexPath(cdt: fv!item, field: "proposalDocuments"),
            expression: recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD(
              recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.appianDocId: rule!AS_CO_UT_indexPath(cdt: fv!item, field: "appianDocID"),
              recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.documentName: rule!AS_CO_UT_indexPath(cdt: fv!item, field: "documentName"),
              recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.version: rule!AS_CO_UT_indexPath(cdt: fv!item, field: "proposalVersion"),
              recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.fileType: rule!AS_CO_UT_indexPath(cdt: fv!item, field: "documentExtension"),
              recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.evaluationId: ri!evaluationId,
              recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.docType: cons!AS_GSS_REF_ID_DOC_TYPE_VENDOR,
              recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.documentSubType: rule!AS_CO_UT_indexPath(cdt: fv!item, field: "documentType"),
              recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.documentDescription: rule!AS_CO_UT_indexPath(
                cdt: fv!item,
                field: "documentDescription"
              ),
              recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.isDeleted: false(),
              recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.sourceApplicationId: cons!AS_GSS_REF_ID_SOURCE_APPLICATION_VM,
              recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.vendorDocumentFactorMapping: if(
