if(
  ri!isVmEnabled = true(),
  a!localVariables(
    local!getVendorDetailsResponse: a!refreshVariable(
      value: rule!AS_GSS_INT_GET_VendorIdentifierDetails(
        solicitationPiid: ri!solicitationPiid,
        isVendorLinked: ri!isVendorLinked
      ),
      refreshOnReferencedVarChange: false()
    ),
    local!vendorIdentifierDetails: if(
      a!isNullOrEmpty(local!getVendorDetailsResponse),
      null,
      if(
        local!getVendorDetailsResponse.success,
        rule!AS_CO_UT_indexPath(
          cdt: local!getVendorDetailsResponse,
          field: "result.body",
          indexDefault: null
        ),
        null
      )
    ),
    if(
      a!isNullOrEmpty(
        value: rule!AS_CO_UT_indexPath(
          cdt: local!vendorIdentifierDetails,
          field: "vendorDetails.legalName"
        )
      ),
      {},
      a!match(
        value: ri!isSLGToggleOn,
        equals: true(),
        then: a!map(
          federalIdentifiers: {},
          stateIdentifiers: #"SYSTEM_SYSRULES_forEach"(
            items: local!vendorIdentifierDetails.vendorDetails,
            expression: rule!AS_CO_UT_replaceNull(
              nullableValue: upper(
                rule!AS_CO_UT_indexPath(cdt: fv!item, field: "tin")
              ),
              replacementValue: "N/A"
            )
          )
        ),
        equals: false(),
        then: a!map(
          federalIdentifiers: #"SYSTEM_SYSRULES_forEach"(
            items: local!vendorIdentifierDetails.vendorDetails,
            expression: concat(
              rule!AS_CO_UT_replaceNull(
                nullableValue: upper(
                  rule!AS_CO_UT_indexPath(cdt: fv!item, field: "uniqueEntityId")
                ),
                replacementValue: "N/A"
              ),
              "|",
              rule!AS_CO_UT_replaceNull(
                nullableValue: upper(
                  rule!AS_CO_UT_indexPath(cdt: fv!item, field: "cage")
                ),
                replacementValue: "N/A"
              )
            )
          ),
          stateIdentifiers: {}
        ),
        default: a!map(
          federalIdentifiers: {},
          stateIdentifiers: {}
        )
      )
    )
  ),
  a!map(
    federalIdentifiers: {},
    stateIdentifiers: {}
  )
)

