a!localVariables(
  local!vendorPageCount: if(
    rule!AS_CO_UT_checkIsNullOrEmpty(value: ri!selectedVendorsMap),
    0,
    length(ri!selectedVendorsMap)
  ),
  local!vendorSteps: if(
    rule!AS_CO_UT_checkIsNullOrEmpty(value: ri!selectedVendorsMap),
    {},
    enumerate(local!vendorPageCount) + 1
  ),
  local!formSteps: { 1, 2 },
  local!formPageCount: length(local!formSteps),
  local!currentFormStep: if(
    ri!isFormPage,
    if(
      ri!isNextAction,
      ri!paging.currentFormStep + 1,
      ri!paging.currentFormStep - 1
    ),
    rule!AS_CO_UT_replaceNull(
      nullableValue: ri!paging.currentFormStep,
      replacementValue: 1
    )
  ),
  local!currentVendorStep: if(
    ri!isFormPage = false(),
    rule!AS_CO_UT_replaceNull(
      nullableValue: ri!selectedVendorIndex,
      replacementValue: if(
        ri!isNextAction,
        ri!paging.currentVendorStep + 1,
        ri!paging.currentVendorStep - 1
      )
    ),
    1
  ),
  local!showVmPage: if(
    ri!isVmSwitch,
    not(ri!paging.showVmPage),
    rule!AS_CO_UT_replaceNull(
      nullableValue: ri!paging.showVmPage,
      replacementValue: ri!isVmEnabled
    )
  ),
  a!map(
    /*Will hold a list of all form page indexes*/
    formSteps: local!formSteps,
    /*Will hold the total count of form pages*/
    formPageCount: local!formPageCount,
    /*Will hold the indexes of all vendors selected/queried*/
    vendorSteps: local!vendorSteps,
    /*Will hold the total count of vendors*/
    vendorPageCount: local!vendorPageCount,
    /*Will hold the current step of the form*/
    currentFormStep: local!currentFormStep,
    /*Will hold the current index of the vendor that is opened*/
    currentVendorStep: local!currentVendorStep,
    formFirstPage: local!formSteps[1],
    formLastPage: local!formPageCount,
    /*Determines if VM Integration page should be shown or not*/
    showVmPage: local!showVmPage
  )
)

_a-0000edca-8e92-8000-9e0b-011c48011c48_.sail_code:
intersection(
  ri!previousIndices,
  if(
    /*Null groups and documents don't filter properly, adding logic to support that*/
    and(
      a!isNullOrEmpty(ri!fieldValuePair.value),
      runtimetypeof(ri!records[ri!fieldValuePair.field]) = 'type!{http://www.appian.com/ae/types/2009}Group?list'
    ),
    wherecontains(
      cast(
        'type!{http://www.appian.com/ae/types/2009}Group',
        null
      ),
      ri!records[ri!fieldValuePair.field]
    ),
    and(
      a!isNullOrEmpty(ri!fieldValuePair.value),
      runtimetypeof(ri!records[ri!fieldValuePair.field]) = 'type!{http://www.appian.com/ae/types/2009}Document?list'
    ),
    wherecontains(
      cast(
        'type!{http://www.appian.com/ae/types/2009}Document',
        null
      ),
      ri!records[ri!fieldValuePair.field]
    ),
    /*Standard filter logic*/
    wherecontains(
      touniformstring(ri!fieldValuePair.value),
      touniformstring(ri!records[ri!fieldValuePair.field])
    )
  )
)

