a!localVariables(
  local!joinedText: joinarray(
    {
      a!defaultValue(
        value: ri!initialInstructions,
        default: {}
      ),
      if(
        ri!showAllowedFileTypes,
        "Supported file formats: pdf, doc, docx, xls, xlsx, ppt, & pptx",
        {}
      ),
      if(
        ri!showMaxDocumentLimit,
        rule!AS_GAM_CO_I18N_UT_displayLabel(
          bundle: ri!bundle,
          bundleKey: "txt_DynamicDocLimit",
          arguments: ri!maxDocumentsAllowed
        ),
        {}
      )
    },
    ". "
  ),
  local!multipleInstructionalTexts: count(
    where(
      {
        a!isNotNullOrEmpty(ri!initialInstructions),
        ri!showAllowedFileTypes,
        ri!showMaxDocumentLimit
      }
    )
  ) > 1,
  if(
    and(
      local!multipleInstructionalTexts,
      charat(local!joinedText, len(local!joinedText)) <> char(46)
    ),
    concat(local!joinedText, "."),
    local!joinedText
  )
)

