if(
  a!isNullOrEmpty(
    ri!currentVendor[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.legalName]
  ),
  {},
  a!localVariables(
    /* To check if Vendor is available in VM */
    local!isAvailableInVm: contains(
      touniformstring(
        ri!allVmVendorIdentifiers.stateIdentifiers
      ),
      ri!currentVendor[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.stateAndLocalIdentifier]
    ),
    /* To check if the vendor is already added to the evaluation */
    local!isExistingVendor: contains(
      touniformstring(
        index(
          ri!existingVendors,
          recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.stateAndLocalIdentifier,
          null
        )
      ),
      ri!currentVendor[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.stateAndLocalIdentifier]
    ),
    /* To check if the vendor is already selected in this instance */
    local!isSelectedVendor: contains(
      touniformstring(
        index(
          ri!selectedVendorsMap.vendorDetails,
          recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.stateAndLocalIdentifier,
          null
        )
      ),
      ri!currentVendor[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.stateAndLocalIdentifier]
    ),
    local!disableVendor: or(
      local!isAvailableInVm,
      local!isExistingVendor,
      if(
        local!isSelectedVendor,
        /* To check if the current SAM.gov vendor is already available in Suite Directory */
        toboolean(ri!currentVendorInMap.isImported) = true(),
        {}
      )
    ),
    local!addressToDisplay: index(
      rule!AS_CO_UT_filterCdtByField(
        cdt: ri!currentVendorInMap.physicalAndMailingAddress,
        field: "addressType",
        value: cons!AS_GSS_REF_LABEL_VENDOR_ADDRESS_PHYSICAL_ADDRESS
      ),
      1,
      null
    ),
    local!currentCountryRefDetails: rule!AS_CO_UT_filterRecordByFieldValues(
      records: ri!refCountries,
      field: recordType!AS_GAM_R_Country_SYNCEDRECORD.samGovCode,
      values: index(
        local!addressToDisplay,
        "countryCode",
        null
      )
    ),
    #"SYSTEM_SYSRULES_cardLayout"(
      contents: {
        /*Vendor name*/
        #"SYSTEM_SYSRULES_sideBySideLayout"(
          items: {
            #"SYSTEM_SYSRULES_sideBySideItem"(
              item: #"SYSTEM_SYSRULES_headingField"(
                text: ri!currentVendorInMap.vendorLegalName,
                size: "EXTRA_SMALL",
                headingTag: "H3",
                color: "STANDARD",
                fontWeight: "SEMI_BOLD",
                marginBelow: "NONE"
              )
            ),
            #"SYSTEM_SYSRULES_sideBySideItem"(
              item: #"SYSTEM_SYSRULES_richTextDisplayField"(
                labelPosition: "COLLAPSED",
                value: #"SYSTEM_SYSRULES_richTextIcon"(
                  icon: if(
                    /*If the same vendor from directory shows up in SAM.gov result as well and if user selects it then the vendor in SAM.gov should not be selected*/
                    and(
                      local!isSelectedVendor,
                      not(local!disableVendor)
                    ),
                    "check-square",
                    "square-o"
                  ),
                  color: if(
                    and(
                      local!isSelectedVendor,
                      not(local!disableVendor)
                    ),
                    "ACCENT",
                    rule!AS_GSS_BrandingValueByKey(
                      brandingKey: "GrayTwo",
                      useSuiteBranding: true
                    )
                  ),
                  size: "MEDIUM"
                )
              ),
              width: "MINIMIZE"
            )
          },
          alignVertical: "MIDDLE",
          marginBelow: "NONE"
        ),
        /*TIN - FEIN*/
        #"SYSTEM_SYSRULES_richTextDisplayField"(
          labelPosition: "COLLAPSED",
          value: #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: {
              /*TIN*/
              "TIN" & ": " & ri!currentVendorInMap.tin,
              " â€¢ ",
              /*CAGE*/
              "FEIN" & ": " & rule!AS_CO_UT_replaceNull(
                nullableValue: ri!currentVendorInMap.fein,
                replacementValue: "-"
              ),
              " â€¢ ",
              /*System Status*/
              "System Status" & ": " & if(
                rule!AS_CO_UT_isBlank(value: ri!currentVendorInMap.isActive),
                "-",
                if(
                  ri!currentVendorInMap.isActive,
                  "Active",
                  "Inactive"
                )
              )
            },
            size: "SMALL"
          ),
          marginBelow: "NONE"
        ),
        /*Address, Source and System Status*/
        #"SYSTEM_SYSRULES_sideBySideLayout"(
          items: {
            /*Address*/
            #"SYSTEM_SYSRULES_sideBySideItem"(
              item: #"SYSTEM_SYSRULES_richTextDisplayField"(
                labelPosition: "COLLAPSED",
                value: #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: "Address" & ": " & if(
                    rule!AS_CO_UT_isBlank(value: local!addressToDisplay),
                    "-",
                    rule!AS_GSS_CO_UT_displayAddress(
                      addressLine1: local!addressToDisplay.addressLine1,
                      addressLine2: local!addressToDisplay.addressLine2,
                      city: local!addressToDisplay.city,
                      state: local!addressToDisplay.stateOrProvinceCode,
