_a-0000ecda-a664-8000-9dc8-011c48011c48_.sail_code:
if(
  rule!AS_CO_UT_checkIsNullOrEmpty(value: ri!data),
  ri!data,
  #"SYSTEM_SYSRULES_forEach"(
    items: ri!data,
    expression: rule!AS_GSS_UT_updateRecordsByModelRecord(
      records: fv!item,
      modelRecord: cast(
        ri!recordType,
        #"urn:appian:function:v1:a:update"(
          fv!item,
          {
            if(
              a!isNullOrEmpty(value: ri!modifiedByField),
              {},
              ri!modifiedByField
            ),
            if(
              a!isNullOrEmpty(value: ri!modifiedOnField),
              {},
              ri!modifiedOnField
            ),
            ri!createdByField,
            ri!createdOnField,
            if(
              a!isNullOrEmpty(value: ri!isActiveField),
              {},
              ri!isActiveField
            ),
            if(
              a!isNullOrEmpty(value: ri!isDeletedField),
              {},
              ri!isDeletedField
            )
          },
          {
            if(
              rule!AS_CO_UT_checkIsNullOrEmpty(value: ri!modifiedByField),
              {},
              rule!AS_CO_UT_replaceNull(
                nullableValue: ri!modifiedBy,
                replacementValue: loggedInUser()
              )
            ),
            if(
              rule!AS_CO_UT_checkIsNullOrEmpty(value: ri!modifiedOnField),
              {},
              now()
            ),
            rule!AS_CO_UT_replaceNull(
              nullableValue: fv!item[ri!createdByField],
              replacementValue: rule!AS_CO_UT_replaceNull(
                nullableValue: ri!modifiedBy,
                replacementValue: loggedInUser()
              )
            ),
            rule!AS_CO_UT_replaceNull(
              nullableValue: fv!item[ri!createdOnField],
              replacementValue: now()
            ),
            if(
              rule!AS_CO_UT_checkIsNullOrEmpty(value: ri!isActiveField),
              {},
              rule!AS_CO_UT_booleanDefaultTrue(boolean: fv!item[ri!isActiveField])
            ),
            if(
              rule!AS_CO_UT_checkIsNullOrEmpty(value: ri!isDeletedField),
              {},
              rule!AS_CO_UT_booleanDefaultFalse(boolean: fv!item[ri!isDeletedField])
            )
          }
        )
      )
    )
  )
)

_a-0000e2ad-62e7-8000-9ba2-011c48011c48_.sail_code:
cast(
  'type!{http://www.appian.com/ae/types/2009}Text',
  a!localVariables(
    local!length: len(ri!value),
    local!minLength: rule!AS_CO_UT_replaceNull(
      nullableValue: ri!minLength,
      replacementValue: 0
    ),
    if(
      /* Does not meet exact length requirement */
      and(
        rule!AS_CO_UT_isNotBlank(ri!minLength),
        rule!AS_CO_UT_isNotBlank(ri!maxLength),
        ri!minLength = ri!maxLength,
        local!length <> ri!minLength
      ),
      rule!AS_CO_UT_replaceNull(
        nullableValue: rule!AS_CO_UT_replaceNull(
          nullableValue: ri!maxLengthMessage,
          replacementValue: ri!minLengthMessage
        ),
        replacementValue: rule!AS_CO_UT_displayCommonObjectBundleLabel(
          bundleKey: if(
            ri!minLength = 1,
            "AS.CO.CommonObjects.vld_EnterExactlySingle",
            "AS.CO.CommonObjects.vld_EnterExactlyMultiple"
          ),
          arguments: ri!minLength
        )
      ),
      /*Too short*/
      if(
        local!length < ri!minLength,
        rule!AS_CO_UT_replaceNull(
          nullableValue: ri!minLengthMessage,
          replacementValue: rule!AS_CO_UT_displayCommonObjectBundleLabel(
            bundleKey: "AS.CO.CommonObjects.vld_EnterAtLeast",
            arguments: local!minLength
          )
        ),
        /*Too long*/
        if(
          and(
            rule!AS_CO_UT_isNotBlank(ri!maxLength),
            local!length > ri!maxLength
          ),
          rule!AS_CO_UT_replaceNull(
            nullableValue: ri!maxLengthMessage,
            replacementValue: rule!AS_CO_UT_displayCommonObjectBundleLabel(
              bundleKey: if(
                ri!maxLength = 1,
                "AS.CO.CommonObjects.vld_EnterNoMoreThanSingle",
                "AS.CO.CommonObjects.vld_EnterNoMoreThanMultiple"
              ),
              arguments: ri!maxLength
            )
          ),
          /*Valid*/
          ""
        )
      )
    )
  )
)

_a-0000ea13-fcbd-8000-9c86-011c48011c48_.sail_code:
rule!AS_CO_UT_queryRecord(
  recordType: recordType!AS_GSS_Criteria_SYNCEDRECORD,
  filters: {
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaId,
      operator: "in",
      value: ri!criteriaId
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AS_GSS_Criteria_SYNCEDRECORD.evaluationId,
      operator: "in",
      value: ri!evaluationId
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AS_GSS_Criteria_SYNCEDRECORD.parentCriteriaId,
      operator: a!match(
        value: ri!isParent,
        equals: true,
        then: "is null",
        equals: false,
        then: "not null",
        default: "in"
      ),
      value: ri!parentCriteriaId
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AS_GSS_Criteria_SYNCEDRECORD.isActive,
      operator: "in",
      value: a!defaultValue(
        value: ri!isActive,
        default: {true}
      )
    ),
    ri!additionalfilters
  },
  relatedRecordData: ri!relatedRecordData,
  fields: rule!AS_CO_UT_rejectNullValuesFromArray(
    {
      ri!fields,
      if(
        ri!includeRelatedRecords,
        {
          recordType!AS_GSS_Criteria_SYNCEDRECORD.CriteriaAssignment,
          recordType!AS_GSS_Criteria_SYNCEDRECORD.EvaluatorTeam,
          recordType!AS_GSS_Criteria_SYNCEDRECORD.Rating,
          recordType!AS_GSS_Criteria_SYNCEDRECORD.evaluation,
          recordType!AS_GSS_Criteria_SYNCEDRECORD.SubCriteria
        },
        {}
      )
    }
  ),
  sort: ri!sortInfo,
  pagingInfo: ri!pagingInfo,
  returnType: ri!returnType,
  executeWhen: ri!executeWhen,
  triggerRefresh: ri!triggerRefresh,
  logicalExpression: ri!logicalExpression,
  aggregationFields: ri!aggregationFields
)

