              item: if(
                rule!AS_CO_UT_isBlank(
                  value: ri!currentVendorInMap.registrationStatusId
                ),
                #"SYSTEM_SYSRULES_richTextDisplayField"(
                  labelPosition: "COLLAPSED",
                  value: #"SYSTEM_SYSRULES_richTextItem_v1"(
                    text: cons!AS_GSS_CO_NULL_REPLACEABLE_TEXT,
                    color: rule!AS_GSS_BrandingValueByKey(
                      brandingKey: "GrayThree",
                      useSuiteBranding: true
                    ),
                    size: "SMALL"
                  )
                ),
                #"SYSTEM_SYSRULES_tagField"(
                  labelPosition: "COLLAPSED",
                  tags: #"SYSTEM_SYSRULES_tagItem_v1"(
                    text: ri!currentVendorInMap.registrationStatusId,
                    backgroundColor: if(
                      tostring(
                        ri!currentVendorInMap.registrationStatusId
                      ) = cons!AS_GSS_SAM_GOV_TXT_STATUS_ACTIVE,
                      rule!AS_GSS_BrandingValueByKey(
                        brandingKey: "GreenOne",
