          ),
          #"SYSTEM_SYSRULES_dynamicLink"(
            label: if(
              cons!AS_GSS_TOGGLE_AUTOMATION_TESTING_ENABLED,
              "Select " & ri!currentVendor[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.legalName],
              ""
            ),
            saveInto: {
              a!save(
                ri!selectedVendorsMap,
                append(
                  ri!selectedVendorsMap,
                  rule!AS_GSS_constructSelectedVendorsMap(
                    selectedVendor: ri!currentVendor,
