_a-0000ed8a-02db-8000-9dfc-011c48011c48_.sail_code:
a!localVariables(
  /* Integration to get vendor details*/
  local!vendorAndDocumentsData: if(
    or(
      rule!AS_CO_UT_isBlank(ri!solicitationNumber),
      rule!AS_CO_UT_isBlank(ri!externalVendorId)
    ),
    null,
    rule!AS_GSS_INT_GET_getVendorsAndDocuments(
      solicitationNumber: ri!solicitationNumber,
      startIndex: a!defaultValue(ri!startIndex, 1),
      batchSize: a!defaultValue(ri!batchSize, 15),
      version: a!defaultValue(ri!version, 1),
      externalVendorId: ri!externalVendorId
    )
  ),
  local!responseBody: rule!AS_GSS_UT_deepIndex(
    item: local!vendorAndDocumentsData,
    indexArray: { "result", "body" }
  ),
  /* Constructing a dictionary to map based on the data returned*/
  if(
    or(
      rule!AS_CO_UT_isBlank(local!vendorAndDocumentsData.result),
      rule!AS_CO_UT_isBlank(
        rule!AS_GSS_UT_deepIndex(
          item: local!responseBody,
          indexArray: { "vendorDetails", "proposalDocuments" }
        )
      )
    ),
    /*   If no data is retrieved from Integration*/
    a!map(
      data: null,
      totalCount: 0,
      error: a!defaultValue(
        value: rule!AS_GSS_UT_deepIndex(
          item: local!responseBody,
          indexArray: { "error" }
        ),
        default: cons!AS_GSS_TXT_NO_RESULTS_FOUND_FROM_SAM_GOV
      ),
      errorDescription: rule!AS_GSS_UT_deepIndex(
        item: local!responseBody,
        indexArray: { "message" }
      ),
      displayProps: rule!AS_GSS_UT_constructDisplayPropsForVmIntegrationResponse(
        statusCode: rule!AS_GSS_UT_deepIndex(
          item: local!vendorAndDocumentsData,
          indexArray: { "result", "statusCode" }
        ),
        isSealed: rule!AS_GSS_UT_deepIndex(
          item: local!responseBody,
          indexArray: { "isSealed" }
        )
      )
    ),
    /*   When data is retrieved */
    a!map(
      data: #"SYSTEM_SYSRULES_forEach"(
        items: local!vendorAndDocumentsData.result.body.vendorDetails[1].proposalDocuments,
        expression: recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD(
          recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.appianDocId: rule!AS_CO_UT_indexPath(cdt: fv!item, field: "appianDocID"),
          recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.documentName: rule!AS_CO_UT_indexPath(cdt: fv!item, field: "documentName"),
          recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.version: rule!AS_CO_UT_indexPath(cdt: fv!item, field: "proposalVersion"),
          recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.fileType: rule!AS_CO_UT_indexPath(cdt: fv!item, field: "documentExtension"),
          recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.evaluationId: ri!evaluationId,
          recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.docType: cons!AS_GSS_REF_ID_DOC_TYPE_VENDOR,
          recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.documentSubType: rule!AS_CO_UT_indexPath(cdt: fv!item, field: "documentType"),
          recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.documentDescription: rule!AS_CO_UT_indexPath(
            cdt: fv!item,
            field: "documentDescription"
          ),
          recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.isDeleted: false(),
          recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.sourceApplicationId: cons!AS_GSS_REF_ID_SOURCE_APPLICATION_VM
        )
      ),
      totalCount: local!vendorAndDocumentsData.result.body.totalCount,
      error: null,
      errorDescription: null,
      displayProps: {}
    )
  )
)

