    ri!value >= 1000000,
    if(
      rule!AS_CO_UT_isNotBlank(ri!minSize),
      text(ri!minSize, "00000000000000000000.000"),
      ri!minSize
    ),
    ri!minSize
  ),
  local!maxSize: if(
    /*Using the direct decimal value when the value is less than 1000000*/
    ri!value >= 1000000,
    if(
      rule!AS_CO_UT_isNotBlank(ri!maxSize),
      text(ri!maxSize, "00000000000000000000.000"),
      ri!maxSize
    ),
    ri!maxSize
  ),
  if(
    rule!AS_CO_UT_isBlank(local!value),
    null,
    if(
      and(
        rule!AS_CO_UT_isNotBlank(local!minSize),
        local!value < local!minSize
      ),
      if(
        isnull(ri!minSizeMessage),
        rule!AS_CO_I18N_UT_displayLabel(
          i18nData: local!i18nData,
          bundleKey: "AS.CO.CommonObjects.vld_MinValue",
          arguments: local!minSize
        ),
        ri!minSizeMessage
      ),
      if(
        and(
          rule!AS_CO_UT_isNotBlank(local!maxSize),
          local!value > local!maxSize
        ),
        if(
          isnull(ri!maxSizeMessage),
          rule!AS_CO_I18N_UT_displayLabel(
            i18nData: local!i18nData,
            bundleKey: "AS.CO.CommonObjects.vld_MaxValue",
            arguments: local!maxSize
          ),
          ri!maxSizeMessage
        ),
        null
      )
    )
  )
)

_a-0000e2cd-bc96-8000-9ba2-011c48011c48_.sail_code:
cast(
   'type!{http://www.appian.com/ae/types/2009}Dictionary',
  a!localVariables(

    local!locale: if(
      ri!localeAgnostic,
      null,
      rule!AS_CO_I18N_UT_getUserLocale(user: loggedInUser())
    ),

    if(
      rule!AS_CO_UT_isBlank(ri!bundleFolder),

      null,

      a!localVariables(
        /*Wrapping this in a local variable to ensure caching -- it will only re-execute if inputs change*/
        local!bundleData: loadbundlepropertiesfile(
          ri!bundleFolder,
          local!locale,
          a!flatten(ri!bundleNames)
        ),

        local!bundleData
      )
    )
  )
)

_a-0000e2ad-62e7-8000-9ba2-011c48011c48_.sail_code:
cast(
   'type!{http://www.appian.com/ae/types/2009}Text',
  if(
    rule!AS_CO_UT_isBlank(ri!text),
    "",
    left(
      ri!text,
      rule!AS_CO_UT_replaceNull(nullableValue: ri!numChars, replacementValue: len(ri!text))
    )
  )
)
