_a-0000e52f-5316-8000-9bac-011c48011c48_.sail_code:
a!localVariables(
  local!sortToUse: if(
    /*SINGLE_OBJECT*/
    ri!returnType = cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
    ri!sort,

    /*AGGREGATION*/
    ri!returnType = cons!AS_CO_ENUM_QE_RETURN_TYPE_AGGREGATION,
    ri!sort,

    /*TOTAL_COUNT*/
    ri!returnType = cons!AS_CO_ENUM_QE_RETURN_TYPE_TOTAL_COUNT,
    null,

    /*DATA_SUBSET or OBJECT_ARRAY*/
    a!localVariables(
      /*Guess what the primary key is by using the first primitive field in the CDT*/
      local!primaryKeyField_guess: index(rule!AS_CO_UT_returnCdtFields(
        type: ri!entityType,
        onlyPrimitiveFields: true
      ), 1, null),

      /*Grab the appropriate input sort by the returnType*/
      local!inputSort: if(
        ri!returnType = cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
        ri!pagingInfo.sort,

        ri!returnType = cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
        ri!sort,

        null
      ),

      /*If the primaryKeyField_guess is not null, build a secondary sort by the PK field*/
      local!secondaryPkSortToAppend: if(
        or(isnull(local!primaryKeyField_guess), ri!isAggregation),
        {},
        #"SYSTEM_SYSRULES_sortInfo"(field: local!primaryKeyField_guess, ascending: true)
      ),

      /*Append the secondary PK sort to the sort*/
      local!sortWithSecondaryPkSortApplied: if(
        rule!AS_CO_UT_isBlank(local!inputSort),
        local!secondaryPkSortToAppend,
        append(local!inputSort, local!secondaryPkSortToAppend)
      ),

      local!sortWithSecondaryPkSortApplied
    )
  ),

  local!sortToUse
)

