_a-0000ec6b-e6c2-8000-9d9d-011c48011c48_.sail_code:
a!localVariables(
  local!factors: rule!AS_CO_UT_filterRecordByFieldValues(
    records: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.criteria],
    field: recordType!AS_GSS_Criteria_SYNCEDRECORD.parentCriteriaId,
    values: { null }
  ),
  local!subFactors: rule!AS_CO_UT_filterRecordByFieldValues(
    records: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.criteria],
    field: recordType!AS_GSS_Criteria_SYNCEDRECORD.parentCriteriaId,
    values: local!factors[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaId]
  ),
  local!isSetupCompleted: not(
    or(
      /*is Evaluation Summary incomplete?*/
      rule!AS_CO_UT_isBlank(
        value: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationTitle]
      ),
      rule!AS_CO_UT_isBlank(
        value: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationNumber]
      ),
      rule!AS_CO_UT_isBlank(
        value: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationDescription]
      ),
      rule!AS_CO_UT_isBlank(
        value: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationStartDate]
      ),
      rule!AS_CO_UT_isBlank(
        value: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationDueDate]
      ),
      rule!AS_CO_UT_isBlank(
        value: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationMethodId]
      ),
      /*is at least one phase completed and filled out*/
      and(
        cons!AS_GSS_TOGGLE_EVALUATION_CHIEF_VISIBILITY,
        rule!AS_CO_UT_isBlank(
          value: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationChief]
        )
      ),
      rule!AS_CO_UT_isBlank(
        value: rule!AS_CO_UT_filterRecordByFieldValues(
          records: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationPhase],
          field: recordType!AS_GSS_EvaluationPhase_SYNCEDRECORD.isActive,
          values: true
        )
      ),
      /*one of the three owner fields is populated*/
      and(
        rule!AS_CO_UT_isBlank(
          value: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.contractingOfficer]
        ),
        rule!AS_CO_UT_isBlank(
          value: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.contractingSpecialist]
        )
      )
    )
  ),
  local!isFactorsCompleted: not(
    or(
      /*is at least one criterion created and filled out */
      rule!AS_CO_UT_isBlank(
        value: rule!AS_CO_UT_filterRecordByFieldValues(
          records: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.criteria],
          field: recordType!AS_GSS_Criteria_SYNCEDRECORD.isActive,
          values: true
        )
      ),
      and(
        rule!AS_CO_UT_isNotBlank(
          input: rule!AS_CO_UT_filterRecordByFieldValues(
            records: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.criteria],
            field: recordType!AS_GSS_Criteria_SYNCEDRECORD.isActive,
            values: true
          )
        ),
        or(
          #"SYSTEM_SYSRULES_forEach"(
            items: rule!AS_CO_UT_filterRecordByFieldValues(
              records: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.criteria],
              field: recordType!AS_GSS_Criteria_SYNCEDRECORD.isActive,
              values: true
            ),
            expression: or(
              rule!AS_CO_UT_isBlank(
                value: fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.factorNumber]
              ),
              rule!AS_CO_UT_isBlank(
                value: fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaName]
              ),
              rule!AS_CO_UT_isBlank(
                value: fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaDescription]
              ),
              rule!AS_CO_UT_isBlank(
                value: fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.refRatingTypeId]
              ),
              rule!AS_CO_UT_isBlank(
                value: fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaChair]
              ),
              rule!AS_CO_UT_isBlank(
                value: fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.dueDate]
              )
            )
          )
        )
      ),
      if(
        ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.isWeightedFactorsRequired],
        or(
          /*Is factor weight does not equal 100*/
          rule!AS_GSS_UT_checkIfAnyWeightIsNullAndMismatchedTotalWeight(
            totalWeight: rule!AS_GSS_UT_returnsSumOfFactorsWeight(
              factorWeights: local!factors[recordType!AS_GSS_Criteria_SYNCEDRECORD.factorWeight]
            ),
            allFactorsWeights: local!factors[recordType!AS_GSS_Criteria_SYNCEDRECORD.factorWeight]
          ),
          /*Is any subfactors do not equal to the factor weights*/
          #"SYSTEM_SYSRULES_forEach"(
            items: local!factors,
            expression: a!localVariables(
              local!subFactors: rule!AS_CO_UT_filterRecordByFieldValues(
                records: local!factors,
                field: recordType!AS_GSS_Criteria_SYNCEDRECORD.parentCriteriaId,
                values: fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaId]
              ),
              local!sumOfSubfactorWeights: rule!AS_GSS_UT_returnsSumOfFactorsWeight(
                factorWeights: local!subFactors[recordType!AS_GSS_Criteria_SYNCEDRECORD.factorWeight]
              ),
              and(
                not(
                  rule!AS_GSS_UT_checkIfNullOrZero(
                    input: fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.factorWeight]
                  )
                ),
                rule!AS_GSS_UT_isNotBlank(value: local!subFactors),
                rule!AS_GSS_UT_checkIfAnyWeightIsNullAndMismatchedTotalWeight(
                  totalWeight: local!sumOfSubfactorWeights,
                  allFactorsWeights: local!subFactors[recordType!AS_GSS_Criteria_SYNCEDRECORD.factorWeight],
                  parentFactorWeight: fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.factorWeight]
                )
              )
            )
          ),
          /*Is any of the factor/subfactor weight is blank*/
          or(
            #"SYSTEM_SYSRULES_forEach"(
              items: {
                local!factors[recordType!AS_GSS_Criteria_SYNCEDRECORD.factorWeight],
                local!subFactors[recordType!AS_GSS_Criteria_SYNCEDRECORD.factorWeight]
              },
              expression: rule!AS_GSS_UT_checkIfNullOrZero(input: fv!item)
            )
          )
        ),
        {}
      )
    )
  ),
  local!isTeamsCompleted: not(
    or(
      /*is at least one evaluator present in the evaluation team*/
      rule!AS_CO_UT_isBlank(
        value: rule!AS_CO_UT_filterRecordByFieldValues(
          records: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.team],
          field: recordType!AS_GSS_EvaluatorTeam_SYNCEDRECORD.isActive,
          values: true
        )
      ),
      #"SYSTEM_SYSRULES_forEach"(
        items: rule!AS_CO_UT_filterRecordByFieldValues(
          records: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.team],
          field: recordType!AS_GSS_EvaluatorTeam_SYNCEDRECORD.isActive,
          values: true
        ),
        expression: rule!AS_CO_UT_isBlank(
          value: rule!AS_CO_UT_filterRecordByFieldValues(
            records: fv!item[recordType!AS_GSS_EvaluatorTeam_SYNCEDRECORD.teamMembership],
            field: recordType!AS_GSS_EvaluatorTeamMembership_SYNCEDRECORD.isActive,
            values: true
          )
        )
      ),
      /*all created criteria have an assigned due date and evaluator*/
      #"SYSTEM_SYSRULES_forEach"(
        items: rule!AS_CO_UT_filterRecordByFieldValues(
          records: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.criteria],
          field: recordType!AS_GSS_Criteria_SYNCEDRECORD.isActive,
          values: true
        ),
        expression: rule!AS_CO_UT_isBlank(
          value: rule!AS_CO_UT_filterRecordByFieldValues(
            records: fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.CriteriaAssignment],
            field: recordType!AS_GSS_CriteriaAssignment_SYNCEDRECORD.isActive,
            values: true
          )
        )
      ),
      #"SYSTEM_SYSRULES_forEach"(
        items: rule!AS_CO_UT_filterRecordByFieldValues(
          records: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.criteria],
          field: recordType!AS_GSS_Criteria_SYNCEDRECORD.isActive,
          values: true
        ),
        expression: a!localVariables(
          local!evaluationCriterion: fv!item,
          or(
            #"SYSTEM_SYSRULES_forEach"(
              items: rule!AS_CO_UT_filterCdtByField(
                cdt: local!evaluationCriterion[recordType!AS_GSS_Criteria_SYNCEDRECORD.CriteriaAssignment],
                field: recordType!AS_GSS_CriteriaAssignment_SYNCEDRECORD.isActive,
                value: true
              ),
              expression: rule!AS_CO_UT_isBlank(
                value: fv!item[recordType!AS_GSS_CriteriaAssignment_SYNCEDRECORD.assignee]
              )
            )
          )
        )
      )
    )
  ),
  local!isVendorsCompleted: not(
    or(
      /*is at least one vendor created with an associated document*/
      rule!AS_CO_UT_isBlank(
        value: rule!AS_CO_UT_filterRecordByFieldValues(
          records: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.vendor],
          field: recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.isActive,
          values: true
        )
      ),
      rule!AS_CO_UT_isBlank(
        value: rule!AS_CO_UT_filterRecordByFieldValues(
          records: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.vendor],
          field: recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.isDeleted,
          values: false
        )
      ),
      or(
        #"SYSTEM_SYSRULES_forEach"(
          items: rule!AS_CO_UT_filterRecordByFieldValues(
            records: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.vendor],
            field: recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.isActive,
            values: true
          ),
          expression: or(
            rule!AS_CO_UT_isBlank(
              wherecontains(
                fv!item[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.vendorId],
                rule!AS_CO_UT_filterRecordByFieldValues(
                  records: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.vendor],
                  field: recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.isDeleted,
                  values: false
                )[recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.vendorId]
              )
            ),
            rule!AS_CO_UT_isBlank(
              fv!item[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.totalPrice]
            )
          )
        )
      )
    )
  ),
  product(
    length(
      where(
        {
          local!isSetupCompleted,
          local!isFactorsCompleted,
          local!isTeamsCompleted,
          local!isVendorsCompleted
        },
        {}
      )
    ),
    cons!AS_GSS_INT_SECTION_WEIGHTAGE_FOR_PROGRESS_CALCULATION
  )
)

