          filters: {
            #"SYSTEM_SYSRULES_queryFilter"(
              field: recordType!AS_GSS_CriteriaAssignment_SYNCEDRECORD.isActive,
              operator: "=",
              value: true()
            )
          }
        ),
        #"SYSTEM_SYSRULES_relatedRecordData"(
          relationship: recordType!AS_GSS_Evaluation_SYNCEDRECORD.team,
          filters: {
            #"SYSTEM_SYSRULES_queryFilter"(
              field: recordType!AS_GSS_EvaluatorTeam_SYNCEDRECORD.isActive,
