_a-0000eb16-753f-8000-9d01-011c48011c48_.sail_code:
a!localVariables(
  local!groups: {
    cons!AS_GSS_GRP_BUSINESS_ROLES,
    cons!AS_GSS_GRP_CONTRACTING_PERSONNEL,
    cons!AS_GSS_TMG_GRP_TASK_RECIPIENTS
  },
  local!isEvaluationChiefExists: #"SYSTEM_SYSRULES_forEach"(
    local!groups,
    contains(
      togroup(
        a!groupMembers(
          group: fv!item,
          direct: true,
          memberType: "GROUP"
        ).data
      ),
      cons!AS_GSS_GRP_EVALUATION_CHAIRS
    )
  ),
  if(
    cons!AS_GSS_TOGGLE_EVALUATION_CHIEF_VISIBILITY,
    /*Returns the groups that doesn't contain Evaluation Chair to add when the toggle is on */
    index(
      local!groups,
      where(not(local!isEvaluationChiefExists)),
      null
    ),
    /*Returns the groups that contains Evaluation Chair to remove when the toggle is off*/
    index(
      local!groups,
      where(local!isEvaluationChiefExists),
      null
    )
  )
)
