a!localVariables(
  local!i18nData: rule!AS_CO_I18N_UT_loadBundleFromFolder(
    bundleFolder: cons!AS_CO_I18N_FLD_INTERNATIONALIZATION_FILES,
    bundleNames: "AS.CO.CommonObjects"
  ),
  if(
    rule!AS_CO_UT_isBlank(value: ri!maxAllowedFileSizeInBytes),
    "",
    if(
      if(
        rule!AS_CO_UT_isBlank(value: ri!fileSizesToCheck),
        0,
        ri!fileSizesToCheck
      ) > ri!maxAllowedFileSizeInBytes,
      if(
        /*If the allowed file size is less then KB, */
        ri!maxAllowedFileSizeInBytes < 1024,
        rule!AS_CO_I18N_UT_displayLabel(
          i18nData: local!i18nData,
          bundleKey: "AS.CO.CommonObjects.vld_FileSizeExceedsAllowedKB",
          arguments: rule!AS_CO_UT_fixed(
            number: ri!maxAllowedFileSizeInBytes / 1024,
            decimals: 3,
            no_commas: true()
          )
        ),
        if(
          /*If the allowed file size is less then MB, */
          ri!maxAllowedFileSizeInBytes < product(1024, 1024),
          rule!AS_CO_I18N_UT_displayLabel(
            i18nData: local!i18nData,
            bundleKey: "AS.CO.CommonObjects.vld_FileSizeExceedsAllowedKB",
            arguments: quotient(ri!maxAllowedFileSizeInBytes, 1024)
          ),
          /*If the allowed file size is in MB*/
          if(
            and(
              ri!maxAllowedFileSizeInBytes >= product(1024, 1024),
              ri!maxAllowedFileSizeInBytes <= product(1024, 1024, 1024)
            ),
            rule!AS_CO_I18N_UT_displayLabel(
              i18nData: local!i18nData,
              bundleKey: "AS.CO.CommonObjects.vld_FileSizeExceedsAllowedMB",
              arguments: quotient(
                quotient(ri!maxAllowedFileSizeInBytes, 1024),
                1024
              )
            ),
            ""
          )
        )
      ),
      ""
    )
  )
)

