/*Do a strict comarison against types*/
cast(
   'type!{http://www.appian.com/ae/types/2009}Boolean',
  and(
    rule!AS_CO_UT_typeCompare(
      value1: ri!value1,
      value2: ri!value2
    ),
    ri!value1 = ri!value2
  )
)

with(  
  local!typeNumber: rule!AS_CO_UT_returnTypeNumber(
    type: ri!type,
    typeOf: ri!typeOf
  ),

  /* If the object is empty, use null or {} depending on if it is array type or not */
  local!objectToCast: if(
    rule!AS_CO_UT_isNotBlank(ri!value),
    ri!value,
    rule!AS_CO_UT_checkIfSingleType(
      type: ri!type,
      typeOf: ri!typeOf
    ),
    null,
    {}
  ),

  cast(
    local!typeNumber,
    local!objectToCast
  )

)

