          fetchTotalCount: true,
          query: #"SYSTEM_SYSRULES_query"(
            logicalExpression: local!logicalExpression,
            pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(startIndex: 1, batchSize: 0)
          )
        ).totalCount,
        refreshOnVarChange: ri!triggerRefresh
      ),
      local!selection: if(
        ri!excludeNestedCdts = true,
        #"SYSTEM_SYSRULES_querySelection"(
          columns: #"SYSTEM_SYSRULES_forEach"(
