          a!isNotNullOrEmpty(ri!querySelection),
          ri!querySelection,
          null
        )
      ),
      local!data: a!refreshVariable(
        value: a!flatten(
          #"SYSTEM_SYSRULES_forEach"(
            items: enumerate(
              ceiling(local!totalCount / local!batchSize)
            ) * local!batchSize + 1,
            expression: #"SYSTEM_SYSRULES_queryEntity_v2"(
