a!localVariables(
  /*The button layout, which can dynamically be inside a section or not*/
  /*Note that when using this pattern of storing an interface as a local variable, it must be set to refreshAlways = true*/
  local!buttonLayout: a!refreshVariable(
    value: #"SYSTEM_SYSRULES_ButtonLayout"(
      primaryButtons: {
        #"SYSTEM_SYSRULES_ButtonWidget"(
          accessibilityText: ri!confirmAccessibilityText,
          label: ri!confirmLabel,
          size: ri!confirmSize,
          saveInto: if(
            rule!AS_CO_UT_isBlank(
              ri!confirmSaveInto
            ),
            {},
            ri!confirmSaveInto
          ),
          style: rule!AS_CO_UT_replaceNull(
            nullableValue: ri!confirmStyle,
            replacementValue: "PRIMARY"
          ),
          validate: true,
          validationGroup: ri!confirmValidationGroup,
          submit: ri!confirmSubmit,
          disabled: ri!confirmDisabled,
          confirmHeader: ri!confirmHeader,
          confirmMessage: ri!confirmMessage
        )
      },
      secondaryButtons: {
        #"SYSTEM_SYSRULES_ButtonWidget"(
          accessibilityText: ri!cancelAccessibilityText,
          label: ri!cancelLabel,
          size: ri!cancelSize,
          saveInto: if(
            rule!AS_CO_UT_isBlank(
              ri!cancelSaveInto
            ),
            {},
            ri!cancelSaveInto
          ),
          style: rule!AS_CO_UT_replaceNull(
            nullableValue: ri!cancelStyle,
            replacementValue: "NORMAL"
          ),
          validate: false,
          submit: ri!cancelSubmit,
          disabled: ri!cancelDisabled,
          confirmHeader: ri!cancelHeader,
          confirmMessage: ri!cancelMessage,
          showWhen: ri!cancelShowWhen
        )
      },
      showWhen: ri!showWhen
    ),
    refreshAlways: true
  ),
  /*Components to show*/
  if(
    ri!includeDivider,
    #"SYSTEM_SYSRULES_sectionLayout_v1"(
      divider: "ABOVE",
      contents: {
        local!buttonLayout
      }
    ),
    local!buttonLayout
  )
)

_a-0000e3e6-fb42-8000-9ba5-011c48011c48_.sail_code:
a!localVariables(
  local!startTime: now(),
  local!isIntegrationOn: cons!AS_GAM_BOL_OFFICE_365,
  local!isIntegrationInitiated: false,
  local!isIntegrationFail: false,
  local!getDocSource: rule!AS_GSS_QR_getEvaluationDocuments(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
    evaluationId: ri!task.evaluation.evaluationId,
    appianDocId: ri!task.reviewDocumentId,
    fields: {
      recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.sourceApplicationId,
      recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.docNameWithExtension
    }
  ),
  local!isDocSourceFromVm: tointeger(
    index(
      local!getDocSource,
      recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.sourceApplicationId,
      null
    )
  ) = cons!AS_GSS_REF_ID_SOURCE_APPLICATION_VM,
  local!isDocSourceFromGcw: tointeger(
    index(
      local!getDocSource,
      recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.sourceApplicationId,
      null
    )
  ) = cons!AS_GSS_REF_ID_SOURCE_APPLICATION_GCW,
  #"SYSTEM_SYSRULES_formLayout_v1"(
    label: if(
      rule!AS_CO_UT_isBlank(ri!formTitle),
      ri!task.taskName,
      ri!formTitle
    ),
    contents: {
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              #"SYSTEM_SYSRULES_columnsLayout"(
                columns: {
                  #"SYSTEM_SYSRULES_columnLayout"(
                    contents: {
                      rule!AS_CO_DSP_displayProminentAndSecondaryBodyText(
                        align: rule!AS_CO_UT_replaceNull(
                          nullableValue: ri!align,
                          replacementValue: "CENTER"
                        ),
                        prominentText: ri!headerText,
                        secondaryText: ri!bodyText,
                        includeMarginAbove: true,
                        includeMarginBelow: true
                      ),
                      if(
                        rule!AS_CO_UT_isBlank(ri!task.docUploadContext),
                        ri!taskContents,
                        {}
                      )
                    }
                  ),
                  #"SYSTEM_SYSRULES_columnLayout"(contents: {}, width: "EXTRA_NARROW")
                }
              ),
              #"SYSTEM_SYSRULES_columnsLayout"(
                columns: #"SYSTEM_SYSRULES_columnLayout"(
                  contents: ri!taskContents,
                  showWhen: rule!AS_CO_UT_isNotBlank(ri!task.docUploadContext)
                )
              )
            },
            width: rule!AS_CO_UT_replaceNull(
              nullableValue: ri!columnWidth,
              replacementValue: "AUTO"
            )
          ),
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              #"SYSTEM_SYSRULES_boxLayout"(
                label: "Task Details",
                contents: {
                  /*Link to push review document to sharepoint */
                  #"SYSTEM_SYSRULES_richTextDisplayField"(
                    label: "Document",
                    value: #"SYSTEM_SYSRULES_richTextItem_v1"(
                      text: index(
                        local!getDocSource,
                        recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.docNameWithExtension,
                        null
                      ),
                      link: if(
                        and(
                          local!isIntegrationOn,
                          local!isDocSourceFromVm = false(),
                          local!isDocSourceFromGcw = false()
                        ),
                        #"SYSTEM_SYSRULES_dynamicLink"(
                          saveInto: {
                            /*To save the review document id to the appianDocId of docUploadContext before pushing it to sharepoint*/
                            a!save(
                              ri!task.docUploadContext.appianDocumentId,
                              ri!task.reviewDocumentId
                            ),
                            #"SYSTEM_SYSRULES_startProcess"(
                              processModel: cons!AS_GSS_PM_UPLOAD_DOCUMENT_TO_SHAREPOINT,
                              processParameters: {
                                task: ri!task,
                                tempWordDocId: ri!task.reviewDocumentId
                              },
                              onSuccess: {
                                a!save(local!isIntegrationInitiated, true)
                              },
                              onError: { a!save(local!isIntegrationFail, true) }
                            ),
                            if(
                              rule!AS_CO_UT_isBlank(ri!sharepointDocLink),
                              {
                                a!save(
                                  ri!task,
                                  rule!AS_GSS_TMG_QE_getRuntimeTask(
                                    taskId: ri!task.taskId,
                                    evaluationId: ri!task.evaluation.evaluationId,
                                    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT
                                  )
                                ),
                                a!save(
                                  ri!sharepointDocLink,
                                  ri!task.docUploadContext.office365DocumentLink
                                )
                              },
                              null
                            )
                          },
                          showWhen: rule!AS_CO_UT_isBlank(ri!sharepointDocLink)
                        ),
                        if(
                          local!isDocSourceFromVm,
                          rule!AS_GSS_CP_singleClickDocumentDownloadLinkFromExternalSource(
                            source: cons!AS_GSS_ENUM_TXT_PROPOSAL_DOC_DOWNLOAD_SOURCE_VM,
                            appianDocId: tointeger(ri!task.reviewDocumentId)
                          ),
                          rule!AS_GAM_CP_documentDownloadLink(document: ri!task.reviewDocumentId)
                        )
                      ),
                      linkStyle: "STANDALONE"
                    ),
                    showWhen: and(
                      rule!AS_CO_UT_isNotBlank(ri!task.reviewDocumentId),
                      rule!AS_CO_UT_isBlank(ri!sharepointDocLink)
                    )
                  ),
                  /*Link to open review document in sharepoint */
                  #"SYSTEM_SYSRULES_richTextDisplayField"(
                    label: "Document",
                    showWhen: and(
                      rule!AS_CO_UT_isNotBlank(ri!sharepointDocLink),
                      rule!AS_CO_UT_isNotBlank(ri!task.reviewDocumentId)
                    ),
                    value: #"SYSTEM_SYSRULES_richTextItem_v1"(
                      text: "Open" & " " & document(ri!task.reviewDocumentId, "name"),
                      link: if(
                        rule!AS_CO_UT_isBlank(ri!sharepointDocLink),
                        {},
                        #"SYSTEM_SYSRULES_safeLink"(uri: ri!sharepointDocLink)
                      ),
                      linkStyle: "STANDALONE"
                    )
                  ),
                  #"SYSTEM_SYSRULES_richTextDisplayField"(
                    label: "Evaluation",
                    value: #"SYSTEM_SYSRULES_richTextItem_v1"(
                      text: ri!task.evaluation.evaluationNumber,
                      link: rule!AS_CO_UT_recordLink(
                        label: ri!task.evaluation.evaluationNumber,
                        recordType: recordType!AS_GSS_Evaluation_RECORD,
                        recordId: ri!task.evaluation.evaluationId,
                        siteWebAddressIdentifier: cons!AS_GSS_ENUM_TEXT_WEB_ADDRESS_IDENTIFIER,
                        showWhen: true,
                        style: "STRONG",
                        newTab: true
                      ),
                      linkStyle: "STANDALONE"
                    ),
                    showWhen: ri!showEvaluationColumn
                  ),
                  rule!AS_CO_INP_textField(
                    label: "Description",
                    labelPosition: "ABOVE",
                    readOnly: true,
                    value: ri!task.taskDesc
                  ),
                  #"SYSTEM_SYSRULES_columnsLayout"(
                    columns: {
                      #"SYSTEM_SYSRULES_columnLayout"(
                        contents: {
                          rule!AS_CO_INP_textField(
                            label: "Assigned Group",
                            labelPosition: "ABOVE",
                            readOnly: true,
                            value: rule!AS_CO_UT_displayGroupOrUser(groupOrUser: ri!task.groupAssignee)
                          )
                        }
                      ),
                      #"SYSTEM_SYSRULES_columnLayout"(
                        contents: {
                          rule!AS_CO_INP_textField(
                            label: "Individual Assignee",
                            labelPosition: "ABOVE",
                            readOnly: true,
                            value: rule!AS_CO_UT_displayUser(user: ri!task.userAssignee)
                          )
                        }
                      )
                    }
                  ),
                  #"SYSTEM_SYSRULES_columnsLayout"(
                    columns: {
                      #"SYSTEM_SYSRULES_columnLayout"(
                        contents: {
                          rule!AS_CO_INP_textField(
                            label: "Due Date",
                            readOnly: true,
                            value: rule!AS_CO_UT_formatDate(date: ri!task.dueDate),
                            labelPosition: "ABOVE"
                          )
                        }
                      ),
                      #"SYSTEM_SYSRULES_columnLayout"(
                        contents: {
                          rule!AS_CO_INP_textField(
                            label: "Duration",
                            readOnly: true,
                            value: if(
                              rule!AS_CO_UT_isBlank(ri!task.duration),
                              "-",
                              ri!task.duration & " " & ri!task.durationUnit.refLabel
                            ),
                            labelPosition: "ABOVE"
                          )
                        }
                      )
                    }
                  )
                }
              )
            },
            width: "MEDIUM"
          )
        }
      )
    },
    buttons: #"SYSTEM_SYSRULES_ButtonLayout"(
      primaryButtons: {
        #"SYSTEM_SYSRULES_ButtonWidget"(
          accessibilityText: ri!confirmAccessibilityText,
          label: rule!AS_CO_UT_replaceNull(
            nullableValue: ri!confirmButtonLabel,
            replacementValue: "Submit"
          ),
          size: ri!confirmSize,
          saveInto: {
            a!save(
              ri!userAction,
              cons!AS_CO_ENUM_USER_ACTION_SUBMIT
            ),
            a!save(ri!startTime, local!startTime),
            a!save(
              ri!task.taskStatus.taskStatusId,
              cons!AS_GSS_TMG_REF_ID_TASK_STATUS_COMPLETE
            ),
            ri!confirmSaveInto
          },
          style: rule!AS_CO_UT_replaceNull(
            nullableValue: ri!confirmStyle,
            replacementValue: "PRIMARY"
          ),
          validate: true,
          submit: true,
          disabled: ri!confirmDisabled,
          confirmHeader: ri!confirmHeader,
          confirmMessage: ri!confirmMessage
        )
      },
      secondaryButtons: {
        #"SYSTEM_SYSRULES_ButtonWidget"(
          accessibilityText: ri!cancelAccessibilityText,
          label: "Exit",
          size: ri!cancelSize,
          saveInto: {
            a!save(
              ri!userAction,
              cons!AS_CO_ENUM_USER_ACTION_CANCEL
            )
          },
          style: rule!AS_CO_UT_replaceNull(
            nullableValue: ri!cancelStyle,
            replacementValue: "NORMAL"
          ),
          validate: false,
          submit: true,
          disabled: false,
          confirmHeader: ri!cancelHeader,
          confirmMessage: ri!cancelMessage,
          showWhen: ri!cancelShowWhen
        )
      }
    ),
    validations: #"SYSTEM_SYSRULES_validationMessage"(
      message: ri!validation,
      validateAfter: "SUBMIT",
      showWhen: ri!validateWhen
    )
  )
)

