#"SYSTEM_SYSRULES_gridField_v2"(
  label: "My Tasks",
  labelPosition: "COLLAPSED",
  shadeAlternateRows: false,
  selectable: true,
  selectionValue: index(
    ri!selectedTasks,
    recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskId,
    null
  ),
  selectionSaveInto: {
    a!save(
      ri!selectedTasks,
      append(ri!selectedTasks, fv!selectedRows)
    ),
    a!save(
      ri!selectedTasks,
      difference(ri!selectedTasks, fv!deselectedRows)
    ),

  },
  data: #"SYSTEM_SYSRULES_recordData"(
    recordType: recordType!AS_GSS_TMG_Task_SYNCEDRECORD,
    fields: {
      recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskId,
      recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskName,
      recordType!AS_GSS_TMG_Task_SYNCEDRECORD.completionDate,
      recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskStatusId,
      recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskBehaviorType.behaviorDisplayName,
      recordType!AS_GSS_TMG_Task_SYNCEDRECORD.dueDate,
      recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskBehaviorType.behaviorTypeCode,
      recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskBehaviorType.behaviorSubtypeCode,
      recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskBehaviorType.configurationLevelCode,
      recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskCategory.categoryName,
      recordType!AS_GSS_TMG_Task_SYNCEDRECORD.userAssignee,
      recordType!AS_GSS_TMG_Task_SYNCEDRECORD.groupAssignee,
      recordType!AS_GSS_TMG_Task_SYNCEDRECORD.evaluationId,
      recordType!AS_GSS_TMG_Task_SYNCEDRECORD.evaluation.evaluationNumber,
      recordType!AS_GSS_TMG_Task_SYNCEDRECORD.evaluation.evaluationTitle,
      recordType!AS_GSS_TMG_Task_SYNCEDRECORD.evaluation.evaluationStatus.refDataId,
      recordType!AS_GSS_TMG_Task_SYNCEDRECORD.criteria.EvaluatorTeam.teamName,
      recordType!AS_GSS_TMG_Task_SYNCEDRECORD.evaluation.contractingOfficer,
      recordType!AS_GSS_TMG_Task_SYNCEDRECORD.evaluation.contractingSpecialist,
      recordType!AS_GSS_TMG_Task_SYNCEDRECORD.evaluation.evaluationChief
    },
    filters: if(
      ri!isHomePage,
      #"SYSTEM_SYSRULES_queryLogicalExpression"(
        operator: "AND",
        filters: {
          #"SYSTEM_SYSRULES_queryFilter"(
            field: recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskStatusId,
            operator: "in",
            value: {
              cons!AS_GSS_TMG_REF_ID_TASK_STATUS_ASSIGNED,
              cons!AS_GSS_TMG_REF_ID_TASK_STATUS_IN_PROGRESS
            }
          ),
          #"SYSTEM_SYSRULES_queryFilter"(
            field: recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskBehaviorType.behaviorTypeCode,
            operator: "<>",
            value: cons!AS_GSS_TMG_ENUM_TASK_BEHAVIOR_TYPE_CODE_AUTOMATED
          )
        },
        logicalExpressions: #"SYSTEM_SYSRULES_queryLogicalExpression"(
          operator: "OR",
          filters: {
            #"SYSTEM_SYSRULES_queryFilter"(
              field: recordType!AS_GSS_TMG_Task_SYNCEDRECORD.userAssignee,
              operator: "=",
              value: ri!user
            )
          },
          logicalExpressions: #"SYSTEM_SYSRULES_queryLogicalExpression"(
            operator: "AND",
            filters: {
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!AS_GSS_TMG_Task_SYNCEDRECORD.userAssignee,
                operator: "is null",
                applyWhen: not(ri!isEvaluator)
              ),
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!AS_GSS_TMG_Task_SYNCEDRECORD.groupAssignee,
                operator: "in",
                value: intersection(
                  a!groupsForUser(username: ri!user),
                  rule!AS_CO_UT_cast(
                    type: 'type!{http://www.appian.com/ae/types/2009}Group?list',
                    value: rule!AS_CO_UT_returnMembersOfAnyGroups(
                      groups: cons!AS_GSS_GRP_ALL_BUSINESS_USERS,
                      onlyDirectMembers: false(),
                      memberType: "GROUP"
                    )
                  )
                ),
                applyWhen: not(ri!isEvaluator)
              )
            }
          )
        ),
        ignoreFiltersWithEmptyValues: true()
      ),
      #"SYSTEM_SYSRULES_queryLogicalExpression"(
        operator: "AND",
        filters: {
          #"SYSTEM_SYSRULES_queryFilter"(
            field: recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskStatusId,
            operator: "in",
            value: {
              cons!AS_GSS_TMG_REF_ID_TASK_STATUS_ASSIGNED,
              cons!AS_GSS_TMG_REF_ID_TASK_STATUS_IN_PROGRESS
            }
          ),
          #"SYSTEM_SYSRULES_queryFilter"(
            field: recordType!AS_GSS_TMG_Task_SYNCEDRECORD.evaluationId,
            operator: "=",
            value: ri!evaluationId,
            applyWhen: rule!AS_CO_UT_isNotBlank(ri!evaluationId)
          ),
          #"SYSTEM_SYSRULES_queryFilter"(
            field: recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskBehaviorType.behaviorTypeCode,
            operator: "<>",
            value: cons!AS_GSS_TMG_ENUM_TASK_BEHAVIOR_TYPE_CODE_AUTOMATED
          ),
          #"SYSTEM_SYSRULES_queryFilter"(
            field: recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskBehaviorType.behaviorSubtypeCode,
            operator: "<>",
            value: cons!AS_GSS_TMG_ENUM_TASK_BEHAVIOR_SUBTYPE_CODE_COMPLETE_EVALUATION
          )
        },
        ignoreFiltersWithEmptyValues: true()
      )
    )
  ),
  columns: {
    #"SYSTEM_SYSRULES_gridColumn"(
      label: "Task Name",
      sortField: recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskName,
      value: #"SYSTEM_SYSRULES_richTextDisplayField"(
        preventWrapping: true,
        value: {
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: fv!row[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskName],
            style: "STRONG",
            link: #"SYSTEM_SYSRULES_startProcessLink"(
              processModel: rule!AS_GSS_TMG_UT_taskProcessAsLink(
                subTypeCode: fv!row[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskBehaviorType.behaviorSubtypeCode],

              ),
              processParameters: if(
                fv!row[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskBehaviorType.behaviorSubtypeCode] = cons!AS_GSS_TMG_ENUM_TASK_BEHAVIOR_SUBTYPE_CODE_COMPLETE_LPTA_EVALUATION,
                {
                  task: rule!AS_GSS_TMG_QR_getTasks(
                    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
                    taskId: fv!row[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskId]
                  )
                },
                {
                  task: rule!AS_GSS_TMG_QE_getRuntimeTask(
                    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
                    taskId: fv!row[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskId]
                  )
                }
              ),
              showWhen: if(
                ri!isHomepage,
                contains(
                  {
                    cons!AS_GSS_TMG_ENUM_TASK_BEHAVIOR_TYPE_CODE_CHECKBOX,
                    cons!AS_GSS_TMG_ENUM_TASK_BEHAVIOR_TYPE_CODE_DATA_ENTRY
                  },
                  fv!row[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskBehaviorType.behaviorTypeCode]
                ),
                if(
                  /*disable task actions*/
                  or(
                    rule!AS_CO_UT_isBlank(fv!row),
                    #"SYSTEM_SYSRULES_forEach"(
                      items: fv!row,
                      expression: if(
                        /*First check that the task status is assigned*/
                        rule!AS_CO_UT_not(
                          contains(
                            {
                              cons!AS_GSS_TMG_REF_ID_TASK_STATUS_ASSIGNED,
                              cons!AS_GSS_TMG_REF_ID_TASK_STATUS_IN_PROGRESS
                            },
                            fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskStatusId]
                          )
                        ),
                        true,
                        if(
                          /*Then check if there is a default assignee on the task*/
                          rule!AS_CO_UT_isNotBlank(
                            fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.groupAssignee]
                          ),
                          /*Return false (not disabled) if EITHER the user is in the default assignee group OR the user is the individual assignee*/
                          /*Returns true (disabled) when the task behaviour type is "Complete Evaluation" or "Complete LPTA Evaluation" and the loggedInUser is not a userAssignee*/
                          and(
                            or(
                              contains(
                                {
                                  cons!AS_GSS_TMG_ENUM_TASK_BEHAVIOR_SUBTYPE_CODE_COMPLETE_EVALUATION,
                                  cons!AS_GSS_TMG_ENUM_TASK_BEHAVIOR_SUBTYPE_CODE_COMPLETE_LPTA_EVALUATION
                                },
                                tostring(
                                  fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskBehaviorType.behaviorSubtypeCode]
                                )
                              ),
                              tostring(
