_a-0000ec6b-e6c2-8000-9d9d-011c48011c48_.sail_code:
or(
  /*Check if all tasks are blank*/
  rule!AS_CO_UT_isBlank(ri!selectedTasks),
  #"SYSTEM_SYSRULES_forEach"(
    items: ri!selectedTasks,
    expression: if(
      /*Returns false (enabled) when the task behaviour type is "Complete Evaluation"*/
      tostring(
        fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskBehaviorType.behaviorSubtypeCode]
      ) = cons!AS_GSS_TMG_ENUM_TASK_BEHAVIOR_SUBTYPE_CODE_COMPLETE_EVALUATION,
      /*Changed it to true because we are not using claim tasks for criteria tasks only using it for process tasks*/
      true,
      if(
        and(
          tostring(
            fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskBehaviorType.behaviorSubtypeCode]
          ) = cons!AS_GSS_TMG_ENUM_TASK_BEHAVIOR_SUBTYPE_CODE_COMPLETE_LPTA_EVALUATION,
          contains(
            touser(
              {
                fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.evaluation.contractingOfficer],
                fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.evaluation.contractingSpecialist],
                fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.evaluation.evaluationChief],
                fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.userAssignee]
              }
            ),
            ri!user
          )
        ),
        false(),
        if(
          /*First check that the task status is assigned*/
          rule!AS_CO_UT_not(
            contains(
              {
                cons!AS_GSS_TMG_REF_ID_TASK_STATUS_ASSIGNED,
                cons!AS_GSS_TMG_REF_ID_TASK_STATUS_IN_PROGRESS
              },
              fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskStatusId]
            )
          ),
          true,
          if(
            /*Then check if there is a default assignee on the task*/
            rule!AS_CO_UT_isNotBlank(
              fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.groupAssignee]
            ),
            /*Return false (not disabled) if EITHER the user is in the default assignee group OR the user is the individual assignee*/
            and(
              rule!AS_CO_UT_not(
                rule!AS_CO_UT_isUserMemberOfGroups(
                  groups: fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.groupAssignee],
                  user: loggedInUser()
                )
              ),
              rule!AS_CO_UT_not(
                fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.userAssignee] = ri!user
              )
            ),
            /*Return false (not disabled) if the user is the individual assignee*/
            rule!AS_CO_UT_not(
              fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.userAssignee] = ri!user
            )
          )
        )
      )
    )
  )
)

_a-0000e5da-a251-8000-9bbe-011c48011c48_.sail_code:
a!localVariables(
  local!factorDetails: rule!AS_GSS_QE_getEvaluationCriteria(
    evaluationId: ri!evaluationId,
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY
  ),
  local!vendorDetails: rule!AS_GSS_QE_getEvaluationVendor(
    evaluationId: ri!evaluationId,
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY
  ),
  /*Fetching only first two factor data*/
  local!finalFactorDetails: index(
    rule!AS_CO_UT_rejectNullValuesFromArray(
      union(
        local!factorDetails,
        local!factorDetails
      )
    ),
    enumerate(2) + 1,
    null
  ),
  /*Fetching only first two vendor data*/
  local!finalVendorDetails: index(
    rule!AS_CO_UT_rejectNullValuesFromArray(
      union(
        local!vendorDetails.legalName,
        local!vendorDetails.legalName
      )
    ),
    enumerate(2) + 1,
    null
  ),
  if(
    /* checking if template is recommendation/consensus */
    ri!isRecommendation,
    {
      /*factor 1*/
      factor1: if(
        rule!AS_CO_UT_isBlank(
          local!finalFactorDetails.factorNumber[1]
        ),
        cons!AS_GSS_TXT_INSERT_INFO,
        local!finalFactorDetails.factorNumber[1] & " | " & local!finalFactorDetails.criteriaName[1]
      ),
      /*factor 2*/
      factor2: if(
        rule!AS_CO_UT_isBlank(
          local!finalFactorDetails.factorNumber[2]
        ),
        cons!AS_GSS_TXT_INSERT_INFO,
        local!finalFactorDetails.factorNumber[2] & " | " & local!finalFactorDetails.criteriaName[2]
      ),
      /*vendorA1*/
      vendorA1: if(
        rule!AS_CO_UT_isBlank(local!finalVendorDetails[1]),
        cons!AS_GSS_TXT_INSERT_INFO,
        local!finalVendorDetails[1]
      ),
      /*vendorB1*/
      vendorB1: if(
        rule!AS_CO_UT_isBlank(local!finalVendorDetails[2]),
        cons!AS_GSS_TXT_INSERT_INFO,
        local!finalVendorDetails[2]
      ),
      /*vendorA2*/
      vendorA2: if(
        or(rule!AS_CO_UT_isBlank(local!finalVendorDetails[1]),rule!AS_CO_UT_isBlank(local!finalFactorDetails[2])),
        cons!AS_GSS_TXT_INSERT_INFO,
        local!finalVendorDetails[1]
      ),
      /*vendorB2*/
      vendorB2: if(
        or(rule!AS_CO_UT_isBlank(local!finalVendorDetails[2]),rule!AS_CO_UT_isBlank(local!finalFactorDetails[2])),
        cons!AS_GSS_TXT_INSERT_INFO,
        local!finalVendorDetails[2]
      )
    },
    {
      /*vendorName*/
      /*vendorName: if(*/
        /*rule!AS_CO_UT_isBlank(local!finalVendorDetails[1]),*/
        /*cons!AS_GSS_TXT_INSERT_INFO,*/
        /*local!finalVendorDetails[1]*/
      /*),*/
      /* GAM-2883: do not populate vendorName since there are multiple per evaluation */
      vendorName: cons!AS_GSS_TXT_INSERT_INFO,

      /*evaluator*/
      /*evaluator: = if(*/
        /*rule!AS_CO_UT_isBlank(*/
          /*local!finalFactorDetails[1].assignments*/
        /*),*/
        /*cons!AS_GSS_TXT_INSERT_INFO,*/
        /*rule!AS_GAM_UT_concatUserFirstNameLastName(*/
          /*firstName: local!finalFactorDetails[1].assignments[1].assignee.firstName,*/
          /*lastName: local!finalFactorDetails[1].assignments[1].assignee.lastName*/
        /*)*/
      /*),*/
      /* GAM-2883: do not populate evaluator since there are multiple per evaluation */
      evaluator: cons!AS_GSS_TXT_INSERT_INFO,

      /*factornumber*/
      /*factorNumber: = if(*/
        /*rule!AS_CO_UT_isBlank(*/
          /*local!finalFactorDetails[1].factorNumber*/
        /*),*/
        /*cons!AS_GSS_TXT_INSERT_INFO,*/
        /*local!finalFactorDetails[1].factorNumber*/
      /*),*/
      /* GAM-2883: do not populate evaluator since there are multiple per evaluation */
      factornumber: cons!AS_GSS_TXT_INSERT_INFO,

      /*chairperson*/
      /*chairPerson: = if(*/
        /*rule!AS_CO_UT_isBlank(*/
          /*local!finalFactorDetails[1].criteriaChair*/
        /*),*/
        /*cons!AS_GSS_TXT_INSERT_INFO,*/
        /*rule!AS_GAM_UT_concatUserFirstNameLastName(*/
          /*firstName: local!finalFactorDetails[1].criteriaChair.firstName,*/
          /*lastName: local!finalFactorDetails[1].criteriaChair.lastName*/
        /*)*/
      /*),*/
      /* GAM-2883: do not populate chairPerson since there are multiple per evaluation */
      chairPerson: cons!AS_GSS_TXT_INSERT_INFO,

      /* GAM-2883: do not populate finalRating since there are multiple per evaluation */
      finalRating: cons!AS_GSS_TXT_INSERT_INFO,

      /* GAM-2883: do not populate finalComment since there are multiple per evaluation */
      finalComment: cons!AS_GSS_TXT_INSERT_INFO,

      /*evalchief*/
      evaluationchief: = if(
        rule!AS_CO_UT_isBlank(
          local!finalFactorDetails[1].evaluation.evaluationChief
        ),
        cons!AS_GSS_TXT_INSERT_INFO,
        rule!AS_GAM_UT_concatUserFirstNameLastName(
          firstName: local!finalFactorDetails[1].evaluation.evaluationChief.firstName,
          lastName: local!finalFactorDetails[1].evaluation.evaluationChief.lastName
        )
      ),
      /*CO*/
      contractingOfficer: = if(
        rule!AS_CO_UT_isBlank(
          local!finalFactorDetails[1].evaluation.contractingOfficer
        ),
        cons!AS_GSS_TXT_INSERT_INFO,
        rule!AS_GAM_UT_concatUserFirstNameLastName(
          firstName: local!finalFactorDetails[1].evaluation.contractingOfficer.firstName,
          lastName: local!finalFactorDetails[1].evaluation.contractingOfficer.lastName
        )
      ),
      /*CS*/
      contractingspecialist: = if(
        rule!AS_CO_UT_isBlank(
          local!finalFactorDetails[1].evaluation.contractingSpecialist
        ),
        cons!AS_GSS_TXT_INSERT_INFO,
        rule!AS_GAM_UT_concatUserFirstNameLastName(
          firstName: local!finalFactorDetails[1].evaluation.contractingSpecialist.firstName,
          lastName: local!finalFactorDetails[1].evaluation.contractingSpecialist.lastName
        )
      )
    }
  )
)

