a!localVariables(
  if(
    /*condition to show error when office 365 is ON and could not be connected*/
    and(
      ri!isIntegrationOn,
      or(
        and(
          toboolean(ri!isIntegrationInitiated) = true,
          rule!AS_CO_UT_isBlank(ri!sharepointDocLink)
        ),
        toboolean(ri!isIntegrationFail) = true
      )
    ),
    {
      rule!AS_CO_DSP_errorBanner(
        label: "Unable to connect to Office 365 at this time. Contact your system administrator."
      )
    },
    {
      if(
        /*Condition to check if the document details and tempWordDocId are present when created from related action*/
        all(
          rule!AS_CO_UT_isNotBlank(_),
          {
            ri!task.docUploadContext.docTypeId,
            ri!task.docUploadContext.docTemplate,
            ri!tempWordDocId
          }
        ),
        {
          /*Step 1*/
          rule!AS_CO_DSP_displayHeader(
            headerText: "Step 1:",
            align: "LEFT"
          ),
          if(
            ri!isIntegrationOn,
            {
              rule!AS_CO_DSP_infoBanner(
                secondaryLabel: "Changes made to the evaluation after generating the document will not be reflected in the document",
                showWhen: and(
                  ri!isIntegrationOn,
                  rule!AS_CO_UT_isBlank(ri!sharepointDocLink)
                )
              ),
              #"SYSTEM_SYSRULES_sideBySideLayout"(
                alignVertical: "MIDDLE",
                items: {
                  #"SYSTEM_SYSRULES_sideBySideItem"(
                    item: #"SYSTEM_SYSRULES_richTextDisplayField"(
                      value: {
                        #"SYSTEM_SYSRULES_richTextItem_v1"(
                          text: {
                            #"SYSTEM_SYSRULES_richTextItem_v1"(
                              text: rule!AS_GAM_CO_I18N_UT_displayLabel(
                                bundle: ri!i18nData,
                                bundleKey: "ins_DocDownloadFirstStepforOffice365",
                                arguments: ri!task.docUploadContext.docTemplate.documentName
                              )
                            )
                          },
                          color: if(
                            rule!AS_CO_UT_isBlank(ri!sharepointDocLink),
                            "",
                            "SECONDARY"
                          ),
                          link: #"SYSTEM_SYSRULES_dynamicLink"(
                            saveInto: {
                              #"SYSTEM_SYSRULES_startProcess"(
                                processModel: cons!AS_GSS_PM_UPLOAD_DOCUMENT_TO_SHAREPOINT,
                                processParameters: {
                                  task: ri!task,
                                  tempWordDocId: ri!tempWordDocId/*Will skip writing to the task table when comes from related action*/

                                },
                                onSuccess: {
                                  a!save(ri!isIntegrationInitiated, true),
                                  /*To save the task details from the Sharepoint process model when the details are null*/
                                  if(
                                    rule!AS_CO_UT_isBlank(ri!task.taskId),
                                    a!save(ri!task, fv!processInfo.pv.task),
                                    {}
                                  )
                                },
                                onError: { a!save(ri!isIntegrationFail, true) }
                              ),
                              if(
                                rule!AS_CO_UT_isBlank(ri!sharepointDocLink),
                                {
                                  a!save(
                                    ri!taskDetail,
                                    rule!AS_GSS_TMG_QE_getRuntimeTask(
                                      taskId: ri!task.taskId,
                                      evaluationId: ri!task.evaluation.evaluationId,
                                      returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT
                                    )
                                  ),
                                  a!save(
                                    ri!sharepointDocLink,
                                    ri!taskDetail.docUploadContext.office365DocumentLink
                                  )
                                },
                                null
                              )
                            },
                            showWhen: rule!AS_CO_UT_isBlank(ri!sharepointDocLink)
                          ),
                          linkStyle: "STANDALONE"
                        )
                      }
                    )
                  )
                }
              )
            },
            #"SYSTEM_SYSRULES_sideBySideLayout"(
              alignVertical: "MIDDLE",
              items: {
                #"SYSTEM_SYSRULES_sideBySideItem"(
                  item: #"SYSTEM_SYSRULES_richTextDisplayField"(
                    value: {
                      #"SYSTEM_SYSRULES_richTextItem_v1"(
                        text: {
                          #"SYSTEM_SYSRULES_richTextItem_v1"(
                            text: "Auto populate and download"
                          ),
                          " ",
                          #"SYSTEM_SYSRULES_richTextItem_v1"(
                            text: ri!task.docUploadContext.docTemplate.documentName,
                            style: "STRONG"
                          )
                        },
                        link: rule!AS_GAM_CP_documentDownloadLink(document: ri!tempWordDocId),
                        linkStyle: "STANDALONE"
                      ),
                      char(13),
                      lower(
                        "OR"
                      ),
                      char(13),
                      #"SYSTEM_SYSRULES_richTextItem_v1"(
                        text: "Download unpopulated" & " " & ri!task.docUploadContext.docTemplate.documentName,
                        link: rule!AS_GAM_CP_documentDownloadLink(
                          document: rule!AS_GSS_getTemplateDocIdBasedOnDocName(
                            docName: ri!task.docUploadContext.docTemplate.documentName
                          )
                        ),
                        linkStyle: "STANDALONE"
                      )
                    }
                  )
                )
              }
            )
          ),
          /*Step 2*/
          rule!AS_CO_DSP_displayHeader(
            headerText: "Step 2:",
            align: "LEFT"
          ),
          #"SYSTEM_SYSRULES_sideBySideLayout"(
            alignVertical: "MIDDLE",
            items: {
              #"SYSTEM_SYSRULES_sideBySideItem"(
                item: if(
                  ri!isIntegrationOn,
                  #"SYSTEM_SYSRULES_richTextDisplayField"(
                    value: #"SYSTEM_SYSRULES_richTextItem_v1"(
                      text: {
                        #"SYSTEM_SYSRULES_richTextItem_v1"(
                          text: "Open and edit document"
                        )
                      },
                      link: if(
                        rule!AS_CO_UT_isBlank(ri!sharepointDocLink),
                        {},
                        #"SYSTEM_SYSRULES_safeLink"(uri: ri!sharepointDocLink)
                      ),
                      linkStyle: "STANDALONE"
                    )
                  ),
                  #"SYSTEM_SYSRULES_richTextDisplayField"(
                    value: #"SYSTEM_SYSRULES_richTextItem_v1"(
                      text: "Open the downloaded document to review, update, and save it"
                    )
                  )
                )
              )
            }
          ),
          /*Step 3*/
          rule!AS_CO_DSP_displayHeader(
            headerText: "Step 3:",
            align: "LEFT"
          ),
          if(
            ri!isIntegrationOn,
            #"SYSTEM_SYSRULES_richTextDisplayField"(
              value: #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: "When it is complete, click finalize to bring document back in"
              )
            ),
            {
              #"SYSTEM_SYSRULES_sideBySideLayout"(
                items: {
                  #"SYSTEM_SYSRULES_sideBySideItem"(
                    item: rule!AS_CO_CP_fileUploadField(
                      label: "Upload saved document",
                      target: tofolder(ri!task.evaluation.folderId),
                      maxSelections: 1,
                      value: ri!task.docUploadContext.appianDocumentId,
                      saveInto: {
                        ri!task.docUploadContext.appianDocumentId
                      },
                      required: true,
                      allowedFileExtensions: cons!AS_GSS_TXT_VALID_FILE_TYPES_FOR_EVALUATION_DOCUMENT,
                      instructions: rule!AS_GSS_TXT_documentUploadInstructions(
                        bundle: ri!i18nData,
                        showAllowedFileTypes: true(),
                        showMaxDocumentLimit: true(),
                        maxDocumentsAllowed: 1
                      )
                    )
                  )
                }
              ),
              #"SYSTEM_SYSRULES_sideBySideLayout"(
                items: {
                  #"SYSTEM_SYSRULES_sideBySideItem"(
                    item: rule!AS_CO_INP_paragraphField(
                      label: "Document Description",
                      instructions: rule!AS_CO_UT_instructionForCharactersEntered(
                        value: ri!task.docUploadContext.docDescription,
                        maxCharacters: cons!AS_CO_ENUM_PARAGRAPH_LENGTH_SHORT
                      ),
                      value: ri!task.docUploadContext.docDescription,
                      saveInto: ri!task.docUploadContext.docDescription,
                      placeholder: "Enter document description",
                      maxLength: cons!AS_CO_ENUM_PARAGRAPH_LENGTH_SHORT
                    ),
                    width: "1X"
                  )
                }
              )
            }
          )
        },
        {}
      )
    }
  )
)

