_a-0000eb72-c933-8000-9d28-011c48011c48_.sail_code:
/*

PLEASE REFER TO THE BOTTOM OF THIS RULE FOR INSTRUCTIONS ON HOW TO USE

*/
/*Error handling*/
a!match(
  value: null,
  whenTrue: a!isNullOrEmpty(ri!returnType),
  then: fn!error(
    concat(
      "Null `returnType` passed to AS_GSS_CO_UT_queryEntity for entityType `",
      ri!entityType,
      "`.  See documentation in AS_GSS_CO_UT_queryEntity for further information on how to use this parameter."
    )
  ),
  whenTrue: not(
    contains(
      rule!AS_CO_CONS_QE_RETURN_TYPES(),
      ri!returnType
    )
  ),
  then: fn!error(
    concat(
      "Unknown `returnType` (",
      ri!returnType,
      ") passed to AS_GSS_CO_UT_queryEntity for entityType `",
      ri!entityType,
      "`.  See documentation in AS_GSS_CO_UT_queryEntity for further information on how to use this parameter."
    )
  ),
  whenTrue: a!isNullOrEmpty(ri!entityType),
  then: fn!error(
    concat(
      "Null `entityType` passed to AS_GSS_CO_UT_queryEntity for entity `",
      ri!entity,
      "`.  See documentation in AS_GSS_CO_UT_queryEntity for further information on how to use this parameter."
    )
  ),
  whenTrue: and(
    ri!returnType = cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
    a!isNullOrEmpty(ri!pagingInfo)
  ),
  then: fn!error(
    concat(
      "`pagingInfo` is required (cannot be null) for `returnType` DATA_SUBSET"
    )
  ),
  whenTrue: and(
    ri!returnType <> cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
    a!isNotNullOrEmpty(ri!pagingInfo)
  ),
  then: fn!error(
    concat(
      "`pagingInfo` must be null when the `returnType` is not DATA_SUBSET, instead use `sort` to pass custom sorting, `batchSize` to pass custom batch size, and `startIndex` & `batchSize` (if null) will be automatically calculated for you"
    )
  ),
  whenTrue: and(
    ri!returnType = cons!AS_CO_ENUM_QE_RETURN_TYPE_AGGREGATION,
    a!isNullOrEmpty(ri!aggregation)
  ),
  then: fn!error(
    "You must provide an input for `aggregation` when `returnType` is AGGREGATION"
  ),
  whenTrue: and(
    not(
      contains(
        {
          cons!AS_CO_ENUM_QE_RETURN_TYPE_AGGREGATION,
          cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET
        },
        ri!returnType
      )
    ),
    a!isNotNullOrEmpty(ri!aggregation)
  ),
  then: fn!error(
    "You cannot provide an input for `aggregation` when `returnType` is not AGGREGATION or DATA_SUBSET"
  ),
  default: a!localVariables(
    local!castResultToType: displayvalue(
      ri!returnType,
      rule!AS_CO_CONS_QE_RETURN_TYPES(),
      {
        /*DATA_SUBSET*/'type!{http://www.appian.com/ae/types/2009}DataSubset',
        /*SINGLE_OBJECT*/ri!entityType,
        /*OBJECT_ARRAY*/rule!AS_CO_UT_returnArrayTypeNumber(type: ri!entityType),
        /*AGGREGATION*/'type!{http://www.appian.com/ae/types/2009}Map?list',
        /*TOTAL_COUNT*/'type!{http://www.appian.com/ae/types/2009}Integer'
      },
      null
    ),
    local!startIndex: if(
      ri!returnType = cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
      /*Use specific pagingInfo for DATA_SUBSET*/
      ri!pagingInfo.startIndex,
      1
    ),
    local!batchSize: displayvalue(
      ri!returnType,
      rule!AS_CO_CONS_QE_RETURN_TYPES(),
      {
        /*DATA_SUBSET*/ri!pagingInfo.batchSize,
        /*SINGLE_OBJECT*/1,
        /*OBJECT_ARRAY*/a!defaultValue(
          value: ri!batchSize,
          default: cons!AS_CO_INT_QUERY_MAX_BATCH_SIZE
        ),
        /*AGGREGATION*/a!defaultValue(
          value: ri!batchSize,
          default: cons!AS_CO_INT_QUERY_MAX_BATCH_SIZE
        ),
        /*TOTAL_COUNT*/0
      },
      null
    ),
    local!sort: rule!AS_CO_UT_zInternal_queryEntity_determineSort(
      entityType: ri!entityType,
      returnType: ri!returnType,
      pagingInfo: ri!pagingInfo,
      sort: ri!sort,
      isAggregation: a!isNotNullOrEmpty(ri!aggregation)
    ),
    local!pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
      startIndex: local!startIndex,
      batchSize: local!batchSize,
      sort: local!sort
    ),
    local!fetchTotalCount: displayvalue(
      ri!returnType,
      rule!AS_CO_CONS_QE_RETURN_TYPES(),
      {
        /*DATA_SUBSET*/true,
        /*SINGLE_OBJECT*/false,
        /*OBJECT_ARRAY*/false,
        /*AGGREGATION*/false,
        /*TOTAL_COUNT*/true
      },
      null
    ),
    local!queryResult: a!refreshVariable(
      value: if(
        ri!executeWhen = false,
        null(
          'type!{http://www.appian.com/ae/types/2009}DataSubset'
        ),
        #"SYSTEM_SYSRULES_queryEntity_v2"(
          entity: ri!entity,
          fetchTotalCount: local!fetchTotalCount,
          query: #"SYSTEM_SYSRULES_query"(
            pagingInfo: local!pagingInfo,
            aggregation: ri!aggregation,
            selection: if(
              and(
                ri!excludeNestedCdts = true,
                a!isNullOrEmpty(ri!aggregation)
              ),
              #"SYSTEM_SYSRULES_querySelection"(
                columns: #"SYSTEM_SYSRULES_forEach"(
                  items: rule!AS_CO_UT_returnCdtFields(
                    type: ri!entityType,
                    onlyPrimitiveFields: true
                  ),
                  expression: #"SYSTEM_SYSRULES_queryColumn"(field: fv!item)
                )
              ),
              null
            ),
            logicalExpression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
              operator: "AND",
              logicalExpressions: {
                #"SYSTEM_SYSRULES_queryLogicalExpression"(
                  operator: "AND",
                  ignoreFiltersWithEmptyValues: true,
                  filters: a!defaultValue(value: ri!filters, default: {})
                ),
                a!defaultValue(value: ri!logicalExpression, default: {})
              }
            )
          )
        )
      ),
      refreshOnVarChange: ri!triggerRefresh,
      refreshAfter: { "RECORD_ACTION" }
    ),
    local!queryResultParse: choose(
      wherecontains(
        ri!returnType,
        rule!AS_CO_CONS_QE_RETURN_TYPES()
      ),
      /*DATA_SUBSET*/local!queryResult,
      /*SINGLE_OBJECT*/local!queryResult.data,
      /*OBJECT_ARRAY*/a!defaultValue(
        value: local!queryResult.data,
        /* Necessary where executeWhen = false */
        default: {}
      ),
      /*AGGREGATION*/a!defaultValue(
        value: local!queryResult.data,
        default: {}
      ),
      /*TOTAL_COUNT*/local!queryResult.totalCount
    ),
    local!castResult: cast(
      local!castResultToType,
      local!queryResultParse
    ),
    local!handleDatasubsetTypeCast: if(
      and(
        ri!returnType = cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
        ri!executeWhen
      ),
      #"urn:appian:function:v1:a:update"(
        local!castResult,
        "data",
        cast(
          rule!AS_CO_UT_returnArrayTypeNumber(type: ri!entityType),
          local!castResult.data
        )
      ),
      local!castResult
    ),
    local!handleDatasubsetTypeCast
  )
)/*

DOCUMENTATION

This rule is designed to minimize the overhead with querying data and then casting the result


Required Inputs:

1. entity 
- This is the data store entity you are querying
-- e.g. cons!AS_DATA_STORE_ENTITY_CASES

2. entityType
- This is a type! input of the data type which maps to the underlying data store entity
- Note that your type! input should not end in parenthesis; this is NOT designed to take in an empty CDT
-- e.g. 'type!{urn:com:appian:types}Case'

3. returnType
- This depends on the purpose for the query, and should be one of the returnTypes in rule!AS_CO_CONS_QE_RETURN_TYPES
-- DATA_SUBSET - This will return a DataSubset; pagingInfo is required
-- SINGLE_OBJECT - This will return a single CDT whose type is the entityType; pagingInfo must be null
-- OBJECT_ARRAY - This will return an array of CDTs whos type is an array of the entityType; pagingInfo must be null
-- AGGREGATION - This will return an array of Dictionaries; pagingInfo must be null
---- NOTE: If you need Aggregation with paging controls, such as for a grid, use returnType DATA_SUBSET
-- TOTAL_COUNT - This will return an Integer of the total count; pagingInfo must be null


Optional Inputs:

4. executeWhen
- The query will not execute if this is false, the query will execute if this is true or null
-- This is meant to operate in a similar manner to the showWhen parameter of components
- Usethistoskipexecutionofaquerywithoutneedingtowrapif()statementsaroundtherule
-- Examples of this would be to skip querying if your filter value is a null ID
- If the query does not execute, this will return a null value whose type is based on the returnType input

5. triggerRefresh
- By default, the query will always re-execute whenever any inputs change
- However, by updating the value of triggerRefresh, you can also trigger a re-execution even if none of the other inputs change

6. excludeNestedCdts
- When true, this will exclude all nested CDTs from the query selection, and only include primitive fields
- When false or null, all fields will be included in the query, including all nested CDTs

7. filters
- Array of filters to apply to the query, built with a!queryFilter()
- These filters will be applied with an AND operator
- These filters will apply in addition to the logicalExpression input, combining both with an AND operator

8. logicalExpression
- A logical expression to apply to the query, built with a!queryLogicalExpression()
- This logical expression will apply in addition to the filters input, combining both with an AND operator

9. aggregation
- Aggregation to apply to the query, built with a!queryAggregation()

10. pagingInfo
- Paging info to apply to the query, built with a!pagingInfo() or derived from fv!pagingInfo in grids
- This is required if passing returnType DATA_SUBSET, otherwise it must be null
- For returnTypes that are not DATA_SUBSET, the batch size will be automatically calculated depending on the returnType
-- For example, passing SINGLE_OBJECT will automatically apply a batch size of 1, whereas OBJECT_ARRAY and AGGREGATION will automatically apply a batch size of -1

11. sort
- Array of sort to apply to the query, built with a!sortInfo()
- This is meant to be used with returnType OBJECT_ARRAY and AGGREGATION, since pagingInfo is not required for these, but a specified sort may be used

12. batchSize
- Batch size to apply to the query
- This is meant to be used with returnType OBJECT_ARRAY and AGGREGATION, since pagingInfo is not required for these, but a specified batchSize may be used

*/

