_a-0000e5da-a251-8000-9bbe-011c48011c48_.sail_code:
#"SYSTEM_SYSRULES_forEach"(
  items: ri!auditParams,
  expression: rule!AS_CO_UT_updateCdtFieldsByFieldValuePairs(
    cdt: fv!item,
    fieldValuePairs: {
      field: "originalObjects",
      value: rule!AS_CO_UT_replaceNull(
        nullableValue: a!flatten(fv!item.originalObjects),
        replacementValue: if(
          /*Initializing null when the objectDse is null*/
          rule!AS_CO_UT_isBlank(fv!item.objectDse),
          rule!AS_CO_UT_initializeBlankLocalVariable(
            type: a!flatten(fv!item).objectTypeNumber
          ),
          if(
            rule!AS_CO_UT_isBlank(fv!item.complexFieldName),
            if(
              /*Initializing null when the idField in the audit config is null*/
              rule!AS_CO_UT_isBlank(
                index(
                  a!flatten(fv!item.auditConfig),
                  "idField",
                  null
                )
              ),
              rule!AS_CO_UT_initializeBlankLocalVariable(
                type: a!flatten(fv!item).objectTypeNumber
              ),
              rule!AS_CO_UT_replaceNull(
                nullableValue: rule!AS_ADT_UT_getOriginalObjects(auditParams: fv!item),
                replacementValue: rule!AS_CO_UT_initializeBlankLocalVariable(
                  type: a!flatten(fv!item).objectTypeNumber
                )
              )
            ),
            if(
              /*Initializing null when idField of complexField's auditConfig is null*/
              rule!AS_CO_UT_isBlank(
                index(
                  rule!AS_CO_UT_filterCdtByField(
                    cdt: fv!item.auditConfig.complexFields,
                    field: "auditFieldName",
                    value: tostring(fv!item.complexFieldName)
                  ),
                  "idField",
                  null
                )
              ),
              rule!AS_CO_UT_initializeBlankLocalVariable(
                type: a!flatten(fv!item).objectTypeNumber
              ),
              /*Getting the original object for the complexFieldName object*/
              rule!AS_CO_UT_replaceNull(
                nullableValue: rule!AS_ADT_UT_getOriginalObjects(
                  auditParams: rule!AS_CO_UT_updateCdtFields(
                    cdt: fv!item,
                    fields: "auditConfig",
                    newValues: rule!AS_CO_UT_filterCdtByField(
                      cdt: fv!item.auditConfig.complexFields,
                      field: "auditFieldName",
                      value: tostring(fv!item.complexFieldName)
                    )
                  )
                ),
                replacementValue: rule!AS_CO_UT_initializeBlankLocalVariable(
                  type: a!flatten(fv!item).objectTypeNumber
                )
              )
            )
          )
        )
      )
    }
  )
)

_a-0000e40a-de32-8000-9ba8-011c48011c48_.sail_code:
cast(
   'type!{http://www.appian.com/ae/types/2009}Integer?list',
  with(
    local!array: a!flatten(ri!array),

    if(

      /*Have to handle separately for document types due to how it casts to string*/
      rule!AS_CO_UT_typeCompare(
        value1: local!array,
        value2: {todocument(null)}
      ),
      wherecontains(
        todocument(ri!values),
        local!array
      ),

      /*Have to handle separately for group types due to how it casts to string*/
      rule!AS_CO_UT_typeCompare(
        value1: local!array,
        value2: {togroup(null)}
      ),
      wherecontains(
        togroup(ri!values),
        local!array
      ),

      /*Casting to string accounts for any primitive type (integer, boolean, ect.)*/
      wherecontains(
        if(
          rule!AS_CO_UT_isBlank(ri!values),
          touniformstring(ri!values),
          touniformstring(a!flatten(ri!values))
        ),
        touniformstring(local!array)
      )
    )
  )
)

