_a-0000e5bc-4a9a-8000-9bbc-011c48011c48_.sail_code:
/* This rule is used to construct the manipulated audit data*/
/*The manipulated audit data can be multiple. We are constructing the audit data for each manipulationAudit in auditParams*/
if(
  rule!AS_CO_UT_isNotBlank(ri!auditParams.complexFieldName),
  /*When the audit is captured as nested even when the concerned object cdts are not nested 
    Eg: AS_GSS_Evaluation and AS_GSS_EvaluationDocume nt are not nested but AS_GSS_A_R_Evaluation and AS_GSS_A_R_EvaluationDocument are nested*/
  rule!AS_GAM_ADT_UT_constructFieldChangesDictionaryForManipulatedAudit(
    initiator: ri!initiator,
    auditParams: ri!auditParams
  ),
  /*If the audit data is not nested and is manipulated and passed from outsite, we can directly construct the audit entry for each ri!auditParams.manipulationAudit*/
  rule!AS_GAM_ADT_UT_constructDynamicDictionaryForManipulatedAudit(
    initiator: ri!initiator,
    auditParams: ri!auditParams
  )
)

_a-0000e5da-a251-8000-9bbe-011c48011c48_.sail_code:
a!localVariables(
  local!updateVendorUei: a!refreshVariable(
    value: ri!vendor.uniqueEntityId,
    refreshOnReferencedVarChange: false
  ),
  local!vendorsData: rule!AS_GSS_QE_getRefVendorDetails(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
    additionalFilters: {
      #"SYSTEM_SYSRULES_queryFilter"(
        field: "isActive",
        operator: "=",
        value: true()
      ),
      if(
        rule!AS_CO_UT_isNotBlank(local!updateVendorUei),
        #"SYSTEM_SYSRULES_queryFilter"(
          field: "uniqueEntityId",
          operator: "<>",
          value: local!updateVendorUei
        ),
        null()
      )
    },

  ),
  local!vendors: upper(
    reject(
      fn!isnull,
      index(
        local!vendorsdata,
        "uniqueEntityId",
        null
      )
    )
  ),
  #"SYSTEM_SYSRULES_boxLayout"(
    label: "Profile",
    contents: {
      #"SYSTEM_SYSRULES_sideBySideLayout"(
        items: {
          /* Legal Name */
          #"SYSTEM_SYSRULES_sideBySideItem"(
            item: rule!AS_CO_INP_textField(
              label: "Legal Name",
              readOnly: ri!readOnly,
              required: true,
              value: ri!vendor.legalName,
              saveInto: ri!vendor.legalName,
              maxLength: cons!AS_CO_ENUM_TEXT_LENGTH_LONG
            )
          ),
          /* UEI */
          #"SYSTEM_SYSRULES_sideBySideItem"(
            item: rule!AS_CO_INP_textField(
              label: "UEI",
              readOnly: ri!readOnly,
              value: ri!vendor.uniqueEntityId,
              required: and(
                rule!AS_CO_UT_isBlank(ri!vendor.cageCode)
              ),
              validations: if(
                not(
                  and(
                    len(ri!vendor.uniqueEntityId) = cons!AS_GAM_ENUM_TEXT_LENGTH_FOR_UEI,
                    rule!AS_GAM_UT_validateAlphanumeric(ri!vendor.uniqueEntityId)
                  )
                ),
                rule!AS_GAM_CO_I18N_UT_displayLabel(
                  bundle: ri!i18nData,
                  bundleKey: "vld_LabelAndAlphanumericValue",
                  arguments: {
                    "UEI",
                    cons!AS_GAM_ENUM_TEXT_LENGTH_FOR_UEI
                  }
                ),
                a!localVariables(
                  local!isVendorPresent: contains(
                    local!vendors,
                    upper(ri!vendor.uniqueEntityId)
                  ),
                  local!apiVendors: if(
                    exact(
                      upper(local!updateVendorUei),
                      upper(ri!vendor.uniqueEntityId)
                    ),
                    null,
                    rule!AS_GAM_GetVendorDetailsFromSAMGov(
                      uniqueEntityId: ri!vendor.uniqueEntityId
                    )
                  ),
                  if(
                    and(
                      not(local!isVendorPresent),
                      rule!AS_CO_UT_isBlank(
                        local!apiVendors.result.body.entityData
                      )
                    ),
                    "",
                    "Vendor with this UEI already exists"
                  )
                )
              ),
              saveInto: ri!vendor.uniqueEntityId,
              maxLength: cons!AS_CO_ENUM_TEXT_LENGTH_LONG
            )
          ),
          /* CAGE */
          #"SYSTEM_SYSRULES_sideBySideItem"(
            item: rule!AS_CO_INP_textField(
              label: "CAGE",
              readOnly: ri!readOnly,
              value: ri!vendor.cageCode,
              required: and(
                rule!AS_CO_UT_isBlank(ri!vendor.uniqueEntityId)
              ),
              validations: if(
                and(
                  len(ri!vendor.cageCode) = cons!AS_GAM_ENUM_TEXT_LENGTH_FOR_CAGE,
                  rule!AS_GAM_UT_validateAlphanumeric(ri!vendor.cageCode)
                ),
                "",
                rule!AS_GAM_CO_I18N_UT_displayLabel(
                  bundle: ri!i18nData,
                  bundleKey: "vld_LabelAndAlphanumericValue",
                  arguments: {
                    "CAGE",
                    cons!AS_GAM_ENUM_TEXT_LENGTH_FOR_CAGE
                  }
                ),

              ),
              saveInto: ri!vendor.cageCode,
              maxLength: cons!AS_CO_ENUM_TEXT_LENGTH_LONG
            )
          ),
          /* ACTIVE */
          #"SYSTEM_SYSRULES_sideBySideItem"(
            item: #"SYSTEM_SYSRULES_richTextDisplayField"(
              label: "",
              labelPosition: "ABOVE",
              value: {
                #"SYSTEM_SYSRULES_richTextItem_v1"(text: char(10)),
                #"SYSTEM_SYSRULES_richTextIcon"(
                  icon: if(
                    or(
                      ri!vendor.isActive = true,
                      rule!AS_CO_UT_isBlank(ri!vendor.isActive)
                    ),
                    "check-square-o",
                    "square-o"
                  ),
                  link: #"SYSTEM_SYSRULES_dynamicLink"(
                    value: if(
                      rule!AS_CO_UT_isBlank(ri!vendor.isActive),
                      false,
                      not({ ri!vendor.isActive })
                    ),
                    saveInto: {
                      ri!vendor.isActive,
                      a!save(
                        ri!showWarning,
                        if(
                          or(
                            ri!vendor.isActive = true,
                            rule!AS_CO_UT_isBlank(ri!vendor.isActive)
                          ),
                          false,
                          true
                        )
                      )
                    }
                  ),
                  linkStyle: "STANDALONE",
                  color: if(
                    or(
                      ri!vendor.isActive = true,
                      rule!AS_CO_UT_isBlank(ri!vendor.isActive)
                    ),
                    "ACCENT",
                    ""
                  ),
                  size: "MEDIUM"
                ),
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: " " & "Active",
                  size: "MEDIUM",
                  style: "STRONG"
                ),
                /* Displays a warning message If made Inactive */
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: char(10),
                  showWhen: ri!showWarning = true
                ),
                #"SYSTEM_SYSRULES_richTextIcon"(
                  icon: "warning",
                  showWhen: ri!showWarning = true,
                  color: "ACCENT"
                ),
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: " " & "Making this inactive will make this vendor unavailable during the create evaluation process",
                  showWhen: ri!showWarning = true,
                  color: "ACCENT",
                  style: "EMPHASIS"
                )
              },
              accessibilityText: "Press Enter to change the Status"
            ),
            showWhen: not(or(ri!isCreated))
          ),
          #"SYSTEM_SYSRULES_sideBySideItem"()
        }
      ),
      #"SYSTEM_SYSRULES_sideBySideLayout"(
        showWhen: not(or(ri!isCreated)),
        items: {
          /* Business Name */
          #"SYSTEM_SYSRULES_sideBySideItem"(
            item: rule!AS_CO_INP_textField(
              label: "Doing Business As",
              readOnly: ri!readOnly,
              value: ri!vendor.businessName,
              saveInto: ri!vendor.businessName,
              maxLength: cons!AS_CO_ENUM_TEXT_LENGTH_LONG
            )
          ),
          /* Business Type */
          #"SYSTEM_SYSRULES_sideBySideItem"(
            item: rule!AS_CO_CP_multipleDropdownField(
              choiceValues: ri!businessTypes.code,
              choiceLabels: ri!businessTypes.description,
              label: "Business Type",
              readOnly: ri!readOnly,
              value: ri!associatedCodes,
              saveInto: {
                a!save(ri!associatedCodes, {}),
                ri!associatedCodes
              }
            )
          ),
          /* SAM.gov Status */
          #"SYSTEM_SYSRULES_sideBySideItem"(
            rule!AS_CO_CP_dropdownField(
              label: "SAM.gov Status",
              readOnly: ri!readOnly,
              choiceValues: cons!AS_GSS_TX_SAM_GOV_VENDOR_STATUSES,
              choiceLabels: cons!AS_GSS_TX_SAM_GOV_VENDOR_STATUSES,
              value: ri!vendor.status,
              saveInto: ri!vendor.status
            )
          ),
          /* Expiration Date */
          #"SYSTEM_SYSRULES_sideBySideItem"(
            item: rule!AS_CO_INP_dateField(
              label: "Expiration Date",
              readOnly: ri!readOnly,
              value: ri!vendor.expirationDate,
              saveInto: ri!vendor.expirationDate
            )
          ),

        }
      )
    },
    marginBelow: "STANDARD"
  )
)

_a-0000e6a4-3c85-8000-9ba5-011c48011c48_.sail_code:
a!localVariables(
  local!i18nData: rule!AS_CO_I18N_UT_loadBundleFromFolder(
    bundleFolder: cons!AS_CO_I18N_FLD_INTERNATIONALIZATION_FILES,
    bundleNames: "AS.CO.CommonObjects"
  ),
  if(
    rule!AS_CO_UT_isBlank(value: ri!maxAllowedFileSizeInBytes),
    "",
    if(
      if(
        rule!AS_CO_UT_isBlank(value: ri!fileSizesToCheck),
        0,
        ri!fileSizesToCheck
      ) > ri!maxAllowedFileSizeInBytes,
      if(
        /*If the allowed file size is less then KB, */
        ri!maxAllowedFileSizeInBytes < 1024,
        rule!AS_CO_I18N_UT_displayLabel(
          i18nData: local!i18nData,
          bundleKey: "AS.CO.CommonObjects.vld_FileSizeExceedsAllowedKB",
          arguments: rule!AS_CO_UT_fixed(
            number: ri!maxAllowedFileSizeInBytes / 1024,
            decimals: 3,
            no_commas: true()
          )
        ),
        if(
          /*If the allowed file size is less then MB, */
          ri!maxAllowedFileSizeInBytes < product(1024, 1024),
          rule!AS_CO_I18N_UT_displayLabel(
            i18nData: local!i18nData,
            bundleKey: "AS.CO.CommonObjects.vld_FileSizeExceedsAllowedKB",
            arguments: quotient(ri!maxAllowedFileSizeInBytes, 1024)
          ),
          /*If the allowed file size is in MB*/
          if(
            and(
              ri!maxAllowedFileSizeInBytes >= product(1024, 1024),
              ri!maxAllowedFileSizeInBytes <= product(1024, 1024, 1024)
            ),
            rule!AS_CO_I18N_UT_displayLabel(
              i18nData: local!i18nData,
              bundleKey: "AS.CO.CommonObjects.vld_FileSizeExceedsAllowedMB",
              arguments: quotient(
                quotient(ri!maxAllowedFileSizeInBytes, 1024),
                1024
              )
            ),
            ""
          )
        )
      ),
      ""
    )
  )
)

