rule!AS_CO_UT_queryEntity(
  entity: cons!AS_GSS_ENT_EVALUATION_DOCUMENT,
  entityType: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_EvaluationDocument',
  returnType: ri!returnType,
  executeWhen: and(
    ri!executeWhen,
    or(
      any(
        rule!AS_CO_UT_isNotBlank(_),
        {
          ri!additionalFilters.value,
          ri!filters.value,
          ri!logicalExpression,
          ri!aggregation
        }        
      ),
      rule!AS_CO_UT_isNotBlank(ri!evaluationId)
    )
  ),
  logicalExpression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
    operator: "AND",
    ignoreFiltersWithEmptyValues: true,
    logicalExpressions: {
      #"SYSTEM_SYSRULES_queryLogicalExpression"(
        ignoreFiltersWithEmptyValues: true,
        operator: "AND",
        filters: {
          #"SYSTEM_SYSRULES_queryFilter"(
            field: "evaluationId",
            operator: "=",
            value: ri!evaluationId
          ),
          if(
            rule!AS_CO_UT_booleanDefaultFalse(
              boolean: ri!fetchAll
            ),
            {},
            #"SYSTEM_SYSRULES_queryFilter"(
              field: "isDeleted",
              operator: "=",
              value: false()
            )
          ),
          #"SYSTEM_SYSRULES_queryFilter"(
            field: "docType.refDataId",
            operator: "in",
            value: ri!docTypeIds
          ),
          ri!additionalFilters
        }
      ),
      ri!logicalExpression
    },
    filters: ri!filters
  ),
  pagingInfo: ri!pagingInfo,
  triggerRefresh: ri!triggerRefresh
)

a!localVariables(
  local!matches: 
