    rule!AS_CO_UT_isBlank(ri!evaluationCriterias),
    null,
    where(
      #"SYSTEM_SYSRULES_forEach"(
        items: merge(ri!evaluationCriterias.factorNumber,ri!evaluationCriterias.criteriaName),
        expression: 
        search(ri!value, concat(fv!item[1],fv!item[2]))
      )
    )
  ),
  local!criteriaNames: #"SYSTEM_SYSRULES_forEach"(
    items: ri!evaluationCriterias,
    expression: rule!AS_GSS_UT_appendFactorNumberAndCriteriaName(criteriaName: fv!item.criteriaName,factorNumber:fv!item.factorNumber)
  ),
  #"SYSTEM_SYSRULES_dataSubset"(
    data: if(
      rule!AS_CO_UT_isBlank(ri!evaluationCriterias),
      null,
      index(
        local!criteriaNames,
        local!matches
      )
    ),
    identifiers: if(
      rule!AS_CO_UT_isBlank(ri!evaluationCriterias),
      null,
      index(
        ri!evaluationCriterias.criteriaId,
        local!matches
      )
    )
  )
)

